            font-size: 18px;
            font-weight: bold;
        }
    </style>
</head>
<body>
    <h1>Band Structure of a Quantum Wire with Rashba and Zeeman Interactions</h1>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-wso" class="slider-label">Rashba spin-orbit interaction strength (wso)</label>
            <input type="range" id="slider-wso" class="slider" min="0" max="2" step="0.1" value="0">
            <span id="value-wso" class="slider-value"></span>
            <hr>
            <h2>magnetic field</h2>
            <label for="slider-n" class="slider-label">subband (n)</label>
            <input type="range" id="slider-n" class="slider" min="0" max="10" step="1" value="0">
            <span id="value-n" class="slider-value"></span>
            <h3>strengths</h3>
            <label for="slider-bx" class="slider-label">x direction (BX)</label>
            <input type="range" id="slider-bx" class="slider" min="0" max="2" step="0.1" value="0">
            <span id="value-bx" class="slider-value"></span>
            <label for="slider-by" class="slider-label">y direction (BY)</label>
            <input type="range" id="slider-by" class="slider" min="0" max="2" step="0.1" value="0">
            <span id="value-by" class="slider-value"></span>
            <label for="slider-bz" class="slider-label">z direction (BZ)</label>
            <input type="range" id="slider-bz" class="slider" min="0" max="2" step="0.1" value="0">
            <span id="value-bz" class="slider-value"></span>
        </div>
        <div id="plot-container"></div>
    </div>

    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        // Initialize the plot
        let plot = Plotly.newPlot('plot-container', {
            title: 'n = 0',
            xaxis: {
                title: 'normalized k length (lwky)',
                range: [-5, 5],
                zeroline: true
            },
            yaxis: {
                title: '2 E / &#x210F;&omega;',
                range: [0, 40],
                zeroline: true
            }
        });

        // Update plot function
        function updatePlot() {
            let n = parseInt(document.getElementById('slider-n').value);
            let wso = parseFloat(document.getElementById('slider-wso').value);
            let BX = parseFloat(document.getElementById('slider-bx').value);
            let BY = parseFloat(document.getElementById('slider-by').value);
            let BZ = parseFloat(document.getElementById('slider-bz').value);

            // Define the x-axis values
            let k = Array.from({length: 100}, (_, i) => i / 10 - 5);

            // Calculate the energy values
            let E_plus = k.map(k => (2 * n + 1) + k ** 2 + Math.sqrt((BZ + wso * k) ** 2 + BX ** 2 + BY ** 2));
            let E_minus = k.map(k => (2 * n + 1) + k ** 2 - Math.sqrt((BZ + wso * k) ** 2 + BX ** 2 + BY ** 2));

            // Check if the term inside the square root is zero for all k
            let termZero = (BZ + wso * k[0]) ** 2 + BX ** 2 + BY ** 2 === 0;

            // Update the plot
            let traces = [];
            if (termZero) {
                traces.push({
                    x: k,
                    y: E_plus,
                    mode: 'lines',
                    line: {color: 'red', dash: 'solid', width: 2}
                });
            } else {
                traces.push({
                    x: k,
                    y: E_plus,
                    mode: 'lines',
                    line: {color: 'red', dash: 'solid', width: 2}
                });
                traces.push({
                    x: k,
                    y: E_minus,
                    mode: 'lines',
                    line: {color: 'red', dash: 'dash', width: 2}
                });
            }

            // Update the title
            let title = `n = ${n}`;
            if (BX !== 0) title += `, BX = ${BX}`;
            if (BY !== 0) title += `, BY = ${BY}`;
            if (BZ !== 0) title += `, BZ = ${BZ}`;
            if (wso !== 0) title += `, wso = ${wso}`;
            plot.layout.title.text = title;

            // Update the plot
            plot.data = traces;
            Plotly.react('plot-container', plot.data, plot.layout);
        }

        // Update the plot when any slider is changed
        document.getElementById('slider-wso').addEventListener('input', updatePlot);
        document.getElementById('slider-n').addEventListener('input', updatePlot);
        document.getElementById('slider-bx').addEventListener('input', updatePlot);
        document.getElementById('slider-by').addEventListener('input', updatePlot);
        document.getElementById('slider-bz').addEventListener('input', updatePlot);

        // Update the slider values
        document.getElementById('value-wso').innerText = document.getElementById('slider-wso').value;
        document.getElementById('value-n').innerText = document.getElementById('slider-n').value;
        document.getElementById('value-bx').innerText = document.getElementById('slider-bx').value;
        document.getElementById('value-by').innerText = document.getElementById('slider-by').value;
        document.getElementById('value-bz').innerText = document.getElementById('slider-bz').value;

        // Update the plot on page load
        updatePlot();
    </script>
</body>
</html>
