        function drawPlot(canvas, data, xLabel, yLabel, xMax, yMax, offset) {
            let ctx = canvas.getContext("2d");
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            ctx.fillStyle = "#d4e6f1";
            ctx.strokeStyle = "#2e86c1";
            ctx.lineWidth = 2;
            for (let i = 0; i < data.length; i++) {
                ctx.beginPath();
                ctx.moveTo(i, data[i] + offset);
                ctx.lineTo(i + 1, data[i] + offset);
                ctx.stroke();
                ctx.lineTo(i + 1, data[i] + offset);
                ctx.fill();
            }
            ctx.font = "12px Arial";
            ctx.textAlign = "left";
            ctx.textBaseline = "top";
            ctx.fillStyle = "#000";
            ctx.fillText(xLabel, 10, 10);
            ctx.fillText(yLabel, canvas.width - 100, canvas.height - 10);
            ctx.beginPath();
            ctx.moveTo(0, 0);
            ctx.lineTo(0, canvas.height);
            ctx.stroke();
            ctx.moveTo(canvas.width, 0);
            ctx.lineTo(canvas.width, canvas.height);
            ctx.stroke();
        }

        document.getElementById("toggle-controls").addEventListener("click", function() {
            let slidersArea = document.getElementById("sliders-area");
            if (slidersArea.style.display === "block") {
                slidersArea.style.display = "none";
            } else {
                slidersArea.style.display = "block";
            }
        });

        let sliders = document.querySelectorAll(".slider-container input[type='range']");
        sliders.forEach(function(slider) {
            slider.addEventListener("input", function() {
                let value = this.value;
                let span = document.querySelector("#" + this.id + "-value");
                span.textContent = value;
                update();
            });
        });

        update();
    </script>
