    <script>
        let v0 = 211;
        let T = 270;
        let tempUnits = 'C';
        let k;
        let T_K;
        let V_current;
        let gasColor;

        function setup() {
            createCanvas(400, 200);
            background(220);
            frameRate(30);
        }

        function draw() {
            background(220);
            drawCylinder();
            drawPiston();
            drawGas();
        }

        function drawCylinder() {
            fill(0);
            noStroke();
            rect(50, height - 50, 300, 50);
            rect(50, height - 100, 300, 50);
            stroke(0);
            line(50, height - 100, 350, height - 100);
        }

        function drawPiston() {
            fill(128);
            noStroke();
            rect(50, height - 150 - V_current, 300, V_current);
        }

        function drawGas() {
            let colorLow = color(128, 0, 128);
            let colorMid = color(255, 255, 0);
            let colorHigh = color(255, 0, 0);
            gasColor = lerpColor(colorLow, colorHigh, (T_K - 0) / (1000 - 0));
            fill(gasColor);
            noStroke();
            rect(50, height - 150, 300, V_current);
        }

        function updateValues() {
            v0 = Number(document.getElementById('slider-v0').value);
            T = Number(document.getElementById('slider-T').value);
            tempUnits = document.querySelector('input[name="temp-units"]:checked').value;
            k = v0 / 273.15;
            if (tempUnits === 'C') {
                T_K = T + 273.15;
                document.getElementById('slider-T').min = -273;
                document.getElementById('slider-T').max = 727;
            } else {
                T_K = T;
                document.getElementById('slider-T').min = 0;
                document.getElementById('slider-T').max = 1000;
            }
            V_current = k * T_K;
            gasColor = lerpColor(color(128, 0, 128), color(255, 0, 0), (T_K - 0) / (1000 - 0));
            plotUpdate();
            draw();
        }

        function plotUpdate() {
            let plotData = [
                {
                    x: [0, 1000],
                    y: [k * 0, k * 1000],
                    mode: 'lines',
                    line: {
                        color: 'Viridis'
                    }
                },
                {
                    x: [T_K],
                    y: [V_current],
                    mode: 'markers',
                    marker: {
                        color: gasColor,
                        size: 12
                    }
                }
            ];
            Plotly.newPlot('plot', plotData, {
                layout: {
                    title: 'Charles\'s Law',
                    xaxis: {
                        title: tempUnits === 'C' ? 'temperature °C' : 'temperature K',
                        range: tempUnits === 'C' ? [-273, 727] : [0, 1000]
                    },
                    yaxis: {
                        title: 'volume',
                        range: [0, 2000]
                    }
                }
            });
        }

        document.getElementById('slider-v0').addEventListener('input', updateValues);
        document.getElementById('slider-T').addEventListener('input', updateValues);
        document.querySelector('input[name="temp-units"]').addEventListener('change', updateValues);

        updateValues();
    </script>
</body>
</html>
