    <script src="https://cdn.mathjax.org/mathjax/latest/MathJax.js?config=TeX-AMS-MML_HTMLorMML"></script>
    <script>
        // Initialize the data table
        const table = document.getElementById('table-container');
        const ages = [30, 35, 40, 45, 50, 55];
        const volumes = [289, 445, 565, 704, 833, 924];
        for (let i = 0; i < ages.length; i++) {
            const row = table.tBodies[0].insertRow(i);
            const ageCell = row.insertCell(0);
            const volumeCell = row.insertCell(1);
            ageCell.textContent = ages[i];
            volumeCell.textContent = volumes[i];
        }

        // Initialize the plot
        const plotContainer = document.getElementById('plot-container');
        const plotData = [
            {
                x: ages,
                y: volumes,
                mode: 'markers',
                marker: { color: 'blue' },
                type: 'scatter'
            }
        ];
        const layout = {
            title: 'forest growth curve',
            xaxis: { title: 'stand age (years)', range: [15, 80] },
            yaxis: { title: 'wood volume, m³ ha⁻¹', range: [0, 1600] },
            plot_bgcolor: '#FFFFF0',
            gridcolor: 'lightgrey',
            zeroline: false,
            showlegend: false
        };
        Plotly.newPlot(plotContainer, plotData, layout);

        // Update function
        function update() {
            const bMax = parseFloat(document.getElementById('slider-bmax').value);
            const b0 = parseFloat(document.getElementById('slider-b0').value);
            const r = parseFloat(document.getElementById('slider-r').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);

            // Update value displays
            document.getElementById('value-bmax').textContent = bMax.toFixed(0);
            document.getElementById('value-b0').textContent = b0.toFixed(1);
            document.getElementById('value-r').textContent = r.toFixed(3);
            document.getElementById('value-beta').textContent = beta.toFixed(3);

            // Calculate Richards curve
            const t = Array.from({ length: 66 }, (_, i) => 15 + i * 5);
            const M = (bMax / b0) ** beta - 1;
            const V = t.map(tValue => bMax / (1 + M * Math.exp(-r * tValue)) ** (1 / beta));

            // Calculate 4/5 law parameters
            const tInf = Math.log((bMax / b0) ** beta - 1) / r;
            const VInf = bMax * ((beta + 1) / (2 * beta)) ** (1 / beta);
            const VPrimeInf = (r / beta) * VInf * (1 - (VInf / bMax) ** beta);
            const u = tInf - (4 / 5) * VInf / VPrimeInf;
            const p = VInf / (tInf - u) ** (4 / 5);

            // Handle edge cases
            if (tInf - u <= 0) {
                u = 0;
                p = 0;
            }

            // Update results display
            document.getElementById('result-u').textContent = u.toFixed(4);
            document.getElementById('result-p').textContent = p.toFixed(4);

            // Update plot
            const richardsData = {
                x: t,
                y: V,
                mode: 'lines',
                line: { color: 'orange' },
                type: 'scatter'
            };
            const fourFiveData = {
                x: t.filter(tValue => tValue > u),
                y: t.filter(tValue => tValue > u).map(tValue => p * (tValue - u) ** (4 / 5)),
                mode: 'lines',
                line: { color: 'blue' },
                type: 'scatter'
            };
            const updatedPlotData = [richardsData, fourFiveData];
            Plotly.react(plotContainer, updatedPlotData, layout);
        }

        // Add event listeners to sliders
        document.querySelectorAll('#control-panel > div > input[type="range"]').forEach(slider => {
            slider.addEventListener('input', update);
        });

        // Initialize the plot and update function
        update();
    </script>
</body>
</html>
