            document.getElementById('slider-conical').addEventListener('input', redraw);
            document.getElementById('slider-rotate-x').addEventListener('input', redraw);
            document.getElementById('slider-rotate-z').addEventListener('input', redraw);
            document.getElementById('btn-units-plus').addEventListener('click', incrementSliderValue.bind(null, 'slider-units'));
            document.getElementById('btn-angle-plus').addEventListener('click', incrementSliderValue.bind(null, 'slider-angle'));
            document.getElementById('btn-conical-plus').addEventListener('click', incrementSliderValue.bind(null, 'slider-conical'));
            document.getElementById('btn-rotate-x-plus').addEventListener('click', incrementSliderValue.bind(null, 'slider-rotate-x'));
            document.getElementById('btn-rotate-z-plus').addEventListener('click', incrementSliderValue.bind(null, 'slider-rotate-z'));
            redraw();
        }

        function onWindowResize() {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        }

        function incrementSliderValue(id) {
            let slider = document.getElementById(id);
            slider.value = parseInt(slider.value) + 1;
            redraw();
        }

        function redraw() {
            let radioValue = parseInt(document.querySelector('input[name="base-polygon"]:checked').value);
            let units = parseInt(document.getElementById('slider-units').value);
            let angle = parseFloat(document.getElementById('slider-angle').value);
            let conical = parseFloat(document.getElementById('slider-conical').value);
            let rotateX = parseInt(document.getElementById('slider-rotate-x').value);
            let rotateZ = parseInt(document.getElementById('slider-rotate-z').value);
            group.clear();
            for (let i = 0; i < units; i++) {
                let geometry = new THREE.ShapeGeometry(new THREE.Path().moveTo(0, 0).splineThru(
                    Array(radioValue).fill(0).map((_, index) => Math.cos((index * 2 * Math.PI) / radioValue))
                ).splineThru(
                    Array(radioValue).fill(0).map((_, index) => Math.sin((index * 2 * Math.PI) / radioValue))
                ));
                let material = new THREE.MeshStandardMaterial({
                    color: new THREE.Color(),
                    metalness: 0.5,
                    roughness: 0.5
                });
                let mesh = new THREE.Mesh(geometry, material);
                let angularAdvanceRad = angle * (Math.PI / 180);
                let angleRad = i * angularAdvanceRad;
                let radius = 4 * Math.sqrt(i);
                let scale = 0.5 * Math.log(i + 2);
                let x = radius * Math.cos(angleRad);
                let y = radius * Math.sin(angleRad);
                let z = conical * radius;
                mesh.position.set(x, y, z);
                mesh.scale.set(scale, scale, scale);
                mesh.rotation.z = angleRad;
                group.add(mesh);
            }
            group.rotation.x = rotateX * Math.PI / 180;
            group.rotation.z = rotateZ * Math.PI / 180;
            scene.background = new THREE.Color(0xffffff);
            renderer.render(scene, camera);
        }

        init();
    </script>
</body>
</html>
