                            const geometry = new THREE.SphereGeometry(0.05, 32, 32);
                            const material = new THREE.MeshPhongMaterial({ color: 0x808080, specular: 0x000000, shininess: 100 });
                            const mesh = new THREE.Mesh(geometry, material);
                            mesh.position.set(i * 0.5 + 0.25, j * 0.5 + 0.25, z * 0.5 + 0.25);
                            atoms.add(mesh);
                        }
                    }
                }
