                const gcd = (a, b) => {
                    if (b === 0) return a;
                    return gcd(b, a % b);
                };
                const hPrime = h / gcd(h, k);
                const kPrime = k / gcd(h, k);
                const lPrime = l / gcd(h, k);
                const min = Math.min(...[
                    -0.5 * hPrime,
                    -0.5 * kPrime,
                    -0.5 * lPrime
                ]);
                const max = Math.max(...[
