            display: inline-block;
            width: 150px;
        }
        #control-panel input[type="range"] {
            width: 200px;
        }
        #control-panel span {
            margin-left: 10px;
        }
        /* Visualization Area Styles */
        #visualization-area {
            padding: 10px;
        }
        #orbit-canvas {
            border: 1px solid #ccc;
        }
        #plot-canvas {
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>
    <!-- Main Container -->
    <div id="main-container">
        <!-- Control Panel -->
        <div id="control-panel">
            <!-- Slider 1: Angle relative to ecliptic -->
            <div>
                <label for="slider-angle">Angle relative to ecliptic:</label>
                <input type="range" id="slider-angle" min="0" max="60" step="0.01" value="23.45">
                <span id="value-angle"></span>
            </div>
            <!-- Slider 2: Longitude of periapsis -->
            <div>
                <label for="slider-longitude">Longitude of periapsis:</label>
                <input type="range" id="slider-longitude" min="0" max="360" step="0.01" value="102.95">
                <span id="value-longitude"></span>
            </div>
            <!-- Slider 3: Orbit eccentricity -->
            <div>
                <label for="slider-eccentricity">Orbit eccentricity:</label>
                <input type="range" id="slider-eccentricity" min="0" max="0.99" step="0.000001" value="0.016722">
                <span id="value-eccentricity"></span>
            </div>
        </div>
        <!-- Visualization Area -->
        <div id="visualization-area">
            <!-- Orbit Sketch -->
            <canvas id="orbit-canvas" width="400" height="400"></canvas>
            <!-- Equation of Time Plot -->
            <canvas id="plot-canvas" width="400" height="200"></canvas>
        </div>
    </div>

    <!-- Scripts -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script>
        // Get the canvas elements
        let orbitCanvas = document.getElementById('orbit-canvas');
        let plotCanvas = document.getElementById('plot-canvas');

        // Initialize p5.js
        let sketch = function(p) {
            p.setup = function() {
                // Create a p5.js canvas for the orbit sketch
                let orbitSketchCanvas = p.createCanvas(400, 400);
                orbitSketchCanvas.parent('orbit-canvas');

                // Create a p5.js canvas for the equation of time plot
                let plotCanvas = p.createCanvas(400, 200);
                plotCanvas.parent('plot-canvas');

                // Set the background color
                p.background(255);

                // Set the stroke color
                p.stroke(165, 82, 37);

                // Set the fill color
                p.noFill();

                // Set the semi-major axis
                let semiMajorAxis = p.width / 2 * 0.4;

                // Set the eccentricity
                let eccentricity = 0;

                // Set the longitude of periapsis
                let longitudeOfPeriapsis = 0;

                // Update the orbit sketch
                p.draw = function() {
                    // Clear the canvas
                    p.background(240);

                    // Draw the ellipse
                    let eccentricity = parseFloat(document.getElementById('slider-eccentricity').value);
                    let longitudeOfPeriapsis = parseFloat(document.getElementById('slider-longitude').value);
                    let angle = parseFloat(document.getElementById('slider-angle').value);
                    let a = semiMajorAxis;
                    let b = a * p.sqrt(1 - eccentricity * eccentricity);
                    p.push();
                    p.translate(p.width / 2, p.height / 2);
                    p.rotate(longitudeOfPeriapsis * p.PI / 180);
                    p.ellipse(0, 0, a * 2, b * 2);
                    p.pop();
                };

                // Update the equation of time plot
                p.draw = function() {
                    // Clear the canvas
                    p.background(255);

                    // Draw the axes
                    p.stroke(0);
                    p.line(0, p.height / 2, p.width, p.height / 2);
                    p.line(p.width / 2, 0, p.width / 2, p.height);

                    // Draw the grid
                    for (let i = 1; i <= 6; i++) {
                        p.line(p.width / 2 + i * 50, 0, p.width / 2 + i * 50, p.height);
                        p.line(0, p.height / 2 + i * 25, p.width, p.height / 2 + i * 25);
                    }

                    // Get the parameters from the sliders
                    let eccentricity = parseFloat(document.getElementById('slider-eccentricity').value);
                    let longitudeOfPeriapsis = parseFloat(document.getElementById('slider-longitude').value);
                    let angle = parseFloat(document.getElementById('slider-angle').value);

                    // Calculate the equation of time
                    let EOT = [];
                    for (let M = 0; M <= 2 * p.PI; M += 0.01) {
                        let E = M;
                        for (let i = 0; i < 10; i++) {
                            E = E - (E - eccentricity * p.sin(E) - M) / (1 - eccentricity * p.cos(E));
                        }
                        let nu = 2 * p.atan2(p.sqrt(1 + eccentricity) * p.sin(E / 2), p.sqrt(1 - eccentricity) * p.cos(E / 2));
                        let lambda = nu + longitudeOfPeriapsis;
                        let alpha = p.atan2(p.cos(angle) * p.sin(lambda), p.cos(lambda));
                        let EOT_rad = M - alpha;
                        if (Math.abs(EOT_rad - EOT[EOT.length - 1]) > p.PI) {
                            EOT_rad += (EOT_rad > EOT[EOT.length - 1]) ? -2 * p.PI : 2 * p.PI;
                        }
                        EOT.push(EOT_rad * (720 / p.PI));
                    }

                    // Scale the y-axis
                    let minY = Math.min(...EOT);
                    let maxY = Math.max(...EOT);
                    p.scale(1, p.height / (maxY - minY));

                    // Draw the curve
                    p.stroke(255, 140, 0);
                    for (let i = 1; i < EOT.length; i++) {
                        p.line(i * 0.01, EOT[i - 1], (i + 1) * 0.01, EOT[i]);
                    }
                };
            };

            p.windowResized = function() {
                p.resizeCanvas(p.windowWidth, p.windowHeight);
            };

            p.draw = function() {
                sketch.setup();
            };
        };

        // Initialize the p5.js sketch
        new p5(sketch);

        // Update the value display
        document.getElementById('value-angle').innerText = parseFloat(document.getElementById('slider-angle').value).toFixed(2);
        document.getElementById('value-longitude').innerText = parseFloat(document.getElementById('slider-longitude').value).toFixed(2);
        document.getElementById('value-eccentricity').innerText = parseFloat(document.getElementById('slider-eccentricity').value).toFixed(6);

        // Add event listeners to the sliders
        document.getElementById('slider-angle').addEventListener('input', function() {
            document.getElementById('value-angle').innerText = parseFloat(this.value).toFixed(2);
            sketch.setup();
        });
        document.getElementById('slider-longitude').addEventListener('input', function() {
            document.getElementById('value-longitude').innerText = parseFloat(this.value).toFixed(2);
            sketch.setup();
        });
        document.getElementById('slider-eccentricity').addEventListener('input', function() {
            document.getElementById('value-eccentricity').innerText = parseFloat(this.value).toFixed(6);
            sketch.setup();
        });
    </script>
</body>
</html>
