    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let c, h_C, delta, plotRange, step, verification;
        let cValue, h_CValue, deltaValue, plotRangeValue;

        function setup() {
            createCanvas(800, 600);
            background(220);
            noLoop();
            c = 4;
            h_C = 1.9;
            delta = 0.5;
            plotRange = 1;
            step = 5;
            verification = true;
            cValue = document.getElementById('c-value');
            h_CValue = document.getElementById('h_C-value');
            deltaValue = document.getElementById('delta-value');
            plotRangeValue = document.getElementById('plot-range-value');
            updateValueDisplays();
            draw();
        }

        function draw() {
            clear();
            background(220);
            setupCoordinateSystem();
            drawGeometricElements();
        }

        function setupCoordinateSystem() {
            translate(width / 2, height * 0.6);
            scale(1, -1);
            scale(plotRange * 50);
        }

        function drawGeometricElements() {
            let F = createVector(0, 0);
            let C = createVector(0, h_C);
            let D = createVector(c / 2, 0);
            let K = createVector(c, 0);
            let M = calculatePointM(c, h_C, delta);
            let B = calculatePointB(c, h_C, M);
            let A = calculatePointA(c, B);
            let E = verification ? calculatePointE(c, B) : null;
            if (step >= 1) {
                drawLine(lambda());
                drawPoints(F, C, D);
                drawLineSegment(F, C);
            }
            if (step >= 2) {
                drawPoint(K);
                drawDashedLines(C, D, K, M, D, M, K);
                drawArc(M, calculateRadius(c, h_C));
                drawPoint(M);
            }
            if (step >= 3) {
                drawPoint(B);
            }
            if (step >= 4) {
                drawPoint(A);
            }
            if (step >= 5) {
                drawTriangle(A, B, C);
            }
            if (verification) {
                drawPoint(E);
                drawDashedLines(C, E, K, B);
            }
        }

        function updateValueDisplays() {
            cValue.textContent = c.toFixed(1);
            h_CValue.textContent = h_C.toFixed(1);
            deltaValue.textContent = delta.toFixed(2);
            plotRangeValue.textContent = plotRange.toFixed(2);
        }

        function calculatePointM(c, h_C, delta) {
            let x_M = (c * h_C) / (2 * h_C + c * delta);
            let y_M = h_C * h_C / (2 * h_C + c * delta);
            return createVector(x_M, y_M);
        }

        function calculatePointB(c, h_C, M) {
            let x_B = 2 * M.x - c / 2;
            return createVector(x_B, 0);
        }

        function calculatePointA(c, B) {
            let x_A = B.x - c;
            return createVector(x_A, 0);
        }

        function calculatePointE(c, B) {
            let x_E = c - B.x;
            return createVector(x_E, 0);
        }

        function calculateRadius(c, h_C) {
            return h_C;
        }

        function lambda() {
            let lambdaX1 = 0;
            let lambdaX2 = width;
            return createVector(lambdaX1, 0), createVector(lambdaX2, 0);
        }

        function drawLine(lambda) {
            stroke(0);
            line(lambda[0].x, lambda[0].y, lambda[1].x, lambda[1].y);
        }

        function drawPoints(F, C, D) {
            stroke(0);
            fill(0);
            ellipse(F.x, F.y, 5);
            ellipse(C.x, C.y, 5);
            ellipse(D.x, D.y, 5);
            fill(0);
            textSize(12);
            textAlign(CENTER, CENTER);
            text('F', F.x, F.y);
            text('C', C.x, C.y);
            text('D', D.x, D.y);
        }

        function drawLineSegment(F, C) {
            stroke(0);
            line(F.x, F.y, C.x, C.y);
        }

        function drawDashedLines(C, D, K, M, D, M, K) {
            stroke(0);
            strokeDashArray([5, 5]);
            line(C.x, C.y, D.x, D.y);
            line(D.x, D.y, K.x, K.y);
            line(M.x, M.y, D.x, D.y);
            line(M.x, M.y, K.x, K.y);
            noStroke();
        }

        function drawArc(M, radius) {
            stroke(0);
            arc(M.x, M.y, radius * 2, radius * 2, 0, PI);
        }

        function drawPoint(P) {
            stroke(0);
            fill(0);
            ellipse(P.x, P.y, 5);
            fill(0);
            textSize(12);
            textAlign(CENTER, CENTER);
            text('M', P.x, P.y);
        }

        function drawTriangle(A, B, C) {
            strokeWeight(2);
            stroke(0);
            line(A.x, A.y, B.x, B.y);
            line(B.x, B.y, C.x, C.y);
            line(C.x, C.y, A.x, A.y);
        }

        function drawDashedLines(C, E, K, B) {
            stroke(0);
            strokeDashArray([5, 5]);
            line(C.x, C.y, E.x, E.y);
            line(K.x, K.y, B.x, B.y);
        }

        function redrawScene() {
            redraw();
        }

        function updateValues() {
            c = parseFloat(document.getElementById('slider-c').value);
            h_C = parseFloat(document.getElementById('slider-h_C').value);
            delta = parseFloat(document.getElementById('slider-delta').value);
            plotRange = parseFloat(document.getElementById('slider-plot-range').value);
            step = parseInt(document.querySelector('input[name="steps"]:checked').value);
            verification = document.getElementById('check-verification').checked;
            updateValueDisplays();
            redrawScene();
        }

        document.getElementById('slider-c').addEventListener('input', updateValues);
        document.getElementById('slider-h_C').addEventListener('input', updateValues);
        document.getElementById('slider-delta').addEventListener('input', updateValues);
        document.getElementById('slider-plot-range').addEventListener('input', updateValues);
        document.querySelectorAll('input[name="steps"]').forEach(radio => {
            radio.addEventListener('change', updateValues);
        });
        document.getElementById('check-verification').addEventListener('change', updateValues);
        setup();
    </script>
</body>
</html>
