        Plotly.newPlot('plot-intensity', plot_intensity.data, plot_intensity.layout);
        Plotly.newPlot('plot-population', plot_population.data, plot_population.layout);
        Plotly.newPlot('plot-coherence', plot_coherence.data, plot_coherence.layout);

        document.getElementById('slider-z').addEventListener('input', function() {
            const z_current = parseFloat(this.value);
            document.getElementById('label-z').textContent = z_current.toFixed(5);
            const blue_data = Array.from({length: 301}, (_, i) => {
