            margin-right: 10px;
        }

        #control-panel > div > input[type="range"] {
            width: 100%;
        }

        #control-panel > div > span {
            margin-left: 10px;
        }

        #plot-container {
            width: 800px;
            height: 400px;
            border: 1px solid #ccc;
            margin: 20px auto;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <!-- Control Panel Section -->
        <div id="control-panel">
            <!-- Heat Transfer Coefficient Slider -->
            <div>
                <label for="slider-ua">heat transfer coefficient (cal/(dm² K s))</label>
                <input type="range" id="slider-ua" min="0" max="20" step="0.1" value="0">
                <span id="ua-value"></span>
            </div>

            <!-- Reverse Reaction Pre-exponential Factor Slider -->
            <div>
                <label for="slider-kr0">reverse reaction pre-exponential factor (1/s)</label>
                <input type="range" id="slider-kr0" min="0" max="10" step="0.1" value="0">
                <span id="kr0-value"></span>
            </div>

            <!-- Feed Temperature Slider -->
            <div>
                <label for="slider-tf">feed temperature (K)</label>
                <input type="range" id="slider-tf" min="250" max="350" step="1" value="265">
                <span id="tf-value"></span>
            </div>

            <!-- Residence Time Slider -->
            <div>
                <label for="slider-tau">residence time (s)</label>
                <input type="range" id="slider-tau" min="100" max="1000" step="10" value="400">
                <span id="tau-value"></span>
            </div>
        </div>

        <!-- Visualization Section -->
        <div id="plot-container"></div>
    </div>

    <!-- CDN for Plotly.js -->
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>

    <!-- JavaScript code -->
    <script>
        // Constants
        const CA0 = 10; // mmol/dm³, initial concentration of A
        const R = 1.987; // cal/mol K, ideal gas constant
        const Ef = 25000; // cal/mol, forward activation energy
        const Dh_rxn = -200000; // cal/mol, enthalpy of reaction
        const Er = Ef - Dh_rxn; // cal/mol, reverse activation energy
        const kf0 = 1e15; // 1/s, forward pre-exponential factor
        const rhocp = 20000; // cal/(dm³ K), product of density and heat capacity

        // Initialize plot
        const plotDiv = document.getElementById('plot-container');
        const plot = Plotly.newPlot(plotDiv, [], {
            title: 'Product Concentration vs Temperature',
            xaxis: { title: 'Temperature (K)' },
            yaxis: { title: 'Product Concentration (mmol/dm³)' },
            annotations: [
                {
                    text: 'mass balance',
                    x: 360,
                    y: 8,
                    xref: 'x',
                    yref: 'y',
                    showarrow: false,
                    ax: 0,
                    ay: -40,
                    font: { size: 12 },
                    xaxis: 'x',
                    yaxis: 'y'
                },
                {
                    text: 'energy balance',
                    x: 280,
                    y: 0,
                    xref: 'x',
                    yref: 'y',
                    showarrow: false,
                    ax: 0,
                    ay: -40,
                    font: { size: 12 },
                    xaxis: 'x',
                    yaxis: 'y'
                }
            ]
        });

        // Update plot on slider changes
        const sliders = document.querySelectorAll('input[type="range"]');
        sliders.forEach(slider => {
            slider.addEventListener('input', () => {
                const ua = parseFloat(document.getElementById('slider-ua').value);
                const kr0 = parseFloat(document.getElementById('slider-kr0').value) * 1e12;
                const tf = parseFloat(document.getElementById('slider-tf').value);
                const tau = parseFloat(document.getElementById('slider-tau').value);

                // Calculate curves
                const T = Array.from({ length: 51 }, (_, i) => 250 + (400 - 250) * i / 50);
                const C_B_mass_balance = T.map(Tf => {
                    const kf = kf0 * Math.exp(-Ef / (R * Tf));
                    const kr = kr0 * Math.exp(-Er / (R * Tf));
                    return (tau * kf * CA0) / (1 + tau * (kf + kr));
                });
                const C_B_energy_balance = T.map(Tf => (rhocp * (Tf - tf) + ua * tau * (Tf - tf)) / (-Dh_rxn));

                // Update plot
                plot.data = [
                    {
                        x: T,
                        y: C_B_mass_balance,
                        mode: 'lines',
                        line: { color: '#008000' }
                    },
                    {
                        x: T,
                        y: C_B_energy_balance,
                        mode: 'lines',
                        line: { color: '#0000FF' }
                    }
                ];
                plot.layout.annotations[0].x = 360;
                plot.layout.annotations[0].y = C_B_mass_balance[C_B_mass_balance.length - 1];
                plot.layout.annotations[1].x = 280;
                plot.layout.annotations[1].y = C_B_energy_balance[C_B_energy_balance.length - 1];
                Plotly.relayout(plotDiv, plot.layout);

                // Update value displays
                document.getElementById('ua-value').textContent = ua.toFixed(1);
                document.getElementById('kr0-value').textContent = (kr0 / 1e12).toExponential();
                document.getElementById('tf-value').textContent = tf.toFixed(0);
                document.getElementById('tau-value').textContent = tau.toFixed(0);
            });
        });
    </script>
</body>
</html>
