        <canvas id="canvas" width="600" height="600"></canvas>
    </div>

</div>

<!-- CDN Libraries -->
<script src="https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js"></script>

<script>
    let k = 0.14;
    let phi = 0;
    let refFrame = 'epitrochoid';
    let envelopeType = 'inner';

    function setup() {
        createCanvas(600, 600);
        background(220);
        stroke(150);
        strokeWeight(1);
        noLoop();
    }

    function draw() {
        background(220);
        drawAxes();
        drawEpitrochoid(k, phi);
        drawEnvelope(k, phi, envelopeType);
        drawReferenceFrame(refFrame, k, phi);
    }

    function drawAxes() {
        line(0, 0, 0, height);
        line(0, height, width, height);
        line(0, 0, width/2, height/2);
        line(width/2, height/2, width, height);
        noStroke();
        fill(0);
        textSize(12);
        textAlign(CENTER, CENTER);
        text('X', width, height+20);
        text('Y', -20, height);
        text('1', width/2, height/2);
        text('-1', width/2, height/2-20);
    }

    function drawEpitrochoid(k, phi) {
        stroke(50, 100, 200);
        strokeWeight(2);
        noFill();
        beginShape();
        for (let t = 0; t < 2*PI; t += 0.01) {
            let x = cos(t) - k * cos(3*t);
            let y = sin(t) - k * sin(3*t);
            vertex(x*width/2 + width/2, y*height/2 + height/2);
        }
        endShape();
    }

    function drawEnvelope(k, phi, type) {
        stroke(230, 150, 0);
        strokeWeight(2);
        noFill();
        let L = 1/3;
        let R = 1;
        let e = k;
        if (type === 'inner') {
            for (let t = 0; t < 2*PI; t += 0.01) {
                let x = (L/k)*sin(t) - sin(t + (L/k)*t);
                let y = -(L/k)*cos(t) + cos(t + (L/k)*t);
                vertex(x*width/2 + width/2, y*height/2 + height/2);
            }
        } else if (type === 'outer') {
            for (let t = 0; t < 2*PI; t += 0.01) {
                let x = (L/k)*sin(t) + sin(t + (L/k)*t);
                let y = -(L/k)*cos(t) - cos(t + (L/k)*t);
                vertex(x*width/2 + width/2, y*height/2 + height/2);
            }
        } else if (type === 'both') {
            for (let t = 0; t < 2*PI; t += 0.01) {
                let x_inner = (L/k)*sin(t) - sin(t + (L/k)*t);
                let y_inner = -(L/k)*cos(t) + cos(t + (L/k)*t);
                let x_outer = (L/k)*sin(t) + sin(t + (L/k)*t);
                let y_outer = -(L/k)*cos(t) - cos(t + (L/k)*t);
                vertex(x_inner*width/2 + width/2, y_inner*height/2 + height/2);
                vertex(x_outer*width/2 + width/2, y_outer*height/2 + height/2);
            }
        }
    }

    function drawReferenceFrame(refFrame, k, phi) {
        if (refFrame === 'epitrochoid') {
            push();
            translate(width/2, height/2);
            rotate(phi);
            stroke(255, 0, 0);
            strokeWeight(2);
            noFill();
            beginShape();
            for (let t = 0; t < 2*PI; t += 0.01) {
                let x = cos(t) - k * cos(3*t);
                let y = sin(t) - k * sin(3*t);
                vertex(x*width/2 + width/2, y*height/2 + height/2);
            }
            endShape();
            pop();
        } else if (refFrame === 'envelope') {
            push();
            translate(width/2, height/2);
            rotate(-phi);
            stroke(255, 0, 0);
            strokeWeight(2);
            noFill();
            beginShape();
            for (let t = 0; t < 2*PI; t += 0.01) {
                let x = (L/k)*sin(t) - sin(t + (L/k)*t);
                let y = -(L/k)*cos(t) + cos(t + (L/k)*t);
                vertex(x*width/2 + width/2, y*height/2 + height/2);
            }
            endShape();
            pop();
        } else if (refFrame === 'fixed centers') {
            push();
            translate(width/2, height/2);
            stroke(255, 0, 0);
            strokeWeight(2);
            noFill();
            beginShape();
            for (let t = 0; t < 2*PI; t += 0.01) {
                let x = cos(t) - k * cos(3*t);
                let y = sin(t) - k * sin(3*t);
                vertex(x*width/2 + width/2, y*height/2 + height/2);
            }
            endShape();
            pop();
            push();
            translate(width/2, height/2);
            stroke(255, 0, 0);
            strokeWeight(2);
            noFill();
            beginShape();
            for (let t = 0; t < 2*PI; t += 0.01) {
                let x = (L/k)*sin(t) - sin(t + (L/k)*t);
                let y = -(L/k)*cos(t) + cos(t + (L/k)*t);
                vertex(x*width/2 + width/2, y*height/2 + height/2);
            }
            endShape();
            pop();
        }
    }

    function update() {
        k = parseFloat(document.getElementById('slider-eccentricity').value);
        phi = parseFloat(document.getElementById('slider-rotation').value);
        refFrame = document.querySelector('input[name="ref-frame"]:checked').value;
        envelopeType = document.querySelector('input[name="envelope-type"]:checked').value;
        redraw();
    }

    function mousePressed() {
        update();
    }

    document.getElementById('slider-eccentricity').addEventListener('input', update);
    document.getElementById('slider-rotation').addEventListener('input', update);
    document.getElementById('radio-epitrochoid').addEventListener('change', update);
    document.getElementById('radio-envelope').addEventListener('change', update);
    document.getElementById('radio-fixed-centers').addEventListener('change', update);
    document.getElementById('radio-inner').addEventListener('change', update);
    document.getElementById('radio-outer').addEventListener('change', update);
    document.getElementById('radio-both').addEventListener('change', update);
</script>

</body>
</html>
```
This code will create a Wankel rotary engine simulation with interactive controls for the eccentricity ratio, rotation angle, reference frame, and envelope type. The simulation uses p5.js for rendering and animation. The user can interact with the controls to change the simulation parameters and observe the resulting changes in the engine's behavior.

