    <script>
        const C_UNIT_CONVERSION = 1000;
        const K_DEPLETION = 0.137;
        let phi_max;
        let formulaDisplay = document.getElementById('formula-display');
        let phimaxDisplay = document.getElementById('phimax-display');
        let rangeDisplay = document.getElementById('range-display');
