        let cAxisValueDisplay = document.getElementById('c-axis-value-display');

        const sliderPhi = document.getElementById('slider-phi');
        const sliderGammaSat = document.getElementById('slider-gamma-sat');
        const sliderR = document.getElementById('slider-r');
        const sliderRpe = document.getElementById('slider-rpe');
        const sliderMw = document.getElementById('slider-mw');
        const sliderPhiAxis = document.getElementById('slider-phi-axis');
        const sliderCAxis = document.getElementById('slider-c-axis');

        const plotContainer = document.getElementById('plot-container');
        const plot = Plotly.newPlot(plotContainer, [], {
            xaxis: { title: 'ϕ (volume fraction)' },
            yaxis: { title: 'C (kg/m³)' },
            showlegend: false
        });

        function updateFormulaDisplay() {
            const gammaSat = parseFloat(sliderGammaSat.value);
            const r = parseFloat(sliderR.value);
            const rpe = parseFloat(sliderRpe.value);
            const mw = parseFloat(sliderMw.value);
            const y1 = `(${3 * gammaSat / r} * ${C_UNIT_CONVERSION} * ϕ)`;
            const y2 = `(${y1}) / (1 - ϕ)`;
            const y3 = `(${3 * gammaSat / r} * ${C_UNIT_CONVERSION} * ϕ + (${K_DEPLETION} * ${mw} / (${rpe} ** 3)) / ϕ)`;
            formulaDisplay.innerHTML = `<span style="color:blue;">${y1}</span>, <span style="color:red;">${y2}</span>, and <span style="color:green;">${y3}</span>`;
        }

        function updatePhimaxDisplay() {
            const gammaSat = parseFloat(sliderGammaSat.value);
            const r = parseFloat(sliderR.value);
            const rpe = parseFloat(sliderRpe.value);
            const mw = parseFloat(sliderMw.value);
            const A = (3 * gammaSat / r) * C_UNIT_CONVERSION;
            const B = K_DEPLETION * mw / (rpe ** 3);
            phi_max = Math.sqrt(B / A);
            phimaxDisplay.textContent = `ϕ_max = ${phi_max}`;
        }

        function updateRangeDisplay() {
            const phi = parseFloat(sliderPhi.value);
            const gammaSat = parseFloat(sliderGammaSat.value);
            const r = parseFloat(sliderR.value);
            const rpe = parseFloat(sliderRpe.value);
            const mw = parseFloat(sliderMw.value);
            const y2 = (3 * gammaSat / r) * C_UNIT_CONVERSION * phi / (1 - phi);
            const y3 = (3 * gammaSat / r) * C_UNIT_CONVERSION * phi + (K_DEPLETION * mw / (rpe ** 3)) / phi;
            rangeDisplay.textContent = `Stability range: ${y2} - ${y3}`;
        }

        function updatePlot() {
            const gammaSat = parseFloat(sliderGammaSat.value);
            const r = parseFloat(sliderR.value);
            const rpe = parseFloat(sliderRpe.value);
            const mw = parseFloat(sliderMw.value);
            const phi = parseFloat(sliderPhi.value);
            const phiAxis = parseFloat(sliderPhiAxis.value);
            const cAxis = parseFloat(sliderCAxis.value);
            const y1 = (3 * gammaSat / r) * C_UNIT_CONVERSION * phi;
            const y2 = y1 / (1 - phi);
            const y3 = (3 * gammaSat / r) * C_UNIT_CONVERSION * phi + (K_DEPLETION * mw / (rpe ** 3)) / phi;
            const x = Array.from({ length: 100 }, (_, i) => i / 99 * phiAxis);
            const y1Data = x.map(y1);
            const y2Data = x.map(y2);
            const y3Data = x.map(y3);
