            const shadedRegionLayout = { shapes: [{ type: 'line', x0: phi, x1: phi, y0: 0, y1: cAxis, line: { color: 'grey', dash: 'dash' } }] };
            Plotly.addTraces(plotContainer, shadedRegionData, shadedRegionLayout);
            const markerData = [
