            updateCanvas();
        }

        function startJoystickDrag(event) {
            joystickKnob.style.top = `${event.clientY - joystickContainer.offsetTop}px`;
            joystickKnob.style.left = `${event.clientX - joystickContainer.offsetLeft}px`;
            v0x = (event.clientX - joystickContainer.offsetLeft) / 50;
            v0y = (event.clientY - joystickContainer.offsetTop) / 50;
            labelV0X.textContent = `v0x: ${v0x.toFixed(2)}`;
            labelV0Y.textContent = `v0y: ${v0y.toFixed(2)}`;
            updateSimulation();
        }

        function updateJoystickPosition(event) {
            joystickKnob.style.top = `${event.clientY - joystickContainer.offsetTop}px`;
            joystickKnob.style.left = `${event.clientX - joystickContainer.offsetLeft}px`;
            v0x = (event.clientX - joystickContainer.offsetLeft) / 50;
            v0y = (event.clientY - joystickContainer.offsetTop) / 50;
            labelV0X.textContent = `v0x: ${v0x.toFixed(2)}`;
            labelV0Y.textContent = `v0y: ${v0y.toFixed(2)}`;
            updateSimulation();
        }

        function stopJoystickDrag() {
            joystickKnob.style.top = `${joystickContainer.offsetHeight / 2}px`;
            joystickKnob.style.left = `${joystickContainer.offsetWidth / 2}px`;
        }

        function updateTextDisplay() {
            const energy = 0.5 * m1 * (v1 + v0x) ** 2 + 0.5 * m2 * (v2 + v0y) ** 2;
            textDisplay.textContent = `v0: ${Math.sqrt(v0x ** 2 + v0y ** 2).toFixed(2)} m/s, v1: ${Math.sqrt((v1 + v0x) ** 2).toFixed(2)} m/s, v2: ${Math.sqrt((v2 + v0y) ** 2).toFixed(2)} m/s, Energy: ${energy.toFixed(2)} J`;
        }

        function updateCanvas() {
            const canvas = document.getElementById('canvas');
            const ctx = canvas.getContext('2d');
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // Draw particles
            ctx.fillStyle = '#FF0000';
            ctx.beginPath();
            ctx.arc(400, 300, 20, 0, Math.PI * 2);
            ctx.fill();

            ctx.fillStyle = '#0000FF';
            ctx.beginPath();
            ctx.arc(500, 300, 20, 0, Math.PI * 2);
            ctx.fill();

            // Draw trajectories
            ctx.strokeStyle = '#FF0000';
            ctx.setLineDash([5, 5]);
            ctx.beginPath();
            ctx.moveTo(400, 300);
            ctx.lineTo(400 + (v1 + v0x) * (t + 10), 300);
            ctx.stroke();

            ctx.strokeStyle = '#0000FF';
            ctx.setLineDash([5, 5]);
            ctx.beginPath();
            ctx.moveTo(500, 300);
            ctx.lineTo(500 + (v2 + v0y) * (t + 10), 300);
            ctx.stroke();

            // Draw observer velocity vector
            ctx.fillStyle = '#000';
            ctx.beginPath();
            ctx.moveTo(400, 300);
            ctx.lineTo(400 + v0x * 100, 300 + v0y * 100);
            ctx.stroke();
            ctx.beginPath();
            ctx.moveTo(400 + v0x * 100, 300 + v0y * 100);
            ctx.lineTo(400 + v0x * 100, 300 + v0y * 100 - 10);
            ctx.lineTo(400 + v0x * 100 - 10, 300 + v0y * 100 - 10);
            ctx.fill();

            // Draw text
            ctx.font = '18px Arial';
            ctx.fillStyle = '#000';
            ctx.textAlign = 'left';
            ctx.textBaseline = 'top';
            ctx.fillText(`v0: ${Math.sqrt(v0x ** 2 + v0y ** 2).toFixed(2)} m/s`, 20, 20);
            ctx.fillText(`v1: ${Math.sqrt((v1 + v0x) ** 2).toFixed(2)} m/s`, 20, 40);
            ctx.fillText(`v2: ${Math.sqrt((v2 + v0y) ** 2).toFixed(2)} m/s`, 20, 60);
            ctx.fillText(`Energy: ${0.5 * m1 * (v1 + v0x) ** 2 + 0.5 * m2 * (v2 + v0y) ** 2}. J`, 20, 80);
        }

        updateSimulation();
    </script>
</body>
</html>
