<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Cryptographic Shift Demo</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            background-color: #f4f4f9;
            margin: 0;
            padding: 20px;
        }
        .main-container {
            display: flex;
            flex-direction: column;
            gap: 20px;
            max-width: 600px;
            margin: 0 auto;
        }
        .control-panel, .display-area {
            background-color: white;
            padding: 15px;
            border-radius: 5px;
        }
        .control-panel {
            display: flex;
            flex-direction: column;
            gap: 10px;
        }
        .alphabet-mapping {
            display: flex;
            flex-direction: column;
            gap: 5px;
        }
        .alphabet-row {
            display: flex;
        }
        .alphabet-row span {
            font-family: monospace;
            padding: 5px;
            border-right: 1px solid #ccc;
        }
        .alphabet-row span:last-child {
            border-right: none;
        }
        #plaintext-alphabet span {
            color: blue;
        }
        #ciphertext-alphabet span {
            color: green;
        }
        .key-info p {
            margin: 0;
        }
        .text-output p {
            margin: 0;
        }
        .text-output span:first-child {
            color: blue;
        }
        .text-output span:last-child {
            color: green;
        }
    </style>
</head>
<body>
    <div class="main-container">
        <div class="control-panel">
            <label for="shift-slider">Shift:</label>
            <input type="range" id="shift-slider" min="0" max="25" step="1" value="12">
            <input type="text" id="text-input" list="phrase-list" value="abcd">
            <datalist id="phrase-list">
                <option value="abcd">
                <option value="test phrase">
                <option value="to be or not to be">
                <option value="the quick brown fox jumped over the lazy dog">
            </datalist>
        </div>
        <div class="display-area">
            <div class="alphabet-mapping">
                <div id="plaintext-alphabet" class="alphabet-row"></div>
                <div id="ciphertext-alphabet" class="alphabet-row"></div>
            </div>
            <div class="key-info">
                <p id="encryption-key-display">encryption key: 12</p>
                <p id="decryption-key-display">decryption key: 14</p>
            </div>
            <div class="text-output">
                <p id="plaintext-output">plaintext : abcd</p>
                <p id="ciphertext-output">ciphertext: MNOP</p>
            </div>
        </div>
    </div>

    <script>
        function updateCipher() {
            const shiftSlider = document.getElementById('shift-slider');
            const textInput = document.getElementById('text-input');
            const shiftValue = parseInt(shiftSlider.value);
            const text = textInput.value;

            updateAlphabetMapping(shiftValue);
            updateKeyDisplays(shiftValue);
            updateTextOutput(text, shiftValue);
        }

        function updateAlphabetMapping(shiftValue) {
            const alphabet = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
            const shiftedAlphabet = alphabet.slice(shiftValue) + alphabet.slice(0, shiftValue);

            const plaintextAlphabet = document.getElementById('plaintext-alphabet');
            const ciphertextAlphabet = document.getElementById('ciphertext-alphabet');

            plaintextAlphabet.innerHTML = '';
            ciphertextAlphabet.innerHTML = '';

            for (let i = 0; i < 26; i++) {
                const plainSpan = document.createElement('span');
                plainSpan.textContent = alphabet[i];
                plaintextAlphabet.appendChild(plainSpan);

                const cipherSpan = document.createElement('span');
                cipherSpan.textContent = shiftedAlphabet[i];
                ciphertextAlphabet.appendChild(cipherSpan);
            }
        }

        function updateKeyDisplays(shiftValue) {
            const encryptionKeyDisplay = document.getElementById('encryption-key-display');
            const decryptionKeyDisplay = document.getElementById('decryption-key-display');

            encryptionKeyDisplay.textContent = `encryption key: ${shiftValue}`;
            decryptionKeyDisplay.textContent = `decryption key: ${(26 - shiftValue) % 26}`;
        }

        function updateTextOutput(text, shiftValue) {
            const plaintextOutput = document.getElementById('plaintext-output');
            const ciphertextOutput = document.getElementById('ciphertext-output');

            plaintextOutput.textContent = `plaintext : ${text}`;
            ciphertextOutput.textContent = `ciphertext: ${encrypt(text, shiftValue)}`;
        }

        function encrypt(text, shift) {
            let result = '';
            for (let char of text) {
                if (char >= 'a' && char <= 'z') {
                    const index = char.charCodeAt(0) - 'a'.charCodeAt(0);
                    const shiftedIndex = (index + shift) % 26;
                    result += String.fromCharCode('A'.charCodeAt(0) + shiftedIndex);
                } else {
                    result += char;
                }
            }
            return result;
        }

        document.getElementById('shift-slider').addEventListener('input', updateCipher);
        document.getElementById('text-input').addEventListener('input', updateCipher);

        window.onload = updateCipher;
    </script>
</body>
</html>
