<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Inclined Plane Simulation</title>
    <style>
        body {
            display: flex;
            margin: 0;
            height: 100vh;
            font-family: Arial, sans-serif;
        }
        #control-panel {
            width: 200px;
            padding: 20px;
            background-color: #f0f0f0;
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #slider-angle {
            writing-mode: bt-lr;
            width: 200px;
            height: 200px;
            transform: rotate(270deg);
        }
        #animation-controls {
            margin-top: 20px;
        }
        #animation-controls button {
            margin: 5px;
            padding: 10px;
            font-size: 16px;
        }
        #visualization-area {
            flex-grow: 1;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
        }
        #info-display {
            font-size: 18px;
            margin-bottom: 20px;
        }
        #p5-canvas {
            border: 1px solid #000;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
    <script src="https://polyfill.io/v3/polyfill.min.js?features=es6"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
</head>
<body>
    <div id="control-panel">
        <label for="slider-angle">slope θ</label>
        <input type="range" id="slider-angle" min="10" max="80" step="1" value="30">
        <div id="animation-controls">
            <p>release block</p>
            <button id="btn-play">▶</button>
            <button id="btn-pause">||</button>
            <button id="btn-reset">|<</button>
        </div>
    </div>
    <div id="visualization-area">
        <p id="info-display">slope = \\( \\theta \\) = 30°; acceleration = \\( g \\sin(\\theta) \\) = 4.905 m/s²</p>
        <canvas id="p5-canvas"></canvas>
    </div>

    <script>
        const g = 9.81;
        let theta = 30;
        let isPlaying = false;
        let startTime = 0;
        let blockPosition = 0;
        let canvas;

        function setup() {
            canvas = createCanvas(window.innerWidth - 200, window.innerHeight);
            canvas.parent('visualization-area');
            updateInfoDisplay();
        }

        function draw() {
            background(220);
            translate(100, height - 100);
            rotate(radians(theta));

            // Draw inclined plane
            fill('lightblue');
            stroke('yellow');
            strokeWeight(2);
            triangle(0, 0, 200, 0, 0, 200);

            // Draw block
            fill('darkgrey');
            stroke('black');
            rect(0, blockPosition, 20, 20);

            // Draw angle marker
            noFill();
            stroke('black');
            arc(0, 0, 30, 30, 0, radians(theta));
            textSize(14);
            textAlign(CENTER, CENTER);
            text('θ', 15, 15);

            // Draw coordinate axes
            stroke('black');
            strokeWeight(1);
            line(0, 0, 200, 0); // x-axis
            line(0, 0, 0, -200); // y-axis

            // Draw force vectors
            drawArrow(0, 0, 0, -50, 'red', 'F_grav=mg');
            drawArrow(0, 0, 0, -50 * cos(radians(theta)), 'deepskyblue', 'normal force');
            drawArrow(0, 0, 50 * sin(radians(theta)), -50 * cos(radians(theta)), 'purple', 'F_net = m g sin(θ)');

            // Draw vector decomposition
            stroke('black');
            strokeDasharray(5);
            line(0, 0, 0, -50 * cos(radians(theta)));
            line(0, -50 * cos(radians(theta)), 50 * sin(radians(theta)), -50 * cos(radians(theta)));

            textSize(12);
            textAlign(RIGHT, CENTER);
            text('m g cos(θ)', 0, -50 * cos(radians(theta)) / 2);
            arc(0, -50 * cos(radians(theta)), 30, 30, 0, radians(theta));

            if (isPlaying) {
                let elapsedTime = millis() - startTime;
                let acceleration = g * sin(radians(theta));
                blockPosition = 0.5 * acceleration * pow(elapsedTime / 1000, 2);
                if (blockPosition > 200) {
                    blockPosition = 200;
                    isPlaying = false;
                }
            }
        }

        function drawArrow(x1, y1, x2, y2, color, label) {
            stroke(color);
            strokeWeight(2);
            line(x1, y1, x2, y2);
            push();
            translate(x2, y2);
            rotate(atan2(y2 - y1, x2 - x1));
            line(0, -10, 10, 0);
            line(0, -10, -10, 0);
            pop();
            textSize(14);
            textAlign(CENTER, BOTTOM);
            text(label, (x1 + x2) / 2, (y1 + y2) / 2);
        }

        function updateInfoDisplay() {
            const acceleration = (g * sin(radians(theta))).toFixed(3);
            document.getElementById('info-display').innerText = `slope = \\( \\theta \\) = ${theta}°; acceleration = \\( g \\sin(\\theta) \\) = ${acceleration} m/s²`;
            MathJax.typesetPromise([document.getElementById('info-display')]);
        }

        document.getElementById('slider-angle').addEventListener('input', (event) => {
            theta = parseInt(event.target.value);
            blockPosition = 0;
            isPlaying = false;
            updateInfoDisplay();
            redraw();
        });

        document.getElementById('btn-play').addEventListener('click', () => {
            isPlaying = true;
            startTime = millis();
        });

        document.getElementById('btn-pause').addEventListener('click', () => {
            isPlaying = false;
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            isPlaying = false;
            blockPosition = 0;
            redraw();
        });
    </script>
</body>
</html>
