                [9, 12, 5, 1, 14, 7, 6, 0, 8, 3, 10, 2, 13, 15, 4, 11]
            ],
            [
                [11, 10, 4, 7, 6, 9, 3, 12, 15, 14, 8, 13, 1, 0, 5, 2],
                [13, 8, 11, 5, 6, 15, 0, 3, 4, 7, 2, 12, 1, 10, 14, 9],
                [10, 6, 9, 0, 12, 11, 7, 13, 15, 1, 3, 14, 5, 2, 8, 4],
                [3, 15, 0, 6, 10, 1, 13, 8, 9, 4, 5, 11, 12, 7, 2, 14]
            ],
            [
                [2, 12, 4, 1, 7, 10, 11, 6, 8, 5, 3, 15, 13, 0, 14, 9],
                [14, 11, 2, 12, 4, 7, 13, 1, 5, 0, 15, 10, 3, 9, 8, 6],
                [4, 2, 1, 11, 10, 13, 7, 8, 15, 9, 12, 5, 6, 3, 0, 14],
                [11, 8, 12, 7, 1, 14, 2, 13, 6, 15, 0, 9, 10, 4, 3, 5]
            ],
            [
                [12, 1, 10, 15, 9, 2, 6, 8, 0, 13, 3, 4, 14, 7, 5, 11],
                [10, 15, 4, 2, 7, 12, 9, 5, 6, 1, 13, 14, 0, 11, 3, 8],
                [9, 14, 15, 5, 2, 8, 12, 3, 7, 0, 4, 10, 1, 13, 11, 6],
                [4, 3, 2, 12, 9, 5, 15, 10, 11, 14, 1, 7, 6, 0, 8, 13]
            ]
        ];
        const KEY_SCHEDULE_ROUNDS = [1, 1, 2, 2, 2, 2, 2, 2, 1, 2, 2, 2, 2, 2, 2, 1];

        function updateDisplay() {
            const message = document.getElementById('input-message').value;
            const key = document.getElementById('input-key').value;
            const steps = parseInt(document.querySelector('input[name="steps"]:checked').value);

            let binaryMessage = '';
            for (let i = 0; i < message.length; i++) {
                binaryMessage += message.charCodeAt(i).toString(2).padStart(8, '0');
            }
            binaryMessage = binaryMessage.padEnd(Math.ceil(binaryMessage.length / 64) * 64, '0');
            const messageBlocks = [];
            for (let i = 0; i < binaryMessage.length; i += 64) {
                messageBlocks.push(binaryMessage.slice(i, i + 64));
            }

            let binaryKey = '';
            for (let i = 0; i < key.length; i++) {
                binaryKey += key.charCodeAt(i).toString(2).padStart(8, '0');
            }
            binaryKey = binaryKey.padEnd(64, '0');

            const C = [];
            const D = [];
            let keySchedule = [];
            const permutedKey = permute(binaryKey, PC1);
            const C0 = permutedKey.slice(0, 28);
            const D0 = permutedKey.slice(28);

            for (let i = 0; i < 16; i++) {
                C[i] = shiftLeft(C0, KEY_SCHEDULE_ROUNDS[i]);
                D[i] = shiftLeft(D0, KEY_SCHEDULE_ROUNDS[i]);
                keySchedule.push(permute(C[i] + D[i], PC2));
            }

            const outputResults = document.getElementById('output-results');
            outputResults.textContent = '';

            for (let s = 2; s <= steps; s += 2) {
                let currentStepCiphertext = '';
                for (let block of messageBlocks) {
                    let L = block.slice(0, 32);
                    let R = block.slice(32);
                    for (let i = 0; i < s; i++) {
                        const expandedR = permute(R, E);
                        const xorResult = xor(expandedR, keySchedule[i]);
                        const subboxed = subbox(xorResult);
                        const permutedSubboxed = permute(subboxed, P);
                        const newR = xor(L, permutedSubboxed);
                        L = R;
                        R = newR;
                    }
                    const finalBlock = permute(R + L, FP);
                    currentStepCiphertext += finalBlock.match(/.{1,4}/g).join(' ') + ' ';
                }
                outputResults.textContent += `${s.toString().padStart(2, ' ')}  ${currentStepCiphertext.trim()}\n`;
            }
        }

        function permute(block, table) {
            return table.map(index => block[index - 1]).join('');
        }

        function shiftLeft(block, shifts) {
            return block.slice(shifts) + block.slice(0, shifts);
        }

        function xor(a, b) {
            return a.split('').map((bit, index) => (parseInt(bit) ^ parseInt(b[index])).toString()).join('');
        }

        function subbox(xorResult) {
            let result = '';
            for (let i = 0; i < 8; i++) {
                const row = parseInt(xorResult.slice(i * 6, i * 6 + 1) + xorResult.slice(i * 6 + 5), 2);
                const col = parseInt(xorResult.slice(i * 6 + 1, i * 6 + 5), 2);
                result += S[i][row][col].toString(2).padStart(4, '0');
            }
            return result;
        }

        document.getElementById('input-message').addEventListener('input', updateDisplay);
        document.getElementById('input-key').addEventListener('input', updateDisplay);
        document.querySelectorAll('input[name="steps"]').forEach(input => {
            input.addEventListener('change', updateDisplay);
        });

        updateDisplay();
    </script>
</body>
</html>
