</head>
<body>
    <div id="control-panel">
        <div id="elements-control" class="control-group">
            <label for="slider-elements">elements (set, then click "randomize")</label>
            <input type="range" id="slider-elements" min="2" max="50" step="1" value="10">
            <span id="elements-value">10</span>
        </div>
        <div id="step-control" class="control-group">
            <label for="slider-step">step</label>
            <input type="range" id="slider-step" min="1" max="1" step="1" value="1">
            <button id="btn-play-pause">▶</button>
            <button id="btn-step-backward">-</button>
            <button id="btn-step-forward">+</button>
            <button id="btn-reset-step">«</button>
            <span id="step-value">1</span>
        </div>
        <div id="sort-control" class="control-group">
            <label>sort</label>
            <button id="btn-shellsort" class="sort-btn active">shellsort</button>
            <button id="btn-bubblesort" class="sort-btn">bubblesort</button>
            <button id="btn-insertionsort" class="sort-btn">insertionsort</button>
            <button id="btn-cocktail" class="sort-btn">cocktail</button>
            <button id="btn-bogosort" class="sort-btn">bogosort</button>
            <button id="btn-randomize">randomize</button>
        </div>
    </div>
    <div id="visualization-area">
        <canvas id="bar-chart-canvas"></canvas>
    </div>

    <script>
        let numElements = 10;
        let currentAlgorithm = 'shellsort';
        let sortHistory = [];
        let currentStep = 1;
        let isPlaying = false;
        let animationTimer;

        const canvasWidth = 800;
        const canvasHeight = 400;
        const margins = 50;

        function setup() {
            createCanvas(canvasWidth, canvasHeight);
            noLoop();
            randomizeArray();
        }

        function draw() {
            background(255);
            drawYAxis();
            drawBars();
        }

        function drawYAxis() {
            stroke(0);
            line(margins, margins, margins, canvasHeight - margins);

            for (let i = 0; i <= 20; i += 5) {
                let y = map(i, 0, 20, canvasHeight - margins, margins);
                line(margins - 5, y, margins, y);
                textSize(12);
                textAlign(RIGHT, CENTER);
                text(i, margins - 10, y);
            }
        }

        function drawBars() {
            let data = sortHistory[currentStep - 1];
            let barWidth = (canvasWidth - margins * 2) / data.length;

            for (let i = 0; i < data.length; i++) {
                let x = margins + i * barWidth;
                let y = map(data[i], 1, numElements, canvasHeight - margins, margins);
                let barHeight = canvasHeight - margins - y;

                fill(map(data[i], 1, numElements, 240, 0), 100, 100);
                stroke(0);
                rect(x, y, barWidth, barHeight);
            }
        }

        function randomizeArray() {
            numElements = parseInt(document.getElementById('slider-elements').value);
            if (currentAlgorithm === 'bogosort') {
                document.getElementById('slider-elements').disabled = true;
                document.getElementById('slider-elements').value = 7;
                numElements = 7;
            } else {
                document.getElementById('slider-elements').disabled = false;
            }
            document.getElementById('elements-value').textContent = numElements;

            let array = Array.from({ length: numElements }, (_, i) => i + 1);
            shuffle(array);

            sortHistory = [array.slice()];
            currentStep = 1;
            document.getElementById('slider-step').max = 1;
            document.getElementById('slider-step').value = 1;
            document.getElementById('step-value').textContent = 1;

            if (isPlaying) {
                stopAnimation();
            }

            redraw();
        }

        function shuffle(array) {
            for (let i = array.length - 1; i > 0; i--) {
                const j = Math.floor(Math.random() * (i + 1));
                [array[i], array[j]] = [array[j], array[i]];
            }
        }

        function startAnimation() {
            animationTimer = setInterval(() => {
                currentStep++;
                if (currentStep > sortHistory.length) {
                    stopAnimation();
                } else {
                    document.getElementById('slider-step').value = currentStep;
                    document.getElementById('step-value').textContent = currentStep;
                    redraw();
                }
            }, 200);
            isPlaying = true;
            document.getElementById('btn-play-pause').textContent = '❚❚';
        }

        function stopAnimation() {
            clearInterval(animationTimer);
            isPlaying = false;
            document.getElementById('btn-play-pause').textContent = '▶';
        }

        function shellsort(arr) {
            let n = arr.length;
            for (let gap = Math.floor(n / 2); gap > 0; gap = Math.floor(gap / 2)) {
                for (let i = gap; i < n; i++) {
                    let temp = arr[i];
                    let j;
                    for (j = i; j >= gap && arr[j - gap] > temp; j -= gap) {
                        arr[j] = arr[j - gap];
                    }
                    arr[j] = temp;
                    sortHistory.push(arr.slice());
                }
            }
        }

        function bubblesort(arr) {
            let n = arr.length;
            for (let i = 0; i < n - 1; i++) {
                for (let j = 0; j < n - i - 1; j++) {
                    if (arr[j] > arr[j + 1]) {
                        [arr[j], arr[j + 1]] = [arr[j + 1], arr[j]];
                    }
                    sortHistory.push(arr.slice());
                }
            }
        }

        function insertionsort(arr) {
            let n = arr.length;
            for (let i = 1; i < n; i++) {
                let key = arr[i];
                let j = i - 1;
                while (j >= 0 && arr[j] > key) {
                    arr[j + 1] = arr[j];
                    j = j - 1;
                }
                arr[j + 1] = key;
                sortHistory.push(arr.slice());
            }
        }

        function cocktailshakerSort(arr) {
            let n = arr.length;
            let swapped = true;
            let start = 0;
            let end = n - 1;
            while (swapped) {
                swapped = false;
                for (let i = start; i < end; i++) {
                    if (arr[i] > arr[i + 1]) {
                        [arr[i], arr[i + 1]] = [arr[i + 1], arr[i]];
                        swapped = true;
                    }
                    sortHistory.push(arr.slice());
                }
                if (!swapped) break;
                swapped = false;
                end--;
                for (let i = end - 1; i >= start; i--) {
                    if (arr[i] > arr[i + 1]) {
                        [arr[i], arr[i + 1]] = [arr[i + 1], arr[i]];
                        swapped = true;
                    }
                    sortHistory.push(arr.slice());
                }
                start++;
            }
        }

        function bogosort(arr) {
            let n = arr.length;
            while (!isSorted(arr)) {
                shuffle(arr);
                sortHistory.push(arr.slice());
            }
        }

        function isSorted(arr) {
            for (let i = 0; i < arr.length - 1; i++) {
                if (arr[i] > arr[i + 1]) return false;
            }
            return true;
        }

        document.getElementById('slider-elements').addEventListener('input', () => {
            document.getElementById('elements-value').textContent = document.getElementById('slider-elements').value;
        });

        document.getElementById('btn-randomize').addEventListener('click', () => {
            randomizeArray();
            switch (currentAlgorithm) {
                case 'shellsort':
                    shellsort(sortHistory[0].slice());
                    break;
                case 'bubblesort':
                    bubblesort(sortHistory[0].slice());
                    break;
                case 'insertionsort':
                    insertionsort(sortHistory[0].slice());
                    break;
                case 'cocktail':
                    cocktailshakerSort(sortHistory[0].slice());
                    break;
                case 'bogosort':
                    bogosort(sortHistory[0].slice());
                    break;
            }
            document.getElementById('slider-step').max = sortHistory.length;
            redraw();
        });

        document.querySelectorAll('.sort-btn').forEach(button => {
            button.addEventListener('click', () => {
                document.querySelector('.sort-btn.active').classList.remove('active');
                button.classList.add('active');
                currentAlgorithm = button.id.replace('btn-', '');
                randomizeArray();
            });
        });

        document.getElementById('slider-step').addEventListener('input', () => {
            currentStep = parseInt(document.getElementById('slider-step').value);
            document.getElementById('step-value').textContent = currentStep;
            redraw();
        });

        document.getElementById('btn-play-pause').addEventListener('click', () => {
            if (isPlaying) {
                stopAnimation();
            } else {
                startAnimation();
            }
        });

        document.getElementById('btn-step-backward').addEventListener('click', () => {
            if (currentStep > 1) {
                currentStep--;
                document.getElementById('slider-step').value = currentStep;
                document.getElementById('step-value').textContent = currentStep;
                redraw();
            }
        });

        document.getElementById('btn-step-forward').addEventListener('click', () => {
            if (currentStep < sortHistory.length) {
                currentStep++;
                document.getElementById('slider-step').value = currentStep;
                document.getElementById('step-value').textContent = currentStep;
                redraw();
            }
        });

        document.getElementById('btn-reset-step').addEventListener('click', () => {
            currentStep = 1;
            document.getElementById('slider-step').value = currentStep;
            document.getElementById('step-value').textContent = currentStep;
            redraw();
        });

        window.onload = () => {
            setup();
            draw();
        };
    </script>
</body>
</html>
