    <!-- CDN for Plotly.js -->
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        const sliderX1 = document.getElementById('slider-x1');
        const sliderV1 = document.getElementById('slider-v1');
        const sliderX2 = document.getElementById('slider-x2');
        const sliderV2 = document.getElementById('slider-v2');
        const valueX1 = document.getElementById('value-x1');
        const valueV1 = document.getElementById('value-v1');
        const valueX2 = document.getElementById('value-x2');
        const valueV2 = document.getElementById('value-v2');
        const resultText = document.getElementById('result-text');
        const btnReset = document.getElementById('btn-reset');

        function updateValues() {
            valueX1.textContent = sliderX1.value;
            valueV1.textContent = parseFloat(sliderV1.value).toFixed(2);
            valueX2.textContent = sliderX2.value;
            valueV2.textContent = parseFloat(sliderV2.value).toFixed(2);
        }

        function calculateIntersection() {
            const x1 = parseFloat(sliderX1.value);
            const v1 = parseFloat(sliderV1.value);
            const x2 = parseFloat(sliderX2.value);
            const v2 = parseFloat(sliderV2.value);

            if (v1 === v2) {
                resultText.textContent = "The runners' paths never intersect";
                return null;
            }

            const T = (x1 - x2) / (v2 - v1);
            const X = x1 + v1 * T;

            if (T < 0 || T > 1000) {
                resultText.textContent = "The runners' paths never intersect";
                return null;
            }

            resultText.textContent = `T = ${T.toFixed(2)} s | X = ${X.toFixed(2)} m`;
            return { T, X };
        }

        function updatePlot(intersection) {
            const x1 = parseFloat(sliderX1.value);
            const v1 = parseFloat(sliderV1.value);
            const x2 = parseFloat(sliderX2.value);
            const v2 = parseFloat(sliderV2.value);

            const trace1 = {
                x: [0, 1000],
                y: [x1, x1 + v1 * 1000],
                mode: 'lines',
                type: 'scatter',
                line: { color: 'rgb(70, 130, 180)' }
            };

            const trace2 = {
                x: [0, 1000],
                y: [x2, x2 + v2 * 1000],
                mode: 'lines',
                type: 'scatter',
                line: { color: 'rgb(255, 165, 0)' }
            };

            let trace3 = {};
            if (intersection) {
                trace3 = {
                    x: [intersection.T],
                    y: [intersection.X],
                    mode: 'markers',
                    type: 'scatter',
                    marker: { color: 'green', size: 10 }
                };
            }

            const data = [trace1, trace2, trace3];
            const layout = {
                title: 'Runner Positions vs Time',
                xaxis: { title: 't', range: [0, 1000] },
                yaxis: { title: 'distance', autorange: true },
                showlegend: false,
                margin: { t: 20, r: 20, b: 40, l: 60 },
                plot_bgcolor: 'white',
                paper_bgcolor: 'white',
                grid: { xgap: 2, ygap: 2, zerolinecolor: 'gray', linecolor: 'lightgray' }
            };

            Plotly.newPlot('plot-container', data, layout);
        }

        function updateAll() {
            updateValues();
            const intersection = calculateIntersection();
            updatePlot(intersection);
        }

        sliderX1.addEventListener('input', updateAll);
        sliderV1.addEventListener('input', updateAll);
        sliderX2.addEventListener('input', updateAll);
        sliderV2.addEventListener('input', updateAll);

        btnReset.addEventListener('click', () => {
            sliderX1.value = 500;
            sliderV1.value = -10;
            sliderX2.value = 100;
            sliderV2.value = 5;
            updateAll();
        });

        // Initialize the plot and values
        updateAll();
    </script>
</body>
</html>
