            margin: 0;
            padding: 0;
            display: flex;
            flex-direction: column;
            align-items: center;
        }
        #control-panel {
            margin-top: 20px;
            display: flex;
            align-items: center;
        }
        #slider-value {
            margin: 0 10px;
        }
        #canvas-container {
            margin-top: 20px;
            border: 1px solid #000;
        }
    </style>
</head>
<body>
    <div id="control-panel">
        <label for="slider-phase">Phosphorus Cycle</label>
        <input type="range" id="slider-phase" min="0" max="8" step="1" value="0">
        <span id="slider-value">0</span>
        <button id="btn-reset">↻</button>
    </div>
    <div id="canvas-container"></div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let sliderPhase;
        let sliderValueDisplay;
        let resetButton;

        function setup() {
            const canvas = createCanvas(600, 500);
            canvas.parent('canvas-container');
            sliderPhase = select('#slider-phase');
            sliderValueDisplay = select('#slider-value');
            resetButton = select('#btn-reset');

            sliderPhase.input(updateSliderValue);
            resetButton.mousePressed(resetSlider);

            updateSliderValue();
        }

        function draw() {
            background(255);
            drawStaticScene();
            drawDynamicElements();
            drawTooltips();
        }

        function drawStaticScene() {
            // Sky
            fill(202, 233, 255);
            rect(0, 0, width, height / 2);

            // Ground
            fill(155, 118, 83);
            rect(0, height / 2, width, height / 2);

            // Sun
            fill(255, 221, 0);
            ellipse(width - 50, 50, 60, 60);
            // Sun rays
            stroke(255, 221, 0);
            strokeWeight(2);
            line(width - 50, 50, width - 30, 30);
            line(width - 50, 50, width - 70, 30);
            line(width - 50, 50, width - 30, 70);
            line(width - 50, 50, width - 70, 70);

            // Clouds
            fill(224, 224, 224);
            ellipse(100, 100, 100, 50);
            ellipse(150, 100, 100, 50);
            ellipse(125, 125, 100, 50);
            fill(80, 80, 80);
            textSize(16);
            textAlign(CENTER, CENTER);
            text("Clouds", 125, 100);

            // Rain
            stroke(0, 0, 255);
            line(100, 100, 110, 120);
            line(150, 100, 140, 120);

            // Rocks
            fill(90, 90, 90);
            ellipse(100, height / 2 + 50, 50, 30);
            ellipse(130, height / 2 + 50, 50, 30);
            ellipse(115, height / 2 + 70, 50, 30);
            fill(80, 80, 80);
            textSize(16);
            textAlign(CENTER, CENTER);
            text("Rocks", 115, height / 2 + 90);

            // Animal
            fill(255);
            noStroke();
            ellipse(width / 2, height / 2 + 50, 50, 30);
            fill(0);
            ellipse(width / 2 - 10, height / 2 + 40, 10, 10);
            ellipse(width / 2 + 10, height / 2 + 40, 10, 10);
            fill(0);
            rect(width / 2 - 10, height / 2 + 60, 20, 20);
            fill(0);
            ellipse(width / 2 - 5, height / 2 + 80, 5, 5);
            ellipse(width / 2 + 5, height / 2 + 80, 5, 5);
            fill(80, 80, 80);
            textSize(16);
            textAlign(CENTER, CENTER);
            text("Animals", width / 2, height / 2 + 100);

            // Plants
            fill(144, 238, 144);
            triangle(width / 2 + 60, height / 2 + 60, width / 2 + 80, height / 2 + 40, width / 2 + 100, height / 2 + 60);
            triangle(width / 2 + 40, height / 2 + 60, width / 2 + 60, height / 2 + 40, width / 2 + 80, height / 2 + 60);
            triangle(width / 2 + 20, height / 2 + 60, width / 2 + 40, height / 2 + 40, width / 2 + 60, height / 2 + 60);
            fill(0, 100, 0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text("Plants", width / 2 + 60, height / 2 + 70);

            // Water
            fill(0, 0, 255, 150);
            ellipse(50, height - 50, 100, 50);
            ellipse(100, height - 50, 100, 50);
            fill(0, 0, 139);
            textSize(16);
            textAlign(CENTER, CENTER);
            text("Water", 75, height - 50);
        }

        function drawDynamicElements() {
            const sliderValue = parseInt(sliderPhase.value());

            if (sliderValue >= 1) {
                drawArrow(100, 150, 100, height / 2 + 50, "Erosion");
            }
            if (sliderValue >= 2) {
                drawArrow(100, height / 2 + 50, 100, height - 50, "Runoff");
            }
            if (sliderValue >= 3) {
                drawArrow(100, height - 50, width / 2, height / 2 + 50, "Soil");
            }
            if (sliderValue >= 4) {
                drawArrow(width / 2, height / 2 + 50, 100, height - 50, "Leaching");
            }
            if (sliderValue >= 5) {
                drawArrow(width / 2, height / 2 + 50, width / 2 + 60, height / 2 + 60, "Plant uptake");
            }
            if (sliderValue >= 6) {
                drawArrow(width / 2 + 60, height / 2 + 60, width / 2, height / 2 + 50, "Animals eat plants");
            }
            if (sliderValue >= 7) {
                drawArrow(width / 2, height / 2 + 50, width / 2, height / 2 + 100, "Decomposition");
            }
            if (sliderValue >= 8) {
                drawCurvedArrow(50, height - 50, 100, height / 2 + 50, "Geologic uplift");
            }
        }

        function drawArrow(x1, y1, x2, y2, label) {
            stroke(0);
            strokeWeight(2);
            line(x1, y1, x2, y2);
            fill(0);
            noStroke();
            triangle(x2, y2, x2 - 10, y2 - 10, x2 - 10, y2 + 10);
            textSize(12);
            textAlign(CENTER, CENTER);
            text(label, (x1 + x2) / 2, (y1 + y2) / 2);
        }

        function drawCurvedArrow(x1, y1, x2, y2, label) {
            stroke(0);
            strokeWeight(2);
            noFill();
            beginShape();
            vertex(x1, y1);
            bezierVertex(x1 + 50, y1 - 50, x2 - 50, y2 - 50, x2, y2);
            endShape();
            fill(0);
            noStroke();
            arc(x2, y2, 10, 10, PI, 0);
            textSize(12);
            textAlign(CENTER, CENTER);
            text(label, (x1 + x2) / 2, (y1 + y2) / 2);
        }

        function drawTooltips() {
            const tooltips = [
                { x: 125, y: 100, text: "Clouds: Water vapor condensed in the atmosphere." },
                { x: 115, y: height / 2 + 90, text: "Rocks: A primary reservoir of phosphorus in the form of phosphate." },
                { x: width / 2, y: height / 2 + 100, text: "Animals: Obtain phosphorus by eating plants or other animals." },
                { x: width / 2 + 60, y: height / 2 + 70, text: "Plants: Absorb phosphate from the soil to build organic molecules." },
                { x: 75, y: height - 50, text: "Water: Phosphorus is carried into water bodies by runoff and leaching." }
            ];

            tooltips.forEach(tooltip => {
                if (mouseX > tooltip.x - 50 && mouseX < tooltip.x + 50 && mouseY > tooltip.y - 20 && mouseY < tooltip.y + 20) {
                    fill(255, 255, 255, 150);
                    noStroke();
                    rect(mouseX + 10, mouseY + 10, 200, 50);
                    fill(0);
                    textSize(14);
                    textAlign(LEFT, TOP);
                    text(tooltip.text, mouseX + 15, mouseY + 15);
                }
            });
        }

        function updateSliderValue() {
            sliderValueDisplay.html(sliderPhase.value());
            redraw();
        }

        function resetSlider() {
            sliderPhase.value(0);
            updateSliderValue();
        }
    </script>
</body>
</html>
