            // Draw unmatched edges
            stroke('#cccccc');
            strokeWeight(2);
            for (let edge of edges) {
                if (!stepData.matching.includes(edge.u * vertices.length + edge.v)) {
                    line(vertices[edge.u].x, vertices[edge.u].y, vertices[edge.v].x, vertices[edge.v].y);
                }
            }

            // Draw matched edges
            const edgeColor = stepData.isFinal ? '#4d4dff' : '#ff4d4d';
            stroke(edgeColor);
            strokeWeight(6);
            for (let edgeIndex of stepData.matching) {
                const edge = edges.find(e => e.u * vertices.length + e.v === edgeIndex || e.v * vertices.length + e.u === edgeIndex);
                if (edge) {
                    line(vertices[edge.u].x, vertices[edge.u].y, vertices[edge.v].x, vertices[edge.v].y);
                }
            }

            // Draw blossom
            if (stepData.blossom) {
                fill('rgba(0, 0, 255, 0.3)');
                stroke(0);
                strokeWeight(1);
                beginShape();
                for (let vertexIndex of stepData.blossom.vertices) {
