            <canvas id="main-canvas"></canvas>
        </div>
    </div>

    <!-- Reset Icon -->
    <div class="reset-icon">+</div>

    <!-- Three.js and CSG Library -->
    <script type="module">
        import * as THREE from 'https://unpkg.com/three@0.158.0/build/three.module.js';
        import { CSG } from 'https://unpkg.com/three-bvh-csg@0.0.16/dist/index.module.js';
        import { OrbitControls } from 'https://unpkg.com/three@0.158.0/examples/jsm/controls/OrbitControls.js';

        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xe0e0e0);

        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.z = 15;

        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth, window.innerHeight);
        document.getElementById('main-canvas').appendChild(renderer.domElement);

        const controls = new OrbitControls(camera, renderer.domElement);

        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        let mainCylinderMesh, drillCylinderMesh, resultMesh, frontTube, backTube;

        function updateScene() {
            if (mainCylinderMesh) scene.remove(mainCylinderMesh);
            if (drillCylinderMesh) scene.remove(drillCylinderMesh);
            if (resultMesh) scene.remove(resultMesh);
            if (frontTube) scene.remove(frontTube);
            if (backTube) scene.remove(backTube);

            const cylinderRadius = parseFloat(document.getElementById('slider-cylinder-radius').value);
            const opacity = parseFloat(document.getElementById('slider-opacity').value);
            const drillRadius = parseFloat(document.getElementById('slider-drill-radius').value);
            const inclination = parseFloat(document.getElementById('slider-inclination').value);
            const rotation = parseFloat(document.getElementById('slider-rotation').value);
            const offset = parseFloat(document.getElementById('slider-offset').value);
            const boundaryOnly = document.getElementById('checkbox-boundary-only').checked;

            const mainCylinderGeometry = new THREE.CylinderGeometry(cylinderRadius, cylinderRadius, 8, 32);
            const mainCylinderMaterial = new THREE.MeshStandardMaterial({ color: 0xD2B48C, opacity: boundaryOnly ? 0.15 : opacity, transparent: true });
            mainCylinderMesh = new THREE.Mesh(mainCylinderGeometry, mainCylinderMaterial);
            scene.add(mainCylinderMesh);

            const drillCylinderGeometry = new THREE.CylinderGeometry(drillRadius, drillRadius, 10, 32);
            drillCylinderMesh = new THREE.Mesh(drillCylinderGeometry, new THREE.MeshBasicMaterial({ visible: false }));
            drillCylinderMesh.rotation.z = Math.PI / 2;
            drillCylinderMesh.rotation.x = inclination;
            drillCylinderMesh.rotation.y = rotation;
            drillCylinderMesh.position.y = offset;
            scene.add(drillCylinderMesh);

            const result = CSG.subtract(mainCylinderMesh, drillCylinderMesh);
            resultMesh = new THREE.Mesh(result.toBufferGeometry(), mainCylinderMaterial);
            resultMesh.visible = !boundaryOnly;
            scene.add(resultMesh);

            const boundaryPointsFront = [];
            const boundaryPointsBack = [];
            for (let t = 0; t <= 2 * Math.PI; t += 0.1) {
                const x = drillRadius * Math.cos(t);
                const z = drillRadius * Math.sin(t);
                const point = new THREE.Vector3(x, 0, z);
                point.applyMatrix4(drillCylinderMesh.matrixWorld);

                const r = cylinderRadius;
                const discriminant = r * r - (point.x * point.x + point.z * point.z);
                if (discriminant >= 0) {
                    const y1 = -Math.sqrt(discriminant);
                    const y2 = Math.sqrt(discriminant);
                    if (point.y > 0) {
                        boundaryPointsFront.push(new THREE.Vector3(point.x, y1, point.z));
                        boundaryPointsBack.push(new THREE.Vector3(point.x, y2, point.z));
                    } else {
                        boundaryPointsBack.push(new THREE.Vector3(point.x, y1, point.z));
                        boundaryPointsFront.push(new THREE.Vector3(point.x, y2, point.z));
                    }
                }
            }

            const tubeGeometryFront = new THREE.TubeGeometry(new THREE.CatmullRomCurve3(boundaryPointsFront), 64, 0.05);
            const tubeMaterialFront = new THREE.MeshBasicMaterial({ color: 0xFFA500 });
            frontTube = new THREE.Mesh(tubeGeometryFront, tubeMaterialFront);
            scene.add(frontTube);

            const tubeGeometryBack = new THREE.TubeGeometry(new THREE.CatmullRomCurve3(boundaryPointsBack), 64, 0.05);
            const tubeMaterialBack = new THREE.MeshBasicMaterial({ color: 0x4169E1 });
            backTube = new THREE.Mesh(tubeGeometryBack, tubeMaterialBack);
            scene.add(backTube);

            renderer.render(scene, camera);
        }

        function onSliderChange() {
            updateScene();
        }

        document.getElementById('slider-cylinder-radius').addEventListener('input', onSliderChange);
        document.getElementById('slider-opacity').addEventListener('input', onSliderChange);
        document.getElementById('slider-drill-radius').addEventListener('input', onSliderChange);
        document.getElementById('slider-inclination').addEventListener('input', onSliderChange);
        document.getElementById('slider-rotation').addEventListener('input', onSliderChange);
        document.getElementById('slider-offset').addEventListener('input', onSliderChange);
        document.getElementById('checkbox-boundary-only').addEventListener('change', onSliderChange);

        function updateLabel(event) {
            const value = event.target.value;
            const labelId = event.target.id.replace('slider-', 'label-');
            document.getElementById(labelId).textContent = value.toFixed(2);
        }

        document.getElementById('slider-cylinder-radius').addEventListener('input', updateLabel);
        document.getElementById('slider-opacity').addEventListener('input', updateLabel);
        document.getElementById('slider-drill-radius').addEventListener('input', updateLabel);
        document.getElementById('slider-inclination').addEventListener('input', updateLabel);
        document.getElementById('slider-rotation').addEventListener('input', updateLabel);
        document.getElementById('slider-offset').addEventListener('input', updateLabel);

        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        });

        updateScene();
    </script>
</body>
</html>
