            select('#value-hue').html(hue.toFixed(2));
            select('#value-saturation').html(saturation.toFixed(2));
            select('#value-brightness').html(brightness.toFixed(2));

            drawHueBar();
            drawSaturationBar(hue, brightness);
            drawBrightnessBar(hue, saturation);
            drawResultBar(hue, saturation, brightness);

            updateArrowPosition(hueArrow, hue, hueCanvas.width);
            updateArrowPosition(saturationArrow, saturation, saturationCanvas.width);
            updateArrowPosition(brightnessArrow, brightness, brightnessCanvas.width);
        }

        function drawHueBar() {
            for (let x = 0; x < width; x++) {
                let h = map(x, 0, width, 0, 1);
                stroke(h, 1, 1);
                line(x, 0, x, height);
            }
        }

        function drawSaturationBar(hue, brightness) {
            background(0);
            for (let x = 0; x < width; x++) {
                let s = map(x, 0, width, 0, 1);
                stroke(hue, s, brightness);
                line(x, 0, x, height);
            }
        }

        function drawBrightnessBar(hue, saturation) {
            background(0);
            for (let x = 0; x < width; x++) {
                let b = map(x, 0, width, 0, 1);
                stroke(hue, saturation, b);
                line(x, 0, x, height);
            }
        }

        function drawResultBar(hue, saturation, brightness) {
            background(0);
            fill(hue, saturation, brightness);
            rect(0, 0, width, height);
        }

        function updateArrowPosition(arrow, value, canvasWidth) {
            const arrowWidth = arrow.elt.offsetWidth;
            const arrowLeft = (value * canvasWidth) - (arrowWidth / 2);
            arrow.position(arrowLeft, -arrow.elt.offsetHeight - 5);
        }

        select('#slider-hue').input(updateVisuals);
        select('#slider-saturation').input(updateVisuals);
        select('#slider-brightness').input(updateVisuals);
        select('#btn-reset').mousePressed(() => {
            select('#slider-hue').value(0);
            select('#slider-saturation').value(0.5);
            select('#slider-brightness').value(1);
            updateVisuals();
        });
    </script>
