        }
        .main-container {
            display: flex;
            flex-direction: column;
            width: 80%;
            max-width: 600px;
            background-color: white;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }
        .controls-container {
            background-color: #f0f0f0;
            padding: 20px;
            border: 1px solid #ccc;
            position: relative;
        }
        .controls-container::after {
            content: '+';
            position: absolute;
            top: 10px;
            right: 10px;
            font-size: 24px;
            color: #333;
        }
        .control-row {
            display: flex;
            align-items: center;
            margin-bottom: 10px;
        }
        label {
            width: 30px;
            margin-right: 10px;
        }
        input[type="range"] {
            flex-grow: 1;
            margin-right: 10px;
        }
        span {
            width: 50px;
            text-align: right;
        }
        #plot-container {
            flex-grow: 1;
            padding: 20px;
        }
    </style>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
</head>
<body>
    <div class="main-container">
        <div id="controls-container">
            <div class="control-row">
                <label for="slider-alpha">α</label>
                <input type="range" id="slider-alpha" min="0.01" max="0.2" step="0.0001" value="0.05">
                <span id="value-alpha">0.05</span>
            </div>
            <div class="control-row">
                <label for="slider-beta">β</label>
                <input type="range" id="slider-beta" min="1" max="20" step="0.01" value="5">
                <span id="value-beta">5</span>
            </div>
            <div class="control-row">
                <label for="slider-gamma">γ</label>
                <input type="range" id="slider-gamma" min="100" max="2000" step="1" value="500">
                <span id="value-gamma">500</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        function solveODE(alpha, beta, gamma) {
            const h = 0.05;
            const tEnd = 20;
            const nSteps = Math.floor(tEnd / h);
            const t = new Array(nSteps + 1).fill(0).map((_, i) => i * h);
            const y = new Array(nSteps + 1).fill(0).map(() => [0, 0]);

            for (let i = 0; i < nSteps; i++) {
                const k1 = [h * f1(y[i][0], y[i][1], alpha, beta, gamma),
                            h * f2(y[i][0], y[i][1], alpha, beta, gamma)];
                const k2 = [h * f1(y[i][0] + k1[0] / 2, y[i][1] + k1[1] / 2, alpha, beta, gamma),
                            h * f2(y[i][0] + k1[0] / 2, y[i][1] + k1[1] / 2, alpha, beta, gamma)];
                const k3 = [h * f1(y[i][0] + k2[0] / 2, y[i][1] + k2[1] / 2, alpha, beta, gamma),
                            h * f2(y[i][0] + k2[0] / 2, y[i][1] + k2[1] / 2, alpha, beta, gamma)];
                const k4 = [h * f1(y[i][0] + k3[0], y[i][1] + k3[1], alpha, beta, gamma),
                            h * f2(y[i][0] + k3[0], y[i][1] + k3[1], alpha, beta, gamma)];

                y[i + 1] = [y[i][0] + (k1[0] + 2 * k2[0] + 2 * k3[0] + k4[0]) / 6,
                           y[i][1] + (k1[1] + 2 * k2[1] + 2 * k3[1] + k4[1]) / 6];
            }

            return { t, deltaT: y.map(row => row[0]), cX: y.map(row => row[1]) };
        }

        function f1(deltaT, cX, alpha, beta, gamma) {
            return gamma * cX * Math.exp(deltaT / (1 + deltaT / alpha)) - beta * deltaT;
        }

        function f2(deltaT, cX, alpha, beta, gamma) {
            return 1 - cX * Math.exp(deltaT / (1 + deltaT / alpha));
        }

        function updatePlot(alpha, beta, gamma) {
            const { t, deltaT, cX } = solveODE(alpha, beta, gamma);
            const scaledCX = cX.map(val => val * 1000);

            const trace1 = {
                x: t,
                y: deltaT,
                mode: 'lines',
                line: { color: 'orange', width: 2 }
            };

            const trace2 = {
                x: t,
                y: scaledCX,
                mode: 'lines',
                line: { color: 'steelblue', width: 2 }
            };

            const layout = {
                xaxis: { title: '', range: [0, 20], zeroline: true },
                yaxis: { title: '', range: [0, 100], zeroline: true },
                showlegend: false,
                margin: { l: 50, r: 20, b: 30, t: 20 },
                plot_bgcolor: '#f0f0f0',
                paper_bgcolor: 'white'
            };

            const annotations = [
                { text: 'ΔT', x: 0, y: 100, xref: 'paper', yref: 'y', xanchor: 'right', yanchor: 'top', showarrow: false, font: { color: 'purple' } },
                { text: '1000 C<sub>X</sub>', x: 0, y: 95, xref: 'paper', yref: 'y', xanchor: 'right', yanchor: 'top', showarrow: false, font: { color: 'purple' } }
            ];

            layout.annotations = annotations;

            Plotly.newPlot('plot-container', [trace1, trace2], layout);
        }

        document.getElementById('slider-alpha').addEventListener('input', (e) => {
            document.getElementById('value-alpha').textContent = e.target.value.toFixed(4);
            updatePlot(parseFloat(e.target.value), parseFloat(document.getElementById('slider-beta').value), parseFloat(document.getElementById('slider-gamma').value));
        });

        document.getElementById('slider-beta').addEventListener('input', (e) => {
            document.getElementById('value-beta').textContent = e.target.value.toFixed(2);
            updatePlot(parseFloat(document.getElementById('slider-alpha').value), parseFloat(e.target.value), parseFloat(document.getElementById('slider-gamma').value));
        });

        document.getElementById('slider-gamma').addEventListener('input', (e) => {
            document.getElementById('value-gamma').textContent = e.target.value;
            updatePlot(parseFloat(document.getElementById('slider-alpha').value), parseFloat(document.getElementById('slider-beta').value), parseFloat(e.target.value));
        });

        // Initialize plot with default values
        updatePlot(0.05, 5, 500);
    </script>
</body>
</html>
