        }
        #main-container {
            width: 80%;
            max-width: 800px;
            display: flex;
            flex-direction: column;
            align-items: stretch;
        }
        #controls-container {
            display: flex;
            flex-direction: column;
            gap: 10px;
            padding: 20px;
            background-color: #fff;
            border-radius: 8px;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }
        .slider-group {
            display: flex;
            align-items: center;
            justify-content: space-between;
        }
        label {
            font-weight: bold;
        }
        input[type="range"] {
            width: 100%;
        }
        #canvas-container {
            margin-top: 20px;
            display: flex;
            justify-content: center;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="controls-container">
            <div class="slider-group">
                <label for="slider-time">time</label>
                <input type="range" id="slider-time" min="0" max="25" step="0.01" value="7.0">
                <span id="value-time">7.00</span>
            </div>
            <div class="slider-group">
                <label for="slider-wavelength">wavelength</label>
                <input type="range" id="slider-wavelength" min="1" max="8" step="0.01" value="4.0">
                <span id="value-wavelength">4.00</span>
            </div>
            <div class="slider-group">
                <label for="slider-longitudinal-amplitude">longitudinal amplitude</label>
                <input type="range" id="slider-longitudinal-amplitude" min="0" max="3" step="0.01" value="2.0">
                <span id="value-longitudinal-amplitude">2.00</span>
            </div>
            <div class="slider-group">
                <label for="slider-latitudinal-amplitude">latitudinal amplitude</label>
                <input type="range" id="slider-latitudinal-amplitude" min="0" max="3" step="0.01" value="1.0">
                <span id="value-latitudinal-amplitude">1.00</span>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let time = 0;
        let wavelength = 4.0;
        let longitudinalAmplitude = 2.0;
        let latitudinalAmplitude = 1.0;

        const gridCols = 10;
        const gridRows = 6;
        let particles = [];

        function setup() {
            const canvas = createCanvas(700, 450);
            canvas.parent('canvas-container');

            for (let col = 0; col < gridCols; col++) {
                for (let row = 0; row < gridRows; row++) {
                    const x = map(col, 0, gridCols - 1, 50, width - 50);
                    const y = map(row, 0, gridRows - 1, 50, height - 50);
                    particles.push({ x, y, col, row });
                }
            }

            document.getElementById('slider-time').addEventListener('input', () => {
                time = parseFloat(document.getElementById('slider-time').value);
                document.getElementById('value-time').textContent = time.toFixed(2);
            });

            document.getElementById('slider-wavelength').addEventListener('input', () => {
                wavelength = parseFloat(document.getElementById('slider-wavelength').value);
                document.getElementById('value-wavelength').textContent = wavelength.toFixed(2);
            });

            document.getElementById('slider-longitudinal-amplitude').addEventListener('input', () => {
                longitudinalAmplitude = parseFloat(document.getElementById('slider-longitudinal-amplitude').value);
                document.getElementById('value-longitudinal-amplitude').textContent = longitudinalAmplitude.toFixed(2);
            });

            document.getElementById('slider-latitudinal-amplitude').addEventListener('input', () => {
                latitudinalAmplitude = parseFloat(document.getElementById('slider-latitudinal-amplitude').value);
                document.getElementById('value-latitudinal-amplitude').textContent = latitudinalAmplitude.toFixed(2);
            });
        }

        function draw() {
            background('#E0F0FF');
            time += 0.03;
            document.getElementById('slider-time').value = time;
            document.getElementById('value-time').textContent = time.toFixed(2);

            const k = TWO_PI / wavelength;

            particles.forEach(particle => {
                const ampDecay = exp(- (particle.row / (gridRows - 1)) * 1.5);
                const ampX = longitudinalAmplitude * ampDecay * 20;
                const ampY = latitudinalAmplitude * ampDecay * 20;
                const phase = k * particle.x - time;

                noFill();
                stroke('#8F998F');
                strokeWeight(2);
                ellipse(particle.x, particle.y, 2 * ampX, 2 * ampY);

                const dx = ampX * cos(phase);
                const dy = ampY * sin(phase);
                particle.currentX = particle.x + dx;
                particle.currentY = particle.y + dy;
            });

            stroke('#0000FF');
            strokeWeight(2);
            drawingContext.setLineDash([5, 5]);

            for (let row = 0; row < gridRows; row++) {
                for (let col = 0; col < gridCols - 1; col++) {
                    const p1 = particles[row * gridCols + col];
                    const p2 = particles[row * gridCols + col + 1];
                    line(p1.currentX, p1.currentY, p2.currentX, p2.currentY);
                }
            }

            drawingContext.setLineDash([]);

            fill('#FF0000');
            noStroke();

            particles.forEach(particle => {
                circle(particle.currentX, particle.currentY, 8);
            });
        }
    </script>
</body>
</html>
