                <button id="btn-arrhenius" class="active-button">Arrhenius</button>
                <button id="btn-exponential">exponential</button>
            </div>
            <div>
                <label>n</label>
                <input type="range" id="slider-n" min="2" max="20" value="6" step="1">
                <span id="value-n">6</span>
            </div>
            <div>
                <label>T<sub>min</sub> (°C)</label>
                <input type="range" id="slider-tmin" min="0" max="50" value="25" step="1">
                <span id="value-tmin">25</span>
            </div>
            <div>
                <label>T<sub>max</sub> (°C)</label>
                <input type="range" id="slider-tmax" min="51" max="100" value="75" step="1">
                <span id="value-tmax">75</span>
            </div>
            <div>
                <label>model parameters</label>
                <label>T<sub>ref</sub> (°C)</label>
                <input type="range" id="slider-tref" min="0" max="100" value="35" step="1">
                <span id="value-tref">35</span>
            </div>
            <div>
                <label>c (°C<sup>-1</sup>)</label>
                <input type="range" id="slider-c" min="0.01" max="0.1" value="0.025" step="0.001">
                <span id="value-c">0.025</span>
            </div>
            <div>
                <label>b (°C)</label>
                <input type="range" id="slider-b" min="200" max="350" value="273.16" step="0.01">
                <span id="value-b">273.16</span>
            </div>
            <div>
                <label>a (°C)</label>
                <input type="range" id="slider-a" min="1000" max="10000" value="4000" step="1">
                <span id="value-a">4000</span>
            </div>
        </div>
        <div class="visualization-area">
            <div id="plot-top" class="plot"></div>
            <div id="plot-bottom" class="plot"></div>
        </div>
    </div>

    <script>
        const plotTop = document.getElementById('plot-top');
        const plotBottom = document.getElementById('plot-bottom');

        const btnArrhenius = document.getElementById('btn-arrhenius');
        const btnExponential = document.getElementById('btn-exponential');

        const sliderN = document.getElementById('slider-n');
        const sliderTMin = document.getElementById('slider-tmin');
        const sliderTMax = document.getElementById('slider-tmax');
        const sliderTRef = document.getElementById('slider-tref');
        const sliderC = document.getElementById('slider-c');
        const sliderB = document.getElementById('slider-b');
        const sliderA = document.getElementById('slider-a');

        const valueN = document.getElementById('value-n');
        const valueTMin = document.getElementById('value-tmin');
        const valueTMax = document.getElementById('value-tmax');
        const valueTRef = document.getElementById('value-tref');
        const valueC = document.getElementById('value-c');
        const valueB = document.getElementById('value-b');
        const valueA = document.getElementById('value-a');

        let model = 'Arrhenius';

        function updateSliderValues() {
            valueN.textContent = sliderN.value;
            valueTMin.textContent = sliderTMin.value;
            valueTMax.textContent = sliderTMax.value;
            valueTRef.textContent = sliderTRef.value;
            valueC.textContent = parseFloat(sliderC.value).toFixed(3);
            valueB.textContent = parseFloat(sliderB.value).toFixed(2);
            valueA.textContent = parseInt(sliderA.value);
        }

        function linearRegression(data) {
            const n = data.length;
            let sumX = 0, sumY = 0, sumXY = 0, sumXX = 0;
            for (let i = 0; i < n; i++) {
                const x = data[i][0];
                const y = data[i][1];
                sumX += x;
                sumY += y;
                sumXY += x * y;
                sumXX += x * x;
            }
            const slope = (n * sumXY - sumX * sumY) / (n * sumXX - sumX * sumX);
            const intercept = (sumY - slope * sumX) / n;
            let ssTotal = 0, ssResidual = 0;
            for (let i = 0; i < n; i++) {
                const y = data[i][1];
                const yPred = slope * data[i][0] + intercept;
                ssTotal += (y - sumY / n) ** 2;
                ssResidual += (y - yPred) ** 2;
            }
            const rSquared = 1 - (ssResidual / ssTotal);
            return { slope, intercept, rSquared };
        }

        function generateData() {
            const n = parseInt(sliderN.value);
            const TMin = parseInt(sliderTMin.value);
            const TMax = parseInt(sliderTMax.value);
            const TRef = parseInt(sliderTRef.value);
            const C = parseFloat(sliderC.value);
            const B = parseFloat(sliderB.value);
            const A = parseInt(sliderA.value);

            const temperatures = Array.from({ length: n }, (_, i) => TMin + (i * (TMax - TMin) / (n - 1)));
            let data = [];
            let transformedData = [];

            if (model === 'Arrhenius') {
                data = temperatures.map(T => ({
                    x: T,
                    y: Math.exp(-A / (T + B)) / Math.exp(-A / (TRef + B))
                }));
                transformedData = data.map(d => ({ x: d.x - TRef, y: Math.log(d.y) }));
            } else {
                data = temperatures.map(T => ({
                    x: T,
                    y: Math.exp(C * (T - TRef))
                }));
                transformedData = data.map(d => ({ x: 1 / (d.x + B), y: Math.log(d.y) }));
            }

            return { data, transformedData };
        }

        function updatePlots() {
            const { data, transformedData } = generateData();

            let fit = linearRegression(transformedData.map(d => [d.x, d.y]));
            const fittedSlope = fit.slope;
            const fittedIntercept = fit.intercept;
            const rSquared = fit.rSquared;

            let topPlotTitle, bottomPlotTitle;
            let topTrace2, bottomTrace2;

            if (model === 'Arrhenius') {
                topPlotTitle = `exponential model fit to Arrhenius data\nc = ${fittedSlope.toFixed(3)}, R² = ${rSquared.toFixed(3)}`;
                bottomPlotTitle = `linear fit of the exponential model\nR² = ${rSquared.toFixed(3)}`;

                topTrace2 = {
                    x: data.map(d => d.x),
                    y: data.map(d => Math.exp(fittedSlope * (d.x - TRef))),
                    mode: 'lines',
                    line: { color: '#d62728' }
                };

                bottomTrace2 = {
                    x: transformedData.map(d => d.x),
                    y: fittedSlope * transformedData.map(d => d.x),
                    mode: 'lines',
                    line: { color: '#2ca02c' }
                };
            } else {
                topPlotTitle = `Arrhenius model fit to exponential data\na = ${-fittedSlope.toFixed(3)}, R² = ${rSquared.toFixed(3)}`;
                bottomPlotTitle = `linear fit of the Arrhenius model\nR² = ${rSquared.toFixed(3)}`;

                topTrace2 = {
                    x: data.map(d => d.x),
                    y: Math.exp(-fittedSlope / (data.map(d => d.x + B))),
                    mode: 'lines',
                    line: { color: '#d62728' }
                };

                bottomTrace2 = {
                    x: transformedData.map(d => d.x),
                    y: -fittedSlope * transformedData.map(d => d.x) + fittedIntercept,
                    mode: 'lines',
                    line: { color: '#1f77b4' }
                };
            }

            const topTrace1 = {
                x: data.map(d => d.x),
                y: data.map(d => d.y),
                mode: 'markers',
                marker: { color: '#d62728' }
            };

            const bottomTrace1 = {
                x: transformedData.map(d => d.x),
                y: transformedData.map(d => d.y),
                mode: 'markers',
                marker: { color: model === 'Arrhenius' ? '#2ca02c' : '#1f77b4' }
            };

            Plotly.newPlot(plotTop, [topTrace1, topTrace2], { title: topPlotTitle, xaxis: { title: 'T (°C)' }, yaxis: { title: 'k(T)/k(T_ref)' } });
            Plotly.newPlot(plotBottom, [bottomTrace1, bottomTrace2], { title: bottomPlotTitle, xaxis: { title: model === 'Arrhenius' ? 'T - T_ref' : '1/(T + b)' }, yaxis: { title: 'log(k(T)/k(T_ref))' } });

            const shapes = [
                { type: 'line', x0: 0, y0: 0, x1: 1, y1: 0, line: { color: 'black', dash: 'dash' } },
                { type: 'line', x0: 0, y0: 0, x1: 0, y1: 1, line: { color: 'black', dash: 'dash' } }
            ];

            Plotly.relayout(plotBottom, { shapes });
        }

        btnArrhenius.addEventListener('click', () => {
            model = 'Arrhenius';
            btnArrhenius.classList.add('active-button');
            btnExponential.classList.remove('active-button');
            updatePlots();
        });

        btnExponential.addEventListener('click', () => {
            model = 'exponential';
            btnExponential.classList.add('active-button');
            btnArrhenius.classList.remove('active-button');
            updatePlots();
        });

        sliderN.addEventListener('input', updateSliderValues);
        sliderTMin.addEventListener('input', updateSliderValues);
        sliderTMax.addEventListener('input', updateSliderValues);
        sliderTRef.addEventListener('input', updateSliderValues);
        sliderC.addEventListener('input', updateSliderValues);
        sliderB.addEventListener('input', updateSliderValues);
        sliderA.addEventListener('input', updateSliderValues);

        sliderTMax.addEventListener('input', () => {
            if (parseInt(sliderTMax.value) <= parseInt(sliderTMin.value)) {
                sliderTMax.value = parseInt(sliderTMin.value) + 1;
                valueTMax.textContent = sliderTMax.value;
            }
            updateSliderValues();
        });

        updatePlots();
    </script>
</body>
</html>
