<body>
    <div id="app-container">
        <div id="control-panel">
            <div class="slider-wrapper">
                <label for="slider-lambda">permeability ratio, λ</label>
                <input type="range" id="slider-lambda" min="-8" max="-4" step="0.01" value="-5">
                <span id="value-lambda">0.00001</span>
            </div>
            <div class="slider-wrapper">
                <label for="slider-omega">storativity ratio, ω</label>
                <input type="range" id="slider-omega" min="-3" max="-1" step="0.01" value="-2">
                <span id="value-omega">0.01</span>
            </div>
            <div class="toggle-buttons">
                <button id="btn-pressure-drop" class="active">pressure drop</button>
                <button id="btn-difference">difference</button>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        // Helper functions for Bessel functions
        function besselK0(x) {
            if (x === 0) return Infinity;
            const y = 2 / x;
            const k0 = -0.5772156649015329 + y * (-0.4227842284668790 + y * (0.4200599566398124 + y * (-0.3965642487704089 + y * (0.3751383685472912 + y * (-0.3511946033465541 + y * (0.3265123930345970 + y * (-0.3021611678917404 + y * (0.2786975827852435 + y * (-0.2559230788974504 + y * (0.2339789700043360 + y * (-0.2128560845362395 + y * (0.1925483397837407 + y * (-0.1731749810687746 + y * (0.1547442506429861 + y * (-0.1373494411873104 + y * (0.1209514595034935 + y * (-0.1055741805635126 + y * (0.0911981595055971 + y * (-0.0778427159383772 + y * (0.0655151389911681 + y * (-0.0542464994344151 + y * (0.0439792971338374 + y * (-0.0346627420424083 + y * (0.0262052824229679 + y * (-0.0184993295268594 + y * (0.0115201474037617 + y * (-0.0052253323074136 + y * (0.0004112694641557)))))))))))))))))))))));
            return k0;
        }

        function besselK1(x) {
            if (x === 0) return Infinity;
            const y = 2 / x;
            const k1 = 0.5772156649015329 + y * (0.4227842284668790 + y * (-0.4200599566398124 + y * (0.3965642487704089 + y * (-0.3751383685472912 + y * (0.3511946033465541 + y * (-0.3265123930345970 + y * (0.3021611678917404 + y * (-0.2786975827852435 + y * (-0.2559230788974504 + y * (0.2339789700043360 + y * (-0.2128560845362395 + y * (0.1925483397837407 + y * (-0.1731749810687746 + y * (0.1547442506429861 + y * (-0.1373494411873104 + y * (0.1209514595034935 + y * (-0.1055741805635126 + y * (0.0911981595055971 + y * (-0.0778427159383772 + y * (0.0655151389911681 + y * (-0.0542464994344151 + y * (0.0439792971338374 + y * (-0.0346627420424083 + y * (0.0262052824229679 + y * (-0.0184993295268594 + y * (0.0115201474037617 + y * (-0.0052253323074136 + y * (0.0004112694641557)))))))))))))))))))))))))));
            return k1;
        }

        // Stehfest algorithm
        function stehfest(laplaceFunc, t, n_terms = 12) {
            let sum = 0;
            for (let k = 1; k <= n_terms; k++) {
                const coeff = Math.log(2) / (k * Math.log(2));
                const term = Math.pow(-1, k + 1) * Math.log(k);
                sum += coeff * laplaceFunc((k * Math.log(2)) / t) * term;
            }
            return sum;
        }

        // Calculate pressure drop
        function calculatePressureDrop(tau, lambda, omega) {
            const s = lambda / ((1 - omega) * tau + lambda);
            const f_s = (omega * (1 - omega) * s + lambda) / ((1 - omega) * s + lambda);
            const p_bar_s = besselK0(Math.sqrt(s * f_s)) / (Math.sqrt(s * f_s) * besselK1(Math.sqrt(s * f_s)));
            const F_s = p_bar_s / s;
            return stehfest(F_s, tau);
        }

        // Update plot
        function updatePlot() {
            const lambda = Math.pow(10, document.getElementById('slider-lambda').value);
            const omega = Math.pow(10, document.getElementById('slider-omega').value);
            document.getElementById('value-lambda').textContent = lambda.toExponential(4);
            document.getElementById('value-omega').textContent = omega.toExponential(4);

            const tau = Array.from({ length: 200 }, (_, i) => Math.pow(10, i / 199 * 10));
            const yValues = tau.map(t => calculatePressureDrop(t, lambda, omega));

            const isPressureDropActive = document.getElementById('btn-pressure-drop').classList.contains('active');
            const title = isPressureDropActive ? "pressure drawdown at the wellbore" : "deviation from the asymptote";
            const yTitle = isPressureDropActive ? "dimensionless pressure drop, ΔP" : "dimensionless difference, ΔP − 1/2 (ln(τ) + 0.8091)";

            const data = [{
                x: tau,
                y: yValues,
                mode: 'lines',
                line: { color: 'red', width: 2 }
            }];

            const layout = {
                title: title,
                xaxis: { title: 'dimensionless time, τ', type: 'log', range: [0, 10], dtick: 2, gridcolor: '#ddd' },
                yaxis: { title: yTitle, range: isPressureDropActive ? [0, 12] : [0, 3.5], gridcolor: '#ddd' },
                margin: { l: 80, r: 50, b: 60, t: 60 },
