            background-color: #f2f2f2;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.min.js"></script>
</head>
<body>
    <div id="left-column">
        <div id="canvas-container"></div>
        <div id="results-container">
            <p>rejuvenation bias <i>u</i> = <span id="results-u">22.7319</span></p>
            <p>site productivity <i>p</i> = <span id="results-p">58.2729</span></p>
        </div>
    </div>
    <div id="right-column">
        <table id="data-table">
            <thead>
                <tr>
                    <th>age</th>
                    <th>volume</th>
                </tr>
            </thead>
            <tbody id="data-table-body">
            </tbody>
        </table>
    </div>

    <script>
        let dataPoints = [
            { age: 30, volume: 289 },
            { age: 35, volume: 445 },
            { age: 40, volume: 565 },
            { age: 45, volume: 704 },
            { age: 50, volume: 833 },
            { age: 55, volume: 924 }
        ];

        let p = 50;
        let u = 1;
        let selectedPoint = null;

        function setup() {
            createCanvas(500, 500).parent('canvas-container');
            recalculate();
        }

        function draw() {
            background('#FFFFE0');
            drawGrid();
            drawAxes();
            drawDataPoints();
            drawFittedCurve();
        }

        function drawGrid() {
            stroke(150, 150, 150);
            strokeWeight(1);
            for (let x = map(20, 20, 80, 0, width); x <= width; x += 50) {
                line(x, 0, x, height);
            }
            for (let y = map(0, 0, 1500, height, 0); y >= 0; y -= 50) {
                line(0, y, width, y);
            }
        }

        function drawAxes() {
            stroke(0);
            strokeWeight(2);
            line(0, height - map(0, 0, 1500, height, 0), width, height - map(0, 0, 1500, height, 0));
            line(map(20, 20, 80, 0, width), 0, map(20, 20, 80, 0, width), height);

            textSize(12);
            fill(0);
            textAlign(CENTER, TOP);
            text("Stand age, yr", width / 2, height - 10);

            textAlign(RIGHT, CENTER);
            text("Wood volume, m³ ha⁻¹", -10, height / 2);

            push();
            translate(width - 10, height / 2);
            rotate(-PI / 2);
            text("4/5 law", 0, 0);
            pop();
        }

        function drawDataPoints() {
            noStroke();
            fill('#0000CD');
            for (let point of dataPoints) {
                let x = map(point.age, 20, 80, 0, width);
                let y = height - map(point.volume, 0, 1500, height, 0);
                ellipse(x, y, 8, 8);
            }
        }

        function drawFittedCurve() {
            stroke('#0000CD');
            strokeWeight(2);
            noFill();
            beginShape();
            for (let x = map(u, 20, 80, 0, width); x <= width; x++) {
                let age = map(x, 0, width, 20, 80);
                let volume = p * pow(age - u, 4 / 5);
                let y = height - map(volume, 0, 1500, height, 0);
                vertex(x, y);
            }
            endShape();
        }

        function recalculate() {
            let learningRate = 0.01;
            let maxIterations = 1000;
            let epsilon = 0.01;

            for (let i = 0; i < maxIterations; i++) {
                let gradientP = 0;
                let gradientU = 0;
                let error = 0;

                for (let point of dataPoints) {
                    let predictedVolume = p * pow(point.age - u, 4 / 5);
                    let residual = point.volume - predictedVolume;
                    error += residual * residual;

                    gradientP += residual * pow(point.age - u, 4 / 5);
                    gradientU -= residual * p * (4 / 5) * pow(point.age - u, -1 / 5);
                }

                p -= learningRate * gradientP;
                u -= learningRate * gradientU;

                if (u > min(dataPoints.map(dp => dp.age)) - epsilon) {
                    u = min(dataPoints.map(dp => dp.age)) - epsilon;
                }
            }

            document.getElementById('results-u').textContent = p.toFixed(4);
            document.getElementById('results-p').textContent = u.toFixed(4);

            let tbody = document.getElementById('data-table-body');
            tbody.innerHTML = '';
            for (let point of dataPoints) {
                let row = document.createElement('tr');
                row.innerHTML = `<td>${point.age}</td><td>${point.volume}</td>`;
                tbody.appendChild(row);
            }

            redraw();
        }

        function mousePressed() {
            if (!mouseIsInsideCanvas()) return;

            for (let point of dataPoints) {
                let x = map(point.age, 20, 80, 0, width);
                let y = height - map(point.volume, 0, 1500, height, 0);
                if (dist(mouseX, mouseY, x, y) < 4) {
                    selectedPoint = point;
                    return;
                }
            }

            let age = map(mouseX, 0, width, 20, 80);
            let volume = map(height - mouseY, 0, height, 0, 1500);
            dataPoints.push({ age, volume });
            recalculate();
        }

        function mouseDragged() {
            if (selectedPoint && mouseIsInsideCanvas()) {
                selectedPoint.age = map(mouseX, 0, width, 20, 80);
                selectedPoint.volume = map(height - mouseY, 0, height, 0, 1500);
                recalculate();
            }
        }

        function mouseReleased() {
            selectedPoint = null;
        }

        function mouseDoubleClicked() {
            if (!mouseIsInsideCanvas()) return;

            for (let i = 0; i < dataPoints.length; i++) {
                let point = dataPoints[i];
                let x = map(point.age, 20, 80, 0, width);
                let y = height - map(point.volume, 0, 1500, height, 0);
                if (dist(mouseX, mouseY, x, y) < 4) {
                    dataPoints.splice(i, 1);
                    recalculate();
                    return;
                }
            }
        }

        function mouseIsInsideCanvas() {
            return mouseX > 0 && mouseX < width && mouseY > 0 && mouseY < height;
        }
    </script>
</body>
</html>
