<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>ODE Visualization</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
            display: flex;
            height: 100vh;
        }
        #main-container {
            display: flex;
            flex-direction: row;
            flex: 1;
        }
        #control-panel {
            width: 300px;
            padding: 20px;
            background-color: #f4f4f4;
        }
        #control-panel div {
            margin-bottom: 20px;
        }
        #visualization-area {
            flex: 1;
            padding: 20px;
        }
        #plot-container {
            width: 100%;
            height: 600px;
        }
    </style>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div>
                <label for="slider-order">expansion order</label>
                <input type="range" id="slider-order" min="1" max="5" step="1" value="5">
                <span id="value-order">5</span>
            </div>
            <div>
                <label for="slider-a">f(0)</label>
                <input type="range" id="slider-a" min="0.1" max="3.0" step="0.1" value="2.0">
                <span id="value-a">2.0</span>
            </div>
            <div>
                <label for="slider-b">f'(0)</label>
                <input type="range" id="slider-b" min="-2.0" max="2.0" step="0.1" value="0.0">
                <span id="value-b">0.0</span>
            </div>
        </div>
        <div id="visualization-area">
            <div id="plot-container"></div>
        </div>
    </div>

    <script>
        const sliderOrder = document.getElementById('slider-order');
        const valueOrder = document.getElementById('value-order');
        const sliderA = document.getElementById('slider-a');
        const valueA = document.getElementById('value-a');
        const sliderB = document.getElementById('slider-b');
        const valueB = document.getElementById('value-b');

        function updateValueDisplays() {
            valueOrder.textContent = sliderOrder.value;
            valueA.textContent = parseFloat(sliderA.value).toFixed(1);
            valueB.textContent = parseFloat(sliderB.value).toFixed(1);
        }

        function calculateDerivatives(order, a, b) {
            const f_derivs = [a, b];
            for (let i = 2; i <= order; i++) {
                let term = 0;
                for (let j = 0; j < i; j++) {
                    term += f_derivs[j] * f_derivs[i - j];
                }
                f_derivs[i] = -(term + 1.5 * f_derivs[i - 1]) / f_derivs[0];
            }
            return f_derivs;
        }

        function calculatePowerSeriesCoefficients(f_derivs, order) {
            const coefficients = [];
            for (let k = 0; k <= order; k++) {
                coefficients[k] = f_derivs[k] / factorial(k);
            }
            return coefficients;
        }

        function factorial(n) {
            return n === 0 || n === 1 ? 1 : n * factorial(n - 1);
        }

        function generatePowerSeriesData(coefficients, order) {
            const data = [];
            const xValues = Array.from({ length: 200 }, (_, i) => i * 1.5 / 199);
            for (const x of xValues) {
                let f_approx = 0;
                for (let k = 0; k <= order; k++) {
                    f_approx += coefficients[k] * Math.pow(x, k);
                }
                data.push([x, f_approx]);
            }
            return data;
        }

        function rungeKutta(y1, y2, h) {
            const k11 = y2;
            const k12 = -(y2 + 1.5 * y1) / y1;

            const k21 = y1 + k11 * h / 2;
            const k22 = y2 + k12 * h / 2;
            const k23 = -(k22 + 1.5 * k21) / k21;

            const k31 = y1 + k21 * h / 2;
            const k32 = y2 + k22 * h / 2;
            const k33 = -(k32 + 1.5 * k31) / k31;

            const k41 = y1 + k31 * h;
            const k42 = y2 + k32 * h;
            const k43 = -(k42 + 1.5 * k41) / k41;

            return [
                y1 + (k11 + 2 * k21 + 2 * k31 + k41) * h / 6,
                y2 + (k12 + 2 * k22 + 2 * k32 + k42) * h / 6
            ];
        }

        function generateNumericalSolutionData(a, b) {
            const data = [];
            let y1 = a, y2 = b;
            const h = 1.5 / 199;
            for (let i = 0; i < 200; i++) {
                data.push([i * h, y1]);
                [y1, y2] = rungeKutta(y1, y2, h);
            }
            return data;
        }

        function determineAgreementRegion(powerSeriesData, numericalData) {
            for (let i = 0; i < powerSeriesData.length; i++) {
                if (Math.abs(powerSeriesData[i][1] - numericalData[i][1]) >= 0.01) {
                    return powerSeriesData[i][0];
                }
            }
            return 1.5;
        }

        function formatPolynomial(coefficients, order) {
            let polynomial = '';
            for (let k = 0; k <= order; k++) {
                if (coefficients[k] !== 0) {
                    const coeffStr = coefficients[k].toFixed(5);
                    const sign = k === 0 ? '' : (coefficients[k] > 0 ? ' + ' : ' - ');
                    const xTerm = k === 0 ? '' : (k === 1 ? ' x' : ` x^${k}`);
                    polynomial += `${sign}${Math.abs(coefficients[k])}${xTerm}`;
                }
            }
            return `f(x) = ${polynomial}`;
        }

        function updatePlot(order, a, b) {
            const f_derivs = calculateDerivatives(order, a, b);
            const coefficients = calculatePowerSeriesCoefficients(f_derivs, order);
            const powerSeriesData = generatePowerSeriesData(coefficients, order);
            const numericalData = generateNumericalSolutionData(a, b);
            const x_limit = determineAgreementRegion(powerSeriesData, numericalData);
            const polynomial = formatPolynomial(coefficients, order);

            const traceRed = {
                type: 'scatter',
                mode: 'lines',
                x: powerSeriesData.map(d => d[0]),
                y: powerSeriesData.map(d => d[1]),
                line: { color: 'red', width: 2 }
            };

            const traceGreen = {
                type: 'scatter',
                mode: 'lines',
                x: numericalData.map(d => d[0]),
                y: numericalData.map(d => d[1]),
                line: { color: 'green', width: 3 }
            };

            const layout = {
                title: polynomial,
                xaxis: { title: 'x', range: [0, 1.5], zeroline: false },
                yaxis: { title: 'f(x)', range: [0, 4], zeroline: false },
                margin: { l: 50, r: 20, t: 50, b: 40 },
                shapes: [{
                    type: 'rect',
                    xref: 'x',
                    yref: 'paper',
                    x0: 0,
                    y0: 0,
                    x1: x_limit,
                    y1: 1,
                    fillcolor: 'lightblue',
                    opacity: 0.4,
                    layer: 'below',
                    line: { width: 0 }
                }]
            };

            Plotly.newPlot('plot-container', [traceRed, traceGreen], layout);
        }

        function init() {
            updateValueDisplays();
            updatePlot(parseInt(sliderOrder.value), parseFloat(sliderA.value), parseFloat(sliderB.value));
        }

        sliderOrder.addEventListener('input', () => {
            updateValueDisplays();
            updatePlot(parseInt(sliderOrder.value), parseFloat(sliderA.value), parseFloat(sliderB.value));
        });

        sliderA.addEventListener('input', () => {
            updateValueDisplays();
            updatePlot(parseInt(sliderOrder.value), parseFloat(sliderA.value), parseFloat(sliderB.value));
        });

        sliderB.addEventListener('input', () => {
            updateValueDisplays();
            updatePlot(parseInt(sliderOrder.value), parseFloat(sliderA.value), parseFloat(sliderB.value));
        });

        init();
    </script>
</body>
</html>
