        let is2DPlot = true;
        let isQWERTY = true;

        const qwertyLayout = [
            ['Q', 'W', 'E', 'R', 'T', 'Y', 'U', 'I', 'O', 'P'],
            ['A', 'S', 'D', 'F', 'G', 'H', 'J', 'K', 'L'],
            ['Z', 'X', 'C', 'V', 'B', 'N', 'M']
        ];

        const dvorakLayout = [
            ['P', 'Y', 'F', 'G', 'C', 'R', 'L'],
            ['A', 'O', 'E', 'U', 'I', 'D', 'H', 'T', 'N', 'S'],
            ['Q', 'J', 'K', 'X', 'B', 'M', 'W', 'V', 'Z']
        ];

        function calculateFrequencies(text) {
            const frequencies = {};
            for (let char of text.toUpperCase()) {
                if (char >= 'A' && char <= 'Z') {
                    frequencies[char] = (frequencies[char] || 0) + 1;
                }
            }
            return frequencies;
        }

        function draw2DPlot(frequencies) {
            scene.clear();
            const maxFrequency = Math.max(...Object.values(frequencies));
            const barWidth = 0.8 / 26;
            const barDepth = 0.5;
            const barHeightScale = 8 / maxFrequency;

            for (let i = 0; i < 26; i++) {
                const char = String.fromCharCode(65 + i);
                const frequency = frequencies[char] || 0;
                const barHeight = frequency * barHeightScale;
                const barColor = new THREE.Color().setHSL((barHeight / maxFrequency) * 0.6, 1, 0.5);

                const geometry = new THREE.BoxGeometry(barWidth, barHeight, barDepth);
                const material = new THREE.MeshBasicMaterial({ color: barColor });
                const bar = new THREE.Mesh(geometry, material);
                bar.position.set(-9.5 + i * (barWidth + 0.2), barHeight / 2, 0);
                scene.add(bar);

                const textCanvas = document.createElement('canvas');
                const context = textCanvas.getContext('2d');
                textCanvas.width = 64;
                textCanvas.height = 64;
                context.font = '48px Arial';
                context.fillStyle = '#000';
                context.textAlign = 'center';
                context.textBaseline = 'middle';
                context.fillText(char, 32, 32);

                const texture = new THREE.CanvasTexture(textCanvas);
                const textMaterial = new THREE.SpriteMaterial({ map: texture, transparent: true });
                const textSprite = new THREE.Sprite(textMaterial);
                textSprite.scale.set(0.5, 0.5, 1);
                textSprite.position.set(-9.5 + i * (barWidth + 0.2), barHeight + 1, 0);
                scene.add(textSprite);
            }

            camera.position.z = 10;
        }

        function draw3DPlot(frequencies) {
            scene.clear();
            const maxFrequency = Math.max(...Object.values(frequencies));
            const keyRadius = 0.5;
            const keyHeightScale = 4 / maxFrequency;
            const layout = isQWERTY ? qwertyLayout : dvorakLayout;

            for (let row = 0; row < layout.length; row++) {
                for (let col = 0; col < layout[row].length; col++) {
                    const char = layout[row][col];
                    const frequency = frequencies[char] || 0.1;
                    const keyHeight = frequency * keyHeightScale;
                    const keyColor = new THREE.Color().setHSL(row / 3, 1, 0.5);

                    const geometry = new THREE.CylinderGeometry(keyRadius, keyRadius, keyHeight, 32);
                    const material = new THREE.MeshStandardMaterial({ color: keyColor });
                    const key = new THREE.Mesh(geometry, material);
                    key.position.set(col * (keyRadius * 2 + 1) - layout[row].length * (keyRadius * 2 + 1) / 2, keyHeight / 2, row * (keyRadius * 2 + 1) - layout.length * (keyRadius * 2 + 1) / 2);
                    scene.add(key);

                    const textCanvas = document.createElement('canvas');
                    const context = textCanvas.getContext('2d');
                    textCanvas.width = 64;
                    textCanvas.height = 64;
                    context.font = '48px Arial';
                    context.fillStyle = '#000';
                    context.textAlign = 'center';
                    context.textBaseline = 'middle';
                    context.fillText(char, 32, 32);

                    const texture = new THREE.CanvasTexture(textCanvas);
                    const textMaterial = new THREE.SpriteMaterial({ map: texture, transparent: true });
                    const textSprite = new THREE.Sprite(textMaterial);
                    textSprite.scale.set(0.5, 0.5, 1);
                    textSprite.position.set(col * (keyRadius * 2 + 1) - layout[row].length * (keyRadius * 2 + 1) / 2, keyHeight + 1, row * (keyRadius * 2 + 1) - layout.length * (keyRadius * 2 + 1) / 2);
                    scene.add(textSprite);
                }
            }

            camera.position.set(0, 10, 20);
            camera.lookAt(0, 0, 0);
        }

        function updateVisualization() {
            const frequencies = calculateFrequencies(currentText);
            if (is2DPlot) {
                draw2DPlot(frequencies);
            } else {
                draw3DPlot(frequencies);
            }
            renderer.render(scene, camera);
        }

        document.getElementById('select-text').addEventListener('change', (event) => {
            currentText = event.target.value;
            updateVisualization();
        });

        document.getElementById('btn-plot-2d').addEventListener('click', () => {
            is2DPlot = true;
            document.getElementById('btn-plot-2d').classList.add('active');
            document.getElementById('btn-plot-3d').classList.remove('active');
            updateVisualization();
        });

        document.getElementById('btn-plot-3d').addEventListener('click', () => {
            is2DPlot = false;
            document.getElementById('btn-plot-2d').classList.remove('active');
            document.getElementById('btn-plot-3d').classList.add('active');
            updateVisualization();
        });

        document.getElementById('btn-keyboard-qwerty').addEventListener('click', () => {
            isQWERTY = true;
            document.getElementById('btn-keyboard-qwerty').classList.add('active');
            document.getElementById('btn-keyboard-dvorak').classList.remove('active');
            if (!is2DPlot) {
                updateVisualization();
            }
        });

        document.getElementById('btn-keyboard-dvorak').addEventListener('click', () => {
            isQWERTY = false;
            document.getElementById('btn-keyboard-qwerty').classList.remove('active');
            document.getElementById('btn-keyboard-dvorak').classList.add('active');
            if (!is2DPlot) {
                updateVisualization();
            }
        });

        updateVisualization();
    </script>
</body>
</html>
