            canvas.parent('canvas-container');

            const numPoints = parseInt(document.getElementById('slider-points').value);
            for (let i = 0; i < numPoints; i++) {
                particles.push({
                    x0: map(i, 0, numPoints - 1, 0, 5),
                    history: []
                });
