            document.getElementById('slider-time').addEventListener('input', updateLabel);
            document.getElementById('slider-trail').addEventListener('input', updateLabel);
            document.getElementById('slider-points').addEventListener('input', updateLabel);
            document.getElementById('slider-p-amp').addEventListener('input', updateLabel);
            document.getElementById('slider-p-wl').addEventListener('input', updateLabel);
            document.getElementById('slider-s-amp').addEventListener('input', updateLabel);
            document.getElementById('slider-s-wl').addEventListener('input', updateLabel);

            document.getElementById('btn-play-pause').addEventListener('click', togglePlayPause);
            document.getElementById('btn-reset').addEventListener('click', resetSimulation);
        }

        function draw() {
            background(255);

            if (isPlaying) {
                time += 0.01;
                if (time >= 5) time = 0;
                document.getElementById('slider-time').value = time;
                document.getElementById('label-time').textContent = time.toFixed(2);
            }

            const p_amp = parseFloat(document.getElementById('slider-p-amp').value);
            const p_wl = parseFloat(document.getElementById('slider-p-wl').value);
            const s_amp = parseFloat(document.getElementById('slider-s-amp').value);
            const s_wl = parseFloat(document.getElementById('slider-s-wl').value);
            const trailLength = parseInt(document.getElementById('slider-trail').value);

            stroke(204, 204, 204);
            line(0, height / 2, width, height / 2);
            line(width / 5, 0, width / 5, height);
            line(width * 2 / 5, 0, width * 2 / 5, height);
            line(width * 3 / 5, 0, width * 3 / 5, height);
            line(width * 4 / 5, 0, width * 4 / 5, height);

            for (let i = 0; i < particles.length; i++) {
                const particle = particles[i];
                const dx = p_amp * sin(TWO_PI * (particle.x0 / p_wl - time));
                const dy = s_amp * sin(TWO_PI * (particle.x0 / s_wl - time));
                const x_final = map(particle.x0 + dx, 0, 5, 0, width);
                const y_final = map(dy, -0.5, 0.5, height, 0);

                particle.history.push({ x: x_final, y: y_final });
                if (particle.history.length > trailLength) {
                    particle.history.shift();
                }

                stroke(204, 204, 204);
                line(x_final, 0, x_final, height);

                noStroke();
                fill(106, 90, 205);
                ellipse(x_final, y_final, 10, 10);

                stroke(106, 90, 205, 100);
                for (const pos of particle.history) {
                    ellipse(pos.x, pos.y, 5, 5);
                }
            }
        }

