            display: flex;
            width: 80%;
            height: 80%;
            border: 1px solid #ccc;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }
        #control-panel {
            width: 25%;
            padding: 20px;
            background-color: #fff;
            border-right: 1px solid #ccc;
        }
        #visualization-area {
            width: 75%;
            padding: 20px;
            display: flex;
            flex-direction: column;
        }
        #formula-display {
            margin-bottom: 20px;
            font-size: 1.2em;
        }
        #canvas-container {
            flex-grow: 1;
        }
        label {
            display: block;
            margin-bottom: 5px;
        }
        input[type="range"] {
            width: 100%;
            margin-bottom: 10px;
        }
        span {
            margin-left: 10px;
        }
    </style>
    <script src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div>
                <label for="slider-t">t</label>
                <input type="range" id="slider-t" min="0" max="4" step="0.01" value="0.1">
                <span id="t-value-display">0.10</span>
            </div>
            <div>
                <label for="slider-k">k</label>
                <input type="range" id="slider-k" min="1" max="10" step="1" value="2">
                <span id="k-value-display">2</span>
            </div>
            <div>
                <label for="checkbox-axes">axes</label>
                <input type="checkbox" id="checkbox-axes" checked>
            </div>
        </div>
        <div id="visualization-area">
            <div id="formula-display"></div>
            <div id="canvas-container"></div>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        const zeros = [2.4048, 5.5201, 8.6537, 11.7915, 14.9309, 18.0711, 21.2116, 24.3525, 27.4935, 30.6346];

        function besselJ0(x) {
            const terms = 10;
            let sum = 0;
            for (let i = 0; i < terms; i++) {
                const term = Math.pow(-1, i) * Math.pow(x / 2, 2 * i) / factorial(i);
                sum += term;
            }
            return sum;
        }

        function factorial(n) {
            if (n === 0 || n === 1) return 1;
            let result = 1;
            for (let i = 2; i <= n; i++) {
                result *= i;
            }
            return result;
        }

        let t = 0.1;
        let k = 2;
        let showAxes = true;

        const sliderT = document.getElementById('slider-t');
        const tValueDisplay = document.getElementById('t-value-display');
        const sliderK = document.getElementById('slider-k');
        const kValueDisplay = document.getElementById('k-value-display');
        const checkboxAxes = document.getElementById('checkbox-axes');

        sliderT.addEventListener('input', () => {
            t = parseFloat(sliderT.value);
            tValueDisplay.textContent = t.toFixed(2);
            redraw();
        });

        sliderK.addEventListener('input', () => {
            k = parseInt(sliderK.value);
            kValueDisplay.textContent = k;
            const omega_k = zeros[k - 1] / 2;
            MathJax.typesetPromise([document.getElementById('formula-display')], `
                \\(\\omega_${k} = ${omega_k.toFixed(2)} \\sqrt{g/l}\\)
            `);
            redraw();
        });

        checkboxAxes.addEventListener('change', () => {
            showAxes = checkboxAxes.checked;
            redraw();
        });

        function setup() {
            createCanvas(400, 400).parent('canvas-container');
        }

        function draw() {
            background(240);
            if (showAxes) {
                stroke(0);
                strokeWeight(1);
                line(0, height / 2, width, height / 2); // x-axis
                line(width / 2, 0, width / 2, height); // y-axis
                textSize(12);
                textAlign(CENTER, BOTTOM);
                text('-2', 0, height / 2);
                text('-1', width / 4, height / 2);
                text('0', width / 2, height / 2);
                text('1', 3 * width / 4, height / 2);
                text('2', width, height / 2);
                textAlign(RIGHT, CENTER);
                text('-1.0', width / 2, 0);
                text('-0.8', width / 2, height / 5);
                text('-0.6', width / 2, 2 * height / 5);
                text('-0.4', width / 2, 3 * height / 5);
                text('-0.2', width / 2, 4 * height / 5);
            }

            const omega_k = zeros[k - 1] / 2;
            const timeFactor = cos(omega_k * t);
            const amplitude = 0.8;
            stroke(0, 0, 128);
            strokeWeight(2);
            noFill();
            beginShape();
            for (let y_norm = 0; y_norm <= 1; y_norm += 0.01) {
                const bessel_arg = zeros[k - 1] * sqrt(y_norm);
                const x_pos = amplitude * timeFactor * besselJ0(bessel_arg);
                const y_pos = -y_norm;
                const x_pixel = map(x_pos, -2, 2, 0, width);
                const y_pixel = map(y_pos, 0, -1.2, height / 2, height);
                vertex(x_pixel, y_pixel);
            }
            endShape();
        }

        function redraw() {
            requestAnimationFrame(draw);
        }

        window.onload = () => {
            MathJax.typesetPromise([document.getElementById('formula-display')], `
                \\(\\omega_${k} = ${zeros[k - 1] / 2.toFixed(2)} \\sqrt{g/l}\\)
            `);
        };
    </script>
</body>
</html>
