<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Gas Phase Fugacity Coefficient Plot</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            height: 100vh;
            background-color: #f4f4f9;
        }
        #app-container {
            width: 80%;
            max-width: 1200px;
            background-color: #fff;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
            border-radius: 8px;
            overflow: hidden;
        }
        #control-panel {
            padding: 20px;
            border-bottom: 1px solid #ddd;
            display: flex;
            flex-direction: column;
            gap: 10px;
        }
        #control-panel > div {
            display: flex;
            align-items: center;
            gap: 10px;
        }
        #control-panel button.selected {
            border: 2px solid red;
        }
        #plot-container {
            width: 100%;
            height: 500px;
        }
    </style>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <div>
                <button id="btn-srk" class="selected">SRK</button>
                <button id="btn-pr">PR</button>
            </div>
            <div>
                <input type="checkbox" id="checkbox-aspen" checked>
                <label for="checkbox-aspen">comparison with Aspen Plus</label>
            </div>
            <div>
                <label for="slider-pressure">pressure P</label>
                <input type="range" id="slider-pressure" min="1" max="20" step="1" value="10">
                <span id="display-pressure">10</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        const Tc = 365.57; // Critical Temperature in K
        const Pc = 46.65; // Critical Pressure in bar
        const ω = 0.144; // Acentric Factor
        const R = 0.0831446; // Gas Constant in L·bar/(mol·K)

        let selectedEos = 'srk';
        let aspenChecked = true;
        let pressure = 10;

        function solveCubic(a, b, c, d) {
            const p = (3 * b - a * a) / 3;
            const q = (2 * a * a * a - 9 * a * b + 27 * c) / 27;
            const discriminant = q * q / 4 + p * p * p / 27;

            if (discriminant > 0) {
                const u = Math.cbrt(-q / 2 + Math.sqrt(discriminant));
                const v = Math.cbrt(-q / 2 - Math.sqrt(discriminant));
                return [u + v - a / 3];
            } else if (discriminant === 0) {
                const u = Math.cbrt(-q / 2);
                return [2 * u - a / 3, -u - a / 3];
            } else {
                const theta = Math.acos(-q / Math.sqrt(-p * p * p / 27));
                const r = 2 * Math.sqrt(-p / 3);
                return [
                    r * Math.cos(theta / 3) - a / 3,
                    r * Math.cos((theta + 2 * Math.PI) / 3) - a / 3,
                    r * Math.cos((theta + 4 * Math.PI) / 3) - a / 3
                ];
            }
        }

        function calculateFugacityCoefficients() {
            const temperatures = Array.from({ length: 201 }, (_, i) => 300 + i);
            const srkData = [];
            const prData = [];

            temperatures.forEach(T => {
                const Tr = T / Tc;
                let a, b, m, alpha, a_eff, A, B, Z;

                if (selectedEos === 'srk') {
                    a = 0.42748 * R * R * Tc * Tc / Pc;
                    b = 0.08664 * R * Tc / Pc;
                    m = 0.480 + 1.574 * ω - 0.176 * ω * ω;
                    alpha = Math.pow(1 + m * (1 - Math.sqrt(Tr)), 2);
                    a_eff = a * alpha;
                    A = a_eff * pressure / (R * T * R * T);
                    B = b * pressure / (R * T);
                    const roots = solveCubic(1, -1, A - B - B * B, -A * B);
                    Z = Math.max(...roots.filter(root => root > 0));
                    const ln_phi = Z - 1 - Math.log(Z - B) - (A / B) * Math.log(1 + B / Z);
                    srkData.push([T, Math.exp(ln_phi)]);
                } else if (selectedEos === 'pr') {
                    a = 0.45724 * R * R * Tc * Tc / Pc;
                    b = 0.07780 * R * Tc / Pc;
                    m = 0.37464 + 1.54226 * ω - 0.26992 * ω * ω;
                    alpha = Math.pow(1 + m * (1 - Math.sqrt(Tr)), 2);
                    a_eff = a * alpha;
                    A = a_eff * pressure / (R * T * R * T);
                    B = b * pressure / (R * T);
                    const roots = solveCubic(1, -(1 - B), A - 3 * B * B - 2 * B, -(A * B - B * B - B * B * B));
                    Z = Math.max(...roots.filter(root => root > 0));
                    const ln_phi = Z - 1 - Math.log(Z - B) - (A / (2 * Math.sqrt(2) * B)) * Math.log((Z + (1 + Math.sqrt(2)) * B) / (Z + (1 - Math.sqrt(2)) * B));
                    prData.push([T, Math.exp(ln_phi)]);
                }
            });

            return { srkData, prData };
        }

        function updatePlot() {
            const { srkData, prData } = calculateFugacityCoefficients();
            const traces = [
                {
                    x: srkData.map(d => d[0]),
                    y: srkData.map(d => d[1]),
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'red', width: 2 },
                    name: 'SRK'
                },
                {
                    x: prData.map(d => d[0]),
                    y: prData.map(d => d[1]),
                    type: 'scatter',
                    mode: 'lines',
                    line: { color: 'blue', width: 2 },
                    name: 'PR',
                    visible: selectedEos === 'pr'
                },
                {
                    x: [300, 310, 320, 330, 340, 350, 360, 370, 380, 390, 400],
                    y: [0.868, 0.881, 0.891, 0.900, 0.909, 0.917, 0.924, 0.930, 0.937, 0.942, 0.946],
                    type: 'scatter',
                    mode: 'markers',
                    marker: { color: 'green', symbol: 'square', size: 8 },
                    name: 'Aspen Plus',
                    visible: aspenChecked && pressure === 10
                }
            ];

            const layout = {
                title: 'Gas-Phase Fugacity Coefficient vs Temperature',
                xaxis: {
                    title: 'temperature (K)',
                    range: [295, 505]
                },
                yaxis: {
                    title: 'gas-phase fugacity coefficient',
                    range: [0.68, 1.02]
                },
                grid: {
                    rows: 1,
                    columns: 1,
                    subplots: ['xy'],
                    xgap: 10,
                    ygap: 10,
                    pattern: 'independent'
                },
                plot_bgcolor: 'lightgrey',
                paper_bgcolor: 'white'
            };

            Plotly.newPlot('plot-container', traces, layout);
        }

        document.getElementById('btn-srk').addEventListener('click', () => {
            selectedEos = 'srk';
            document.getElementById('btn-srk').classList.add('selected');
            document.getElementById('btn-pr').classList.remove('selected');
            updatePlot();
        });

        document.getElementById('btn-pr').addEventListener('click', () => {
            selectedEos = 'pr';
            document.getElementById('btn-pr').classList.add('selected');
            document.getElementById('btn-srk').classList.remove('selected');
            updatePlot();
        });

        document.getElementById('checkbox-aspen').addEventListener('change', (event) => {
            aspenChecked = event.target.checked;
            updatePlot();
        });

        document.getElementById('slider-pressure').addEventListener('input', (event) => {
            pressure = parseInt(event.target.value, 10);
            document.getElementById('display-pressure').textContent = pressure;
            updatePlot();
        });

        updatePlot();
    </script>
</body>
</html>
