<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <title>A Visual Proof of Viviani's Theorem</title>
    <!-- Include p5.js from CDN -->
    <script src="https://cdn.jsdelivr.net/npm/p5@1.4.1/lib/p5.js"></script>
    <style>
        body {
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            margin: 0;
            background-color: #f0f0f0;
        }
        #demo-container {
            width: 600px;
            height: 520px;
            background-color: white;
            border-radius: 8px;
            box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
        }
        #control-panel {
            padding: 10px;
            background-color: #e0e0e0;
            border-bottom: 1px solid #ccc;
        }
        #canvas-container {
            width: 100%;
            height: calc(100% - 40px);
            position: relative;
        }
    </style>
</head>
<body>
    <div id="demo-container">
        <!-- Section 1: Control Panel -->
        <div id="control-panel">
            <label>Step</label>
            <input type="radio" id="step1" name="step-selector" value="1" checked>
            <label for="step1">1</label>
            <input type="radio" id="step2" name="step-selector" value="2">
            <label for="step2">2</label>
            <input type="radio" id="step3" name="step-selector" value="3">
            <label for="step3">3</label>
        </div>
        <!-- Section 2: Visualization Area -->
        <div id="canvas-container">
            <!-- p5.js canvas will be created here -->
        </div>
    </div>
    <script>
        let canvas;
        let currentStep = 1;
        let pX = 240; // Initial x-coordinate of point P
        let pY = 312; // Initial y-coordinate of point P
        let triangleVertices = [];
        let mainAltitudeLength;

        function setup() {
            canvas = createCanvas(600, 520);
            canvas.parent('canvas-container');
            calculateTriangleVertices();
            mainAltitudeLength = dist(triangleVertices[0].x, triangleVertices[0].y, triangleVertices[1].x, triangleVertices[1].y);
        }

        function draw() {
            background(255);
            drawMainTriangle();
            drawMainAltitude();
            drawPointP();

            switch (currentStep) {
                case 1:
                    drawPerpendiculars();
                    break;
                case 2:
                    drawSmallTriangles();
                    break;
                case 3:
                    drawRearrangedTriangles();
                    break;
            }
        }

        function calculateTriangleVertices() {
            const height = 400;
            const base = height * sqrt(3) / 3;
            const centerX = width / 2;
            const centerY = height / 2;
            triangleVertices.push(createVector(centerX - base / 2, centerY + height / 2));
            triangleVertices.push(createVector(centerX + base / 2, centerY + height / 2));
            triangleVertices.push(createVector(centerX, centerY - height / 2));
        }

        function drawMainTriangle() {
            stroke(0);
            noFill();
            beginShape();
            for (let i = 0; i < triangleVertices.length; i++) {
                vertex(triangleVertices[i].x, triangleVertices[i].y);
            }
            endShape(CLOSE);

            // Label vertices
            textSize(16);
            textAlign(CENTER, BOTTOM);
            fill(0);
            text('A', triangleVertices[0].x, triangleVertices[0].y);
            text('B', triangleVertices[1].x, triangleVertices[1].y);
            text('C', triangleVertices[2].x, triangleVertices[2].y);
        }

        function drawMainAltitude() {
            const midAB = p5.Vector.lerp(triangleVertices[0], triangleVertices[1], 0.5);
            stroke(173, 216, 230, 128);
            strokeWeight(2);
            line(midAB.x, midAB.y, triangleVertices[2].x, triangleVertices[2].y);
        }

        function drawPointP() {
            fill(128);
            ellipse(pX, pY, 10, 10);
            stroke(0);
            line(pX - 3, pY - 3, pX + 3, pY + 3);
            line(pX - 3, pY + 3, pX + 3, pY - 3);
            fill(255, 0, 0);
            textSize(12);
            text('P', pX + 10, pY - 10);
        }

        function drawPerpendiculars() {
            const d_AB = getDistanceToSide(pX, pY, triangleVertices[0], triangleVertices[1]);
            const d_BC = getDistanceToSide(pX, pY, triangleVertices[1], triangleVertices[2]);
            const d_AC = getDistanceToSide(pX, pY, triangleVertices[2], triangleVertices[0]);

            const foot_AB = getFootOfPerpendicular(pX, pY, triangleVertices[0], triangleVertices[1]);
            const foot_BC = getFootOfPerpendicular(pX, pY, triangleVertices[1], triangleVertices[2]);
            const foot_AC = getFootOfPerpendicular(pX, pY, triangleVertices[2], triangleVertices[0]);

            stroke(0);
            line(pX, pY, foot_AB.x, foot_AB.y);
            line(pX, pY, foot_BC.x, foot_BC.y);
            line(pX, pY, foot_AC.x, foot_AC.y);

            fill(0, 0, 255);
            ellipse(foot_AB.x, foot_AB.y, 5, 5);
            ellipse(foot_BC.x, foot_BC.y, 5, 5);
            ellipse(foot_AC.x, foot_AC.y, 5, 5);
        }

        function drawSmallTriangles() {
            const d_AB = getDistanceToSide(pX, pY, triangleVertices[0], triangleVertices[1]);
            const d_BC = getDistanceToSide(pX, pY, triangleVertices[1], triangleVertices[2]);
            const d_AC = getDistanceToSide(pX, pY, triangleVertices[2], triangleVertices[0]);

            const side_AB = d_AB * 2 / sqrt(3);
            const side_BC = d_BC * 2 / sqrt(3);
            const side_AC = d_AC * 2 / sqrt(3);

            drawEquilateralTriangle(pX, pY, side_AB, color(255, 0, 0, 128), true);
            drawEquilateralTriangle(pX, pY, side_AC, color(0, 255, 0, 128), false);
            drawEquilateralTriangle(pX, pY, side_BC, color(0, 0, 255, 128), false);
        }

        function drawRearrangedTriangles() {
            const d_AB = getDistanceToSide(pX, pY, triangleVertices[0], triangleVertices[1]);
            const d_BC = getDistanceToSide(pX, pY, triangleVertices[1], triangleVertices[2]);
            const d_AC = getDistanceToSide(pX, pY, triangleVertices[2], triangleVertices[0]);

            const side_AB = d_AB * 2 / sqrt(3);
            const side_BC = d_BC * 2 / sqrt(3);
            const side_AC = d_AC * 2 / sqrt(3);

            const baseY = triangleVertices[0].y;
            const redTriangleBaseY = baseY;
            const blueTriangleBaseY = redTriangleBaseY - side_AB * sin(PI / 3);
            const greenTriangleBaseY = blueTriangleBaseY - side_BC * sin(PI / 3);

            drawEquilateralTriangle(triangleVertices[0].x, redTriangleBaseY, side_AB, color(255, 0, 0, 128), true);
            drawEquilateralTriangle(triangleVertices[0].x, blueTriangleBaseY, side_BC, color(0, 0, 255, 128), true);
            drawEquilateralTriangle(triangleVertices[0].x, greenTriangleBaseY, side_AC, color(0, 255, 0, 128), true);

            fill(128);
            ellipse(pX, pY, 10, 10);
            stroke(0);
            line(pX - 3, pY - 3, pX + 3, pY + 3);
            line(pX - 3, pY + 3, pX + 3, pY - 3);
        }

        function drawEquilateralTriangle(x, y, side, fillColor, flip) {
            const height = side * sqrt(3) / 2;
            const baseX = x - side / 2;
            const baseY = y;
            const topX = x;
            const topY = y - height;

            fill(fillColor);
            noStroke();
            beginShape();
            vertex(baseX, baseY);
            vertex(topX, topY);
            vertex(baseX + side, baseY);
            endShape(CLOSE);

            if (flip) {
                fill(0);
                textSize(12);
                text('x', baseX + side / 2, baseY - 5);
            } else {
                fill(0);
                textSize(12);
                text('y', topX, topY - 5);
            }
        }

        function getDistanceToSide(px, py, v1, v2) {
            const numerator = abs((v2.y - v1.y) * px - (v2.x - v1.x) * py + v2.x * v1.y - v2.y * v1.x);
            const denominator = sqrt(pow(v2.y - v1.y, 2) + pow(v2.x - v1.x, 2));
            return numerator / denominator;
        }

        function getFootOfPerpendicular(px, py, v1, v2) {
            const t = ((px - v1.x) * (v2.x - v1.x) + (py - v1.y) * (v2.y - v1.y)) / ((v2.x - v1.x) * (v2.x - v1.x) + (v2.y - v1.y) * (v2.y - v1.y));
            return createVector(v1.x + t * (v2.x - v1.x), v1.y + t * (v2.y - v1.y));
        }

        function mousePressed() {
            const d = dist(mouseX, mouseY, pX, pY);
            if (d < 5) {
                isDragging = true;
            }
        }

        function mouseReleased() {
            isDragging = false;
        }

        function mouseDragged() {
            if (isDragging) {
                pX = mouseX;
                pY = mouseY;
                constrainPointInTriangle();
                redraw();
            }
        }

        function constrainPointInTriangle() {
            const inside = p5.Vector.sub(createVector(pX, pY), triangleVertices[0]).cross(p5.Vector.sub(triangleVertices[1], triangleVertices[0])).z > 0 &&
                          p5.Vector.sub(createVector(pX, pY), triangleVertices[1]).cross(p5.Vector.sub(triangleVertices[2], triangleVertices[1])).z > 0 &&
                          p5.Vector.sub(createVector(pX, pY), triangleVertices[2]).cross(p5.Vector.sub(triangleVertices[0], triangleVertices[2])).z > 0;

            if (!inside) {
                pX = 240;
                pY = 312;
            }
        }

        document.querySelectorAll('input[name="step-selector"]').forEach(radio => {
            radio.addEventListener('change', () => {
                currentStep = parseInt(radio.value);
                redraw();
            });
        });
    </script>
</body>
</html>
