</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="dwarf-planet-controls">
                <label for="dwarf-planet-selector">dwarf planet</label>
                <div id="dwarf-planet-selector">
                    <button data-planet="Ceres">Ceres</button>
                    <button data-planet="Pluto" class="active">Pluto</button>
                    <button data-planet="Makemake">Makemake</button>
                    <button data-planet="Haumea">Haumea</button>
                    <button data-planet="Eris">Eris</button>
                </div>
            </div>
            <div id="time-controls">
                <label for="time-slider">time</label>
                <input type="range" id="time-slider" min="0" max="110000" step="1" value="2922">
                <button id="play-pause-button">▶</button>
            </div>
        </div>
        <div id="visualization-container">
            <div id="date-display">Tue 1 Jan 2008 00:00:00</div>
            <canvas id="3d-canvas"></canvas>
        </div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/three@0.140.0/build/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.140.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        const orbitalElements = {
            Mercury: { a: 0.3871, e: 0.2056, i: 7.005, Ω: 48.331, ω: 29.124, M_epoch: 174.796 },
            Venus: { a: 0.7233, e: 0.0068, i: 3.395, Ω: 76.680, ω: 54.884, M_epoch: 50.416 },
            Earth: { a: 1.0000, e: 0.0167, i: 0.000, Ω: -11.260, ω: 114.208, M_epoch: 358.617 },
            Mars: { a: 1.5237, e: 0.0934, i: 1.850, Ω: 49.562, ω: 286.502, M_epoch: 19.412 },
            Ceres: { a: 2.7691, e: 0.0760, i: 10.593, Ω: 80.393, ω: 73.598, M_epoch: 77.372 },
            Pluto: { a: 39.482, e: 0.2488, i: 17.160, Ω: 110.299, ω: 113.834, M_epoch: 14.882 },
            Haumea: { a: 43.132, e: 0.1912, i: 28.220, Ω: 122.155, ω: 239.544, M_epoch: 204.03 },
            Makemake: { a: 45.791, e: 0.1559, i: 29.006, Ω: 79.623, ω: 294.821, M_epoch: 95.895 },
            Eris: { a: 67.668, e: 0.4418, i: 44.187, Ω: 35.951, ω: 151.291, M_epoch: 206.51 }
        };

        const j2000 = new Date('2000-01-01T12:00:00Z');

        function calculatePosition(date, orbitalElements) {
            const d = (date - j2000) / (1000 * 60 * 60 * 24);
            const P = 365.25 * Math.pow(orbitalElements.a, 3 / 2);
            let M = (orbitalElements.M_epoch + 360 / P * d) % 360;

            // Convert degrees to radians
            const i_rad = orbitalElements.i * Math.PI / 180;
            const Ω_rad = orbitalElements.Ω * Math.PI / 180;
            const ω_rad = orbitalElements.ω * Math.PI / 180;

            // Solve Kepler's equation for E using Newton-Raphson method
            let E = M; // Initial guess
            const tolerance = 1e-6;
            while (true) {
                const f = E - orbitalElements.e * Math.sin(E) - M;
                const df = 1 - orbitalElements.e * Math.cos(E);
                const deltaE = f / df;
                E -= deltaE;
                if (Math.abs(deltaE) < tolerance) break;
            }

            // Calculate true anomaly ν
            const ν = 2 * Math.atan(Math.sqrt((1 + orbitalElements.e) / (1 - orbitalElements.e)) * Math.tan(E / 2));

            // Calculate heliocentric distance r
            const r = orbitalElements.a * (1 - orbitalElements.e * Math.cos(E));

            // Calculate position in the orbital plane
            const x_orb = r * Math.cos(ν);
            const y_orb = r * Math.sin(ν);

            // Transform to 3D ecliptic coordinate system
            const x = x_orb * Math.cos(ω_rad) - y_orb * Math.sin(ω_rad) * Math.cos(i_rad);
            const y = x_orb * Math.sin(ω_rad) + y_orb * Math.cos(ω_rad) * Math.cos(i_rad);
            const z = y_orb * Math.sin(i_rad);

            return new THREE.Vector3(x, y, z);
        }

        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setSize(window.innerWidth, window.innerHeight);
        document.getElementById('visualization-container').appendChild(renderer.domElement);

        const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
        scene.add(ambientLight);

        const sun = new THREE.Mesh(new THREE.SphereGeometry(0.5, 32, 32), new THREE.MeshBasicMaterial({ color: 0xffff00 }));
        scene.add(sun);

        const planets = ['Mercury', 'Venus', 'Earth', 'Mars'];
        const planetColors = [0x808080, 0xfafad2, 0x0000ff, 0xff4500];
        const planetObjects = {};

        planets.forEach((planet, index) => {
            const planetMesh = new THREE.Mesh(new THREE.SphereGeometry(0.1, 32, 32), new THREE.MeshLambertMaterial({ color: planetColors[index] }));
            scene.add(planetMesh);
            planetObjects[planet] = planetMesh;
        });

        const dwarfPlanetColors = { Ceres: 0x800080, Pluto: 0x800000, Makemake: 0x800000, Haumea: 0x800000, Eris: 0x800000 };
        let selectedDwarfPlanet = 'Pluto';
        let dwarfPlanetMesh;
        let dwarfPlanetOrbitLine;

        function updateDwarfPlanet() {
            if (dwarfPlanetMesh) {
                scene.remove(dwarfPlanetMesh);
            }
            if (dwarfPlanetOrbitLine) {
                scene.remove(dwarfPlanetOrbitLine);
            }

            const orbitCurve = new THREE.EllipseCurve(
                0, 0, // ax, ay
                orbitalElements[selectedDwarfPlanet].a, orbitalElements[selectedDwarfPlanet].a * (1 - Math.pow(orbitalElements[selectedDwarfPlanet].e, 2)), // rx, ry
                0, 2 * Math.PI, // aStartAngle, aEndAngle
                false, // aClockwise
                0 // aRotation
            );

            const points = orbitCurve.getPoints(200);
            const orbitGeometry = new THREE.BufferGeometry().setFromPoints(points);
            const orbitMaterial = new THREE.LineBasicMaterial({ color: 0xff0000 });
            dwarfPlanetOrbitLine = new THREE.Line(orbitGeometry, orbitMaterial);
            scene.add(dwarfPlanetOrbitLine);

            const position = calculatePosition(new Date(j2000.getTime() + 2922 * 24 * 60 * 60 * 1000), orbitalElements[selectedDwarfPlanet]);
            dwarfPlanetMesh = new THREE.Mesh(new THREE.SphereGeometry(0.1, 32, 32), new THREE.MeshLambertMaterial({ color: dwarfPlanetColors[selectedDwarfPlanet] }));
            dwarfPlanetMesh.position.copy(position);
            scene.add(dwarfPlanetMesh);
        }

        updateDwarfPlanet();

        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        camera.position.set(0, 15, 30);
        controls.update();

        let isAnimating = false;
        let animationFrameId;

        function animate() {
            if (!isAnimating) return;

            const slider = document.getElementById('time-slider');
            slider.value = (parseInt(slider.value) + 2) % slider.max;
            updateTimeDisplay(new Date(j2000.getTime() + parseInt(slider.value) * 24 * 60 * 60 * 1000));
            updatePositions(new Date(j2000.getTime() + parseInt(slider.value) * 24 * 60 * 60 * 1000));
            renderer.render(scene, camera);
            animationFrameId = requestAnimationFrame(animate);
        }

        function updateTimeDisplay(date) {
            const options = { weekday: 'short', year: 'numeric', month: 'short', day: 'numeric' };
            document.getElementById('date-display').textContent = date.toLocaleDateString('en-US', options) + ' 00:00:00';
        }

        function updatePositions(date) {
            planets.forEach(planet => {
                const position = calculatePosition(date, orbitalElements[planet]);
                planetObjects[planet].position.copy(position);
            });

            const dwarfPosition = calculatePosition(date, orbitalElements[selectedDwarfPlanet]);
            dwarfPlanetMesh.position.copy(dwarfPosition);
        }

        document.getElementById('time-slider').addEventListener('input', () => {
            const slider = document.getElementById('time-slider');
            const date = new Date(j2000.getTime() + parseInt(slider.value) * 24 * 60 * 60 * 1000);
            updateTimeDisplay(date);
            updatePositions(date);
            renderer.render(scene, camera);
        });

        document.getElementById('play-pause-button').addEventListener('click', () => {
            isAnimating = !isAnimating;
            const button = document.getElementById('play-pause-button');
            button.textContent = isAnimating ? '⏸' : '▶';
            if (isAnimating) {
                animationFrameId = requestAnimationFrame(animate);
            } else {
                cancelAnimationFrame(animationFrameId);
            }
        });

        document.getElementById('dwarf-planet-selector').addEventListener('click', (event) => {
            if (event.target.tagName === 'BUTTON') {
                const buttons = document.querySelectorAll('#dwarf-planet-selector button');
                buttons.forEach(button => button.classList.remove('active'));
                event.target.classList.add('active');
                selectedDwarfPlanet = event.target.getAttribute('data-planet');
                updateDwarfPlanet();
                renderer.render(scene, camera);
            }
        });

        window.addEventListener('resize', () => {
            camera.aspect = window.innerWidth / window.innerHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(window.innerWidth, window.innerHeight);
        });

        renderer.render(scene, camera);
    </script>
</body>
</html>
