</head>
<body>

    <div id="main-container">
        <!-- Left Panel: Controls -->
        <div id="control-panel">
            <label for="select-cfc">CFC plot</label>
            <select id="select-cfc"></select>

            <label for="checkbox-show-all">Show all</label>
            <input type="checkbox" id="checkbox-show-all">

            <label for="slider-year">Year</label>
            <input type="range" id="slider-year" min="1940" max="2020" step="1" value="1950">
            <span id="label-year">1950</span>

            <label for="slider-time">Time (s)</label>
            <input type="range" id="slider-time" min="0" max="30" step="1" value="1">
            <span id="label-time">1</span>
        </div>

        <!-- Right Panel: Visualizations -->
        <div id="visualization-panel">
            <div id="plots-container">
                <div id="plot-ozone" class="plot"></div>
                <div id="plot-cfc" class="plot"></div>
            </div>
            <div id="scene-container"></div>
        </div>
    </div>

    <script>
        const cfcData = {
            'CFC-12': { color: 'purple', data: [[1940, 5], [1950, 20], [1960, 50], [1970, 150], [1980, 320], [1990, 480], [2000, 540], [2010, 520], [2020, 500]] },
            'CFC-11': { color: 'orange', data: [[1940, 2], [1950, 10], [1960, 30], [1970, 90], [1980, 180], [1990, 260], [2000, 250], [2010, 230], [2020, 220]] },
            'CFC-113': { color: 'black', data: [[1940, 0], [1950, 1], [1960, 5], [1970, 15], [1980, 40], [1990, 80], [2000, 85], [2010, 75], [2020, 70]] },
            'CCl4': { color: 'green', data: [[1940, 50], [1950, 60], [1960, 75], [1970, 90], [1980, 100], [1990, 105], [2000, 95], [2010, 85], [2020, 80]] }
        };

        const selectCfc = document.getElementById('select-cfc');
        const checkboxShowAll = document.getElementById('checkbox-show-all');
        const sliderYear = document.getElementById('slider-year');
        const labelYear = document.getElementById('label-year');
        const sliderTime = document.getElementById('slider-time');
        const labelTime = document.getElementById('label-time');

        const plotOzone = document.getElementById('plot-ozone');
        const plotCfc = document.getElementById('plot-cfc');
        const sceneContainer = document.getElementById('scene-container');

        // Initialize select options
        Object.keys(cfcData).forEach(cfc => {
            const option = document.createElement('option');
            option.value = cfc;
            option.textContent = cfc;
            selectCfc.appendChild(option);
        });
        selectCfc.value = 'CFC-12';

        // Initialize plots
        let ozonePlot = null;
        let cfcPlot = null;

        function updateCfcPlot() {
            const selectedCfc = selectCfc.value;
            const showAll = checkboxShowAll.checked;
            const traces = [];

            if (showAll) {
                Object.keys(cfcData).forEach(cfc => {
                    const trace = {
                        x: cfcData[cfc].data.map(d => d[0]),
                        y: cfcData[cfc].data.map(d => d[1]),
                        mode: 'lines',
                        name: cfc,
                        line: { color: cfcData[cfc].color }
                    };
                    traces.push(trace);
                });
            } else {
                const trace = {
                    x: cfcData[selectedCfc].data.map(d => d[0]),
                    y: cfcData[selectedCfc].data.map(d => d[1]),
                    mode: 'lines+markers',
                    name: selectedCfc,
                    line: { color: cfcData[selectedCfc].color },
                    marker: { color: 'red' }
                };
                traces.push(trace);
            }

            const layout = {
                title: 'CFC concentration',
                xaxis: { title: 'year', range: [1920, 2020] },
                yaxis: { range: [0, 600] }
            };

            if (cfcPlot) {
                Plotly.react(plotCfc, traces, layout);
            } else {
                Plotly.newPlot(plotCfc, traces, layout);
                cfcPlot = plotCfc;
            }
        }

        function updateOzonePlot() {
            const selectedCfc = selectCfc.value;
            const year = parseInt(sliderYear.value);
            const time = parseFloat(sliderTime.value);

            const cfcTrace = cfcData[selectedCfc].data.find(d => d[0] === year);
            const cfcConcentration = cfcTrace ? cfcTrace[1] : 0;
            const k = cfcConcentration / 100; // Example rate constant calculation

            const trace = {
                x: [0, time],
                y: [0, k * cfcConcentration * time],
                mode: 'lines',
                name: 'Ozone Concentration',
                line: { color: 'blue' }
            };

            const layout = {
                title: 'concentration of ozone',
                xaxis: { title: 'time (s)', range: [0, 30] },
                yaxis: { title: '\\(1/[O_3]\\)', range: [0, 110] }
            };

            if (ozonePlot) {
                Plotly.react(plotOzone, [trace], layout);
            } else {
                Plotly.newPlot(plotOzone, [trace], layout);
                ozonePlot = plotOzone;
            }
        }

        function update3DScene() {
            const year = parseInt(sliderYear.value);
            const cfcConcentration = cfcData[selectCfc.value].data.find(d => d[0] === year)[1];
            const opacity = 1 - (cfcConcentration / 600); // Inverse proportionality

            if (ozoneLayer) {
                ozoneLayer.material.opacity = opacity;
            }
        }

        function update() {
            updateCfcPlot();
            updateOzonePlot();
            update3DScene();
        }

        // Initialize 3D scene
        let renderer, scene, camera, earth, ozoneLayer, atmosphere, sun, radiation;

        function init3DScene() {
            scene = new THREE.Scene();
            camera = new THREE.PerspectiveCamera(75, sceneContainer.clientWidth / sceneContainer.clientHeight, 0.1, 1000);
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(sceneContainer.clientWidth, sceneContainer.clientHeight);
            sceneContainer.appendChild(renderer.domElement);

            // Earth
            const earthGeometry = new THREE.SphereGeometry(1, 32, 32);
            const earthMaterial = new THREE.MeshBasicMaterial({ color: 0x0000ff });
            earth = new THREE.Mesh(earthGeometry, earthMaterial);
            scene.add(earth);

            // Ozone Layer
            const ozoneGeometry = new THREE.SphereGeometry(1.05, 32, 32);
            const ozoneMaterial = new THREE.MeshBasicMaterial({ color: 0x00ff00, transparent: true, opacity: 0.5 });
            ozoneLayer = new THREE.Mesh(ozoneGeometry, ozoneMaterial);
            scene.add(ozoneLayer);

            // Atmosphere
            const atmosphereGeometry = new THREE.SphereGeometry(1.1, 32, 32);
            const atmosphereMaterial = new THREE.MeshBasicMaterial({ color: 0xffaa88, transparent: true, opacity: 0.1 });
            atmosphere = new THREE.Mesh(atmosphereGeometry, atmosphereMaterial);
            scene.add(atmosphere);

            // Sun
            const sunGeometry = new THREE.SphereGeometry(0.1, 32, 32);
            const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00 });
            sun = new THREE.Mesh(sunGeometry, sunMaterial);
            sun.position.set(-2, 0, 0);
            scene.add(sun);

            // Radiation
            const arrowLength = 1.5;
            const arrowColor = 0xffa500;
            radiation = [];
            for (let i = 0; i < 10; i++) {
                const angle = Math.random() * Math.PI * 2;
                const arrow = new THREE.ArrowHelper(new THREE.Vector3(Math.cos(angle), Math.sin(angle), 0).normalize(), new THREE.Vector3(-2, 0, 0), arrowLength, arrowColor);
                scene.add(arrow);
                radiation.push(arrow);
            }

            camera.position.z = 3;
        }

        function animate() {
            requestAnimationFrame(animate);
            renderer.render(scene, camera);
        }

        init3DScene();
        animate();

        // Event listeners
        selectCfc.addEventListener('change', update);
        checkboxShowAll.addEventListener('change', update);
        sliderYear.addEventListener('input', () => {
            labelYear.textContent = sliderYear.value;
            update();
        });
        sliderTime.addEventListener('input', () => {
            labelTime.textContent = sliderTime.value;
            updateOzonePlot();
        });

        // Initial update
        update();
    </script>

</body>
</html>
