    <title>Eratosthenes's Measure of the Earth's Circumference</title>
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 20px;
            background-color: #f0f0f0;
        }
        #control-panel {
            margin-bottom: 20px;
        }
        #visualization-container {
            width: 600px;
            height: 450px;
            margin: 0 auto;
        }
    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <h1>Eratosthenes's Measure of the Earth's Circumference</h1>
    <p>This visualization demonstrates how Eratosthenes measured the Earth's circumference using the angle of the sun's rays.</p>
    <div id="control-panel">
        <label for="slider-post-angle">post angle</label>
        <input type="range" id="slider-post-angle" min="1" max="60" step="0.1" value="7">
        <span id="display-post-angle">7</span>
        <label for="checkbox-zoom">zoom</label>
        <input type="checkbox" id="checkbox-zoom">
    </div>
    <div id="visualization-container">
        <canvas id="demo-canvas"></canvas>
    </div>

    <script>
        let angle = 7;
        let isZoomed = false;

        function setup() {
            createCanvas(600, 450);
            angleMode(DEGREES);
            noLoop();
        }

        function draw() {
            background(240);
            if (isZoomed) {
                drawZoomedView();
            } else {
                drawFullView();
            }
        }

        function drawFullView() {
            const earthRadius = 150;
            const earthX = width / 4;
            const earthY = height / 2;

            // Draw Earth
            fill('#468296');
            ellipse(earthX, earthY, earthRadius * 2, earthRadius * 2);
            fill(0);
            textSize(16);
            textAlign(CENTER, CENTER);
            text("Earth", earthX, earthY);

            // Draw Syene line
            stroke(0);
            strokeWeight(2);
            line(earthX, earthY, earthX + earthRadius, earthY);
            text("Syene", earthX + earthRadius, earthY + 10);

            // Calculate Alexandria position
            const alexandriaX = earthX + earthRadius * cos(angle);
            const alexandriaY = earthY + earthRadius * sin(angle);

            // Draw Alexandria line
            line(earthX, earthY, alexandriaX, alexandriaY);
            line(alexandriaX, alexandriaY, alexandriaX, height - 20);
            text("Alexandria", alexandriaX, height - 10);

            // Draw angle arc and label
            noFill();
            stroke(0);
            arc(earthX, earthY, earthRadius * 2, earthRadius * 2, 0, angle);
            fill(0);
            textSize(16);
            text(`${angle.toFixed(1)}°`, earthX + earthRadius * cos(angle / 2), earthY + earthRadius * sin(angle / 2));

            // Draw observation point
            fill(0);
            noStroke();
            ellipse(alexandriaX, alexandriaY, 5, 5);
        }

        function drawZoomedView() {
            const sectorRadius = 200;
            const centerX = 150;
            const centerY = height - 100;

            // Draw Earth sector
            fill('#468296');
            arc(centerX, centerY, sectorRadius * 2, sectorRadius * 2, 0, angle);
            fill(0);
            textSize(16);
            textAlign(CENTER, BOTTOM);
            text("Earth", centerX, centerY - sectorRadius - 10);

            // Draw Syene and Alexandria radii
            stroke(0);
            strokeWeight(2);
            line(centerX, centerY, centerX + sectorRadius, centerY);
            line(centerX, centerY, centerX + sectorRadius * cos(angle), centerY - sectorRadius * sin(angle));

            // Draw labels
            text("Syene", centerX + sectorRadius, centerY);
            text("Alexandria", centerX + sectorRadius * cos(angle), centerY - sectorRadius * sin(angle));

            // Draw angle arc and label
            noFill();
            stroke(0);
            arc(centerX, centerY, sectorRadius * 2, sectorRadius * 2, 0, angle);
            fill(0);
            textSize(16);
            text(`${angle.toFixed(1)}°`, centerX + sectorRadius * cos(angle / 2), centerY - sectorRadius * sin(angle / 2));

            // Draw post and shadow
            const postLength = 30;
            fill('#DAA520');
            stroke(0);
            strokeWeight(1);
            triangle(
                centerX + sectorRadius * cos(angle),
                centerY - sectorRadius * sin(angle),
                centerX + sectorRadius * cos(angle) + postLength,
                centerY - sectorRadius * sin(angle),
                centerX + sectorRadius * cos(angle),
                centerY - sectorRadius * sin(angle) - postLength
            );
        }

        function updateDisplay() {
            const slider = document.getElementById('slider-post-angle');
            const display = document.getElementById('display-post-angle');
            angle = parseFloat(slider.value);
            display.textContent = angle.toFixed(1);
            redraw();
        }

        function toggleZoom() {
            const checkbox = document.getElementById('checkbox-zoom');
            isZoomed = checkbox.checked;
            redraw();
        }

        document.getElementById('slider-post-angle').addEventListener('input', updateDisplay);
        document.getElementById('checkbox-zoom').addEventListener('change', toggleZoom);
    </script>
</body>
</html>
