        <div id="control-panel">
            <button id="btn-reset">+</button>
            <div class="slider-container">
                <label for="slider-moon-pos">position of moon</label>
                <input type="range" id="slider-moon-pos" min="0" max="360" step="1" value="144">
            </div>
            <div class="slider-container">
                <label for="slider-moon-pull">pull from moon</label>
                <input type="range" id="slider-moon-pull" min="0" max="100" step="1" value="40">
            </div>
            <div class="slider-container">
                <label for="slider-sun-pos">position of sun</label>
                <input type="range" id="slider-sun-pos" min="0" max="360" step="1" value="144">
            </div>
            <div class="slider-container">
                <label for="slider-sun-pull">pull from sun</label>
                <input type="range" id="slider-sun-pull" min="0" max="100" step="1" value="20">
            </div>
        </div>
        <div id="viz-container"></div>
    </div>

    <script>
        let baseWaterRadius = 180;
        let scalingFactor = 0.5;

        function setup() {
            createCanvas(500, 500).parent('viz-container');
            noLoop();
        }

        function draw() {
            clear();
            let moonAngle = parseInt(document.getElementById('slider-moon-pos').value);
            let moonPull = parseInt(document.getElementById('slider-moon-pull').value);
            let sunAngle = parseInt(document.getElementById('slider-sun-pos').value);
            let sunPull = parseInt(document.getElementById('slider-sun-pull').value);

            beginShape();
            for (let theta = 0; theta <= 360; theta++) {
                let thetaRad = radians(theta);
                let moonEffect = moonPull * cos(thetaRad - radians(moonAngle)) ** 2;
                let sunEffect = sunPull * cos(thetaRad - radians(sunAngle)) ** 2;
                let totalRadius = baseWaterRadius + scalingFactor * (moonEffect + sunEffect);
                let x = width / 2 + totalRadius * cos(thetaRad);
                let y = height / 2 + totalRadius * sin(thetaRad);
                vertex(x, y);
            }
            endShape(CLOSE);
            fill('#aed6f1');
            stroke('#000000');
            strokeWeight(1);

            fill('#966F33');
            noStroke();
            ellipse(width / 2, height / 2, 360, 360);
        }

        document.querySelectorAll('.slider-container input').forEach(slider => {
            slider.addEventListener('input', () => {
                redraw();
            });
        });

        document.getElementById('btn-reset').addEventListener('click', () => {
            document.getElementById('slider-moon-pos').value = 144;
            document.getElementById('slider-moon-pull').value = 40;
            document.getElementById('slider-sun-pos').value = 144;
            document.getElementById('slider-sun-pull').value = 20;
            redraw();
        });
    </script>
</body>
</html>
