                <p>telescopic view</p>
            </div>
            <div id="orbit-view-wrapper">
                <canvas id="orbit-canvas"></canvas>
                <p>orbit diagram</p>
            </div>
        </div>
    </div>

    <script>
        const earthPositionSlider = document.getElementById('slider-earth-position');
        const cometPositionSlider = document.getElementById('slider-comet-position');
        const comaSlider = document.getElementById('slider-coma');
        const tailSlider = document.getElementById('slider-tail');
        const tailSpreadSlider = document.getElementById('slider-tail-spread');

        const telescopicCanvas = document.getElementById('telescopic-canvas');
        const orbitCanvas = document.getElementById('orbit-canvas');

        const orbitScene = new THREE.Scene();
        const orbitCamera = new THREE.PerspectiveCamera(75, orbitCanvas.clientWidth / orbitCanvas.clientHeight, 0.1, 1000);
        orbitCamera.position.set(0, 20, 20);
        orbitCamera.lookAt(0, 0, 0);

        const telescopicScene = new THREE.Scene();
        const telescopicCamera = new THREE.PerspectiveCamera(15, telescopicCanvas.clientWidth / telescopicCanvas.clientHeight, 0.1, 1000);

        const orbitRenderer = new THREE.WebGLRenderer({ canvas: orbitCanvas });
        const telescopicRenderer = new THREE.WebGLRenderer({ canvas: telescopicCanvas });

        const ambientLight = new THREE.AmbientLight(0x404040);
        const sunLight = new THREE.PointLight(0xffff00, 1, 100);
        sunLight.position.set(0, 0, 0);
        orbitScene.add(ambientLight);
        orbitScene.add(sunLight);

        const earthGeometry = new THREE.SphereGeometry(0.1, 32, 32);
        const earthMaterial = new THREE.MeshBasicMaterial({ color: 0x0000ff });
        const earth = new THREE.Mesh(earthGeometry, earthMaterial);
        orbitScene.add(earth);

        const sunGeometry = new THREE.SphereGeometry(0.2, 32, 32);
        const sunMaterial = new THREE.MeshBasicMaterial({ color: 0xffff00, emissive: 0xffff00 });
        const sun = new THREE.Mesh(sunGeometry, sunMaterial);
        orbitScene.add(sun);

        const cometGroup = new THREE.Group();

        const nucleusGeometry = new THREE.SphereGeometry(0.2, 32, 32);
        const nucleusMaterial = new THREE.MeshBasicMaterial({ color: 0xffffff });
        const nucleus = new THREE.Mesh(nucleusGeometry, nucleusMaterial);
        cometGroup.add(nucleus);

        const comaGeometry = new THREE.SphereGeometry(1, 32, 32);
        const comaMaterial = new THREE.MeshBasicMaterial({ color: 0x90ee90, opacity: 0.5, transparent: true });
        const coma = new THREE.Mesh(comaGeometry, comaMaterial);
        cometGroup.add(coma);

        const tailGeometry = new THREE.ConeGeometry(0.5, 2, 32);
        const tailMaterial = new THREE.MeshBasicMaterial({ color: 0x90ee90, opacity: 0.4, transparent: true });
        const tail = new THREE.Mesh(tailGeometry, tailMaterial);
        cometGroup.add(tail);

        orbitScene.add(cometGroup);
        telescopicScene.add(cometGroup);

        function createOrbit(a, e, color) {
            const curve = new THREE.EllipseCurve(
                0, 0,
                a, a,
                0, 2 * Math.PI,
                false, 0
            );
            const points = curve.getPoints(50);
            const geometry = new THREE.BufferGeometry().setFromPoints(points);
            const material = new THREE.LineBasicMaterial({ color: color });
            return new THREE.Line(geometry, material);
        }

        const earthOrbit = createOrbit(4, 0.0167, 0x0000ff);
        const marsOrbit = createOrbit(6.1, 0.0934, 0xff0000);
        const jupiterOrbit = createOrbit(20.8, 0.0489, 0xffa500);
        const cometOrbit = createOrbit(14.4, 0.432, 0xffff00);

        orbitScene.add(earthOrbit);
        orbitScene.add(marsOrbit);
        orbitScene.add(jupiterOrbit);
        orbitScene.add(cometOrbit);

        function update() {
            const earthAngle = (earthPositionSlider.value * Math.PI) / 180;
            earth.position.x = 4 * Math.cos(earthAngle);
            earth.position.z = 4 * Math.sin(earthAngle);

            const cometAngle = (cometPositionSlider.value * Math.PI) / 180;
            const cometX = 14.4 * Math.cos(cometAngle);
            const cometZ = 14.4 * Math.sin(cometAngle);
            cometGroup.position.set(cometX, 0, cometZ);

            const tailLength = (tailSlider.value / 100) * 2;
            tail.scale.y = tailLength;

            const tailWidth = (tailSpreadSlider.value / 100) * 0.5;
            tail.scale.x = tailWidth;
            tail.scale.z = tailWidth;

            const comaScale = (comaSlider.value / 100) * 1;
            coma.scale.set(comaScale, comaScale, comaScale);

            telescopicCamera.position.copy(earth.position);
            telescopicCamera.position.y += 1;
            telescopicCamera.lookAt(cometGroup.position);

            orbitRenderer.render(orbitScene, orbitCamera);
            telescopicRenderer.render(telescopicScene, telescopicCamera);
        }

        function animate() {
            requestAnimationFrame(animate);
            update();
        }

        earthPositionSlider.addEventListener('input', update);
        cometPositionSlider.addEventListener('input', update);
        comaSlider.addEventListener('input', update);
        tailSlider.addEventListener('input', update);
        tailSpreadSlider.addEventListener('input', update);

        window.addEventListener('resize', () => {
            orbitCamera.aspect = orbitCanvas.clientWidth / orbitCanvas.clientHeight;
            orbitCamera.updateProjectionMatrix();
            orbitRenderer.setSize(orbitCanvas.clientWidth, orbitCanvas.clientHeight);

            telescopicCamera.aspect = telescopicCanvas.clientWidth / telescopicCanvas.clientHeight;
            telescopicCamera.updateProjectionMatrix();
            telescopicRenderer.setSize(telescopicCanvas.clientWidth, telescopicCanvas.clientHeight);
        });

        animate();
    </script>
</body>
</html>
