<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="control-group">
                <label for="slider-m">m</label>
                <input type="range" id="slider-m" min="0" max="10" step="0.001" value="2.845">
                <span id="label-m">2.845</span>
            </div>
            <div class="control-group">
                <label for="slider-x">x</label>
                <input type="range" id="slider-x" min="0" max="10" step="0.1" value="4.1">
                <span id="label-x">4.1</span>
            </div>
            <div class="control-group">
                <label for="slider-y">y</label>
                <input type="range" id="slider-y" min="0.1" max="5" step="0.001" value="1.498">
                <span id="label-y">1.498</span>
            </div>
        </div>
        <div id="visualization">
            <canvas id="canvas" width="800" height="500"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        let mSlider, xSlider, ySlider;
        let labelM, labelX, labelY;
        let canvas;

        function setup() {
            canvas = createCanvas(800, 500);
            canvas.parent('visualization');

            mSlider = select('#slider-m');
            xSlider = select('#slider-x');
            ySlider = select('#slider-y');
            labelM = select('#label-m');
            labelX = select('#label-x');
            labelY = select('#label-y');

            mSlider.input(updateValues);
            xSlider.input(updateValues);
            ySlider.input(updateValues);

            updateValues();
        }

        function draw() {
            background(240);
            stroke(0);
            fill(255);

            // Draw posts
            rect(100, 150, 10, 300);
            rect(700, 150, 10, 300);

            // Draw horizontal reference line
            drawDashedLine(100, 150, 700, 150);

            // Calculate positions
            let x = parseFloat(xSlider.value());
            let y = parseFloat(ySlider.value());
            let L = 10;
            let g = 9.81;
            let Fg = mSlider.value() * g;
            let theta1 = atan(y / x);
            let theta2 = atan(y / (L - x));
            let T1 = (Fg * cos(theta2)) / sin(theta1 + theta2);
            let T2 = (Fg * cos(theta1)) / sin(theta1 + theta2);

            // Draw ropes
            line(100, 150, map(x, 0, L, 100, 700), 150 + y * 50);
            line(700, 150, map(x, 0, L, 100, 700), 150 + y * 50);

            // Draw mass
            ellipse(map(x, 0, L, 100, 700), 150 + y * 50, 20);

            // Draw angles
            arc(100, 150, 50, 50, 0, theta1);
            arc(700, 150, 50, 50, PI, theta2);

            // Draw labels
            textSize(16);
            fill(0);
            text("T\u2081 = " + T1.toFixed(4) + " N", width / 2 - 100, 50);
            text("T\u2082 = " + T2.toFixed(4) + " N", width / 2 + 50, 50);
            text("θ\u2081", 100 + 25 * cos(theta1 / 2), 150 + 25 * sin(theta1 / 2));
            text("θ\u2082", 700 - 25 * cos(theta2 / 2), 150 + 25 * sin(theta2 / 2));
        }

        function updateValues() {
            labelM.html(mSlider.value());
            labelX.html(xSlider.value());
            labelY.html(ySlider.value());
        }

        function drawDashedLine(x1, y1, x2, y2) {
            let dashLength = 10;
            let dashCount = int(dist(x1, y1, x2, y2) / dashLength);
            let angle = atan2(y2 - y1, x2 - x1);
            for (let i = 0; i < dashCount; i++) {
                line(x1 + i * dashLength * cos(angle), y1 + i * dashLength * sin(angle),
                     x1 + (i * dashLength + dashLength / 2) * cos(angle), y1 + (i * dashLength + dashLength / 2) * sin(angle));
            }
        }
    </script>
</body>
</html>
