    </style>
</head>
<body>
    <div id="main-container">
        <div id="controls-container">
            <h2>Initial Conditions</h2>
            <div class="control-row">
                <label for="slider-displacement">displacement</label>
                <input type="range" id="slider-displacement" min="-3" max="3" step="0.1" value="0.1">
                <span id="value-displacement">0.1</span>
            </div>
            <div class="control-row">
                <label for="slider-velocity">velocity</label>
                <input type="range" id="slider-velocity" min="-5" max="5" step="0.1" value="1">
                <span id="value-velocity">1.0</span>
            </div>
            <h2>System Parameters</h2>
            <div class="control-row">
                <label for="slider-stiffness">spring stiffness</label>
                <input type="range" id="slider-stiffness" min="0.1" max="5" step="0.01" value="1">
                <span id="value-stiffness">1.00</span>
            </div>
            <div class="control-row">
                <label for="slider-mass">mass</label>
                <input type="range" id="slider-mass" min="0.1" max="5" step="0.01" value="1">
                <span id="value-mass">1.00</span>
            </div>
            <div class="control-row">
                <label for="slider-damping">dampening</label>
                <input type="range" id="slider-damping" min="0" max="5" step="0.1" value="0.1">
                <span id="value-damping">0.1</span>
            </div>
            <h2>Proportional Displacement Gain</h2>
            <div class="control-row">
                <label for="slider-proportional-gain">proportional displacement gain</label>
                <input type="range" id="slider-proportional-gain" min="0" max="5" step="0.1" value="0.1">
                <span id="value-proportional-gain">0.1</span>
            </div>
            <h2>Derivative Velocity Gain</h2>
            <div class="control-row">
                <label for="slider-derivative-gain">derivative velocity gain</label>
                <input type="range" id="slider-derivative-gain" min="0" max="5" step="0.1" value="0.1">
                <span id="value-derivative-gain">0.1</span>
            </div>
            <h2>Release System</h2>
            <div class="control-row">
                <button id="btn-play">▶</button>
                <button id="btn-pause">||</button>
                <button id="btn-reset">|◀</button>
            </div>
        </div>
        <div id="visuals-container">
            <div id="scene-container"></div>
            <div id="plot-displacement"></div>
            <div id="plot-velocity"></div>
        </div>
    </div>

    <!-- Three.js -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <!-- Plotly.js -->
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script>
        let time = 0;
        let animationFrameId;
        let isRunning = false;

        const sliders = [
            { id: 'slider-displacement', spanId: 'value-displacement' },
            { id: 'slider-velocity', spanId: 'value-velocity' },
            { id: 'slider-stiffness', spanId: 'value-stiffness' },
            { id: 'slider-mass', spanId: 'value-mass' },
            { id: 'slider-damping', spanId: 'value-damping' },
            { id: 'slider-proportional-gain', spanId: 'value-proportional-gain' },
            { id: 'slider-derivative-gain', spanId: 'value-derivative-gain' }
        ];

        sliders.forEach(({ id, spanId }) => {
            const slider = document.getElementById(id);
            const span = document.getElementById(spanId);
            slider.addEventListener('input', () => {
                span.textContent = parseFloat(slider.value).toFixed(2);
                pauseAndReset();
                updateSystemParameters();
                redrawPlots();
                updateScene();
            });
        });

        const btnPlay = document.getElementById('btn-play');
        btnPlay.addEventListener('click', startAnimation);

        const btnPause = document.getElementById('btn-pause');
        btnPause.addEventListener('click', pauseAnimation);

        const btnReset = document.getElementById('btn-reset');
        btnReset.addEventListener('click', resetAnimation);

        // Initialize 3D Scene
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        const renderer = new THREE.WebGLRenderer();
        renderer.setSize(window.innerWidth / 3, window.innerHeight / 3);
        document.getElementById('scene-container').appendChild(renderer.domElement);

        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const directionalLight = new THREE.DirectionalLight(0xffffff, 0.5);
        directionalLight.position.set(1, 1, 1).normalize();
        scene.add(directionalLight);

        const baseGeometry = new THREE.BoxGeometry(2, 0.2, 2);
        const baseMaterial = new THREE.MeshStandardMaterial({ color: 0x00ff00 });
        const base = new THREE.Mesh(baseGeometry, baseMaterial);
        scene.add(base);

        const massGeometry = new THREE.BoxGeometry(0.2, 0.2, 0.2);
        const massMaterial = new THREE.MeshStandardMaterial({ color: 0xffff00 });
        const mass = new THREE.Mesh(massGeometry, massMaterial);
        scene.add(mass);

        const springGeometry = new THREE.TubeGeometry(new THREE.CatmullRomCurve3([
            new THREE.Vector3(-1, 0, 0),
            new THREE.Vector3(0, 0, 0),
            new THREE.Vector3(1, 0, 0)
        ]), 64, 0.02, 8, false);
        const springMaterial = new THREE.MeshStandardMaterial({ color: 0xff0000 });
        const spring = new THREE.Mesh(springGeometry, springMaterial);
        scene.add(spring);

        const damperGeometry = new THREE.CylinderGeometry(0.05, 0.05, 0.4, 32);
        const damperMaterial = new THREE.MeshStandardMaterial({ color: 0x0000ff });
        const damper = new THREE.Mesh(damperGeometry, damperMaterial);
        damper.position.set(1.2, 0, 0);
        scene.add(damper);

        const enclosureGeometry = new THREE.BoxGeometry(2.1, 0.3, 2.1);
        const enclosureMaterial = new THREE.MeshBasicMaterial({ color: 0x000000, wireframe: true });
        const enclosure = new THREE.Mesh(enclosureGeometry, enclosureMaterial);
        scene.add(enclosure);

        camera.position.z = 5;

        // Initialize Plots
        const displacementData = [{
            x: [],
            y: [],
            mode: 'lines',
            line: { color: 'blue' }
        }];
        const displacementLayout = {
            title: 'Displacement vs Time',
            xaxis: { title: 'Time (s)', range: [0, 10] },
            yaxis: { title: 'Displacement (m)', range: [-3, 3] },
            margin: { t: 20, b: 20, l: 20, r: 20 },
            showlegend: false
        };
        Plotly.newPlot('plot-displacement', displacementData, displacementLayout);

        const velocityData = [{
            x: [],
            y: [],
            mode: 'lines',
            line: { color: 'green' }
        }];
        const velocityLayout = {
            title: 'Velocity vs Time',
            xaxis: { title: 'Time (s)', range: [0, 10] },
            yaxis: { title: 'Velocity (m/s)', range: [-5, 5] },
            margin: { t: 20, b: 20, l: 20, r: 20 },
            showlegend: false
        };
        Plotly.newPlot('plot-velocity', velocityData, velocityLayout);

        // System Parameters
        let x0 = 0.1, v0 = 1, k = 1, m = 1, c = 0.1, Kp = 0.1, Kd = 0.1;

        function updateSystemParameters() {
            x0 = parseFloat(document.getElementById('slider-displacement').value);
            v0 = parseFloat(document.getElementById('slider-velocity').value);
            k = parseFloat(document.getElementById('slider-stiffness').value);
            m = parseFloat(document.getElementById('slider-mass').value);
            c = parseFloat(document.getElementById('slider-damping').value);
            Kp = parseFloat(document.getElementById('slider-proportional-gain').value);
            Kd = parseFloat(document.getElementById('slider-derivative-gain').value);
        }

        function calculateTrajectory() {
            const dt = 0.01;
            const steps = 1000;
            const x = [x0];
            const v = [v0];
            for (let i = 1; i <= steps; i++) {
                const a = (-k * x[i - 1] - c * v[i - 1] + Kp * x[i - 1] + Kd * v[i - 1]) / m;
                v.push(v[i - 1] + a * dt);
                x.push(x[i - 1] + v[i] * dt);
            }
            return { x, v };
        }

        function redrawPlots() {
            const { x, v } = calculateTrajectory();
            displacementData[0].x = x;
            displacementData[0].y = x;
            Plotly.redraw('plot-displacement');

            velocityData[0].x = x;
            velocityData[0].y = v;
            Plotly.redraw('plot-velocity');
        }

        function updateScene() {
            const { x, v } = calculateTrajectory();
            const index = Math.floor(time / 0.01);
            if (index < x.length) {
                mass.position.x = x[index];
                spring.scale.x = x[index] + 1;
                damper.position.x = x[index] + 1.2;
            }
        }

        function animate() {
            if (!isRunning) return;
            time += 0.01;
            if (time > 10) {
                pauseAnimation();
                return;
            }
            updateScene();
            Plotly.restyle('plot-displacement', {
                x: [[time]],
                y: [[calculateTrajectory().x[Math.floor(time / 0.01)]]]
            }, [0]);
            Plotly.restyle('plot-velocity', {
                x: [[time]],
                y: [[calculateTrajectory().v[Math.floor(time / 0.01)]]]
            }, [0]);
            animationFrameId = requestAnimationFrame(animate);
        }

        function startAnimation() {
            if (!isRunning) {
                isRunning = true;
                animate();
            }
        }

        function pauseAnimation() {
            isRunning = false;
            cancelAnimationFrame(animationFrameId);
        }

        function resetAnimation() {
            pauseAnimation();
            time = 0;
            updateScene();
            Plotly.restyle('plot-displacement', {
                x: [[0]],
                y: [[x0]]
            }, [0]);
            Plotly.restyle('plot-velocity', {
                x: [[0]],
                y: [[v0]]
            }, [0]);
        }

        function pauseAndReset() {
            pauseAnimation();
            resetAnimation();
        }

        // Initial setup
        updateSystemParameters();
        redrawPlots();
        updateScene();
    </script>
</body>
</html>
