    <!-- Libraries -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/numeric/1.2.6/numeric.min.js"></script>

    <script>
        let isAnimating = false;
        let t = 0;
        let omega;
        let A;
        let c;
        let masses = [1, 1, 1];
        let displacements = [0.1, 0.1, 0];
        let tension = 50;

        const sketch = function(p) {
            p.setup = function() {
                p.createCanvas(500, 500);
                resetSimulation();
            };

            p.draw = function() {
                if (!isAnimating) return;
                p.background(255);
                p.stroke(0);
                p.line(0, p.height / 2, p.width, p.height / 2);
                p.line(p.width / 4, p.height / 2, p.width / 4, p.height / 2 - displacements[0] * p.height / 2);
                p.line(p.width / 2, p.height / 2, p.width / 2, p.height / 2 - displacements[1] * p.height / 2);
                p.line(p.width * 3 / 4, p.height / 2, p.width * 3 / 4, p.height / 2 - displacements[2] * p.height / 2);
                p.line(p.width, p.height / 2, p.width, p.height / 2);

                p.fill(255, 0, 0);
                p.circle(p.width / 4, p.height / 2 - displacements[0] * p.height / 2, Math.sqrt(masses[0]) * 10);
                p.circle(p.width / 2, p.height / 2 - displacements[1] * p.height / 2, Math.sqrt(masses[1]) * 10);
                p.circle(p.width * 3 / 4, p.height / 2 - displacements[2] * p.height / 2, Math.sqrt(masses[2]) * 10);

                t += 0.02;
                updatePlots();
            };
        };

        const myp5 = new p5(sketch, 'main-animation-container');

        function resetSimulation() {
            isAnimating = false;
            document.getElementById('btn-play-pause').innerText = '▶';
            t = 0;

            const M = numeric.diag(masses);
            const K = tension * [[2, -1, 0], [-1, 2, -1], [0, -1, 2]];
            const C = numeric.dot(numeric.dot(numeric.inv(numeric.sqrtm(M)), K), numeric.inv(numeric.sqrtm(M)));
            const eigResult = numeric.eig(C);
            omega = eigResult.lambda.x.map(Math.sqrt);
            A = eigResult.E.x;

            const y0 = displacements;
            c = numeric.solve(A, y0);

            updatePlots();
        }

        function updatePlots() {
            const displacementData = [
                { x: Array.from({ length: 500 }, (_, i) => i / 100), y: Array.from({ length: 500 }, (_, i) => c[0] * A[0] * Math.cos(omega[0] * (i / 100))), type: 'scatter', mode: 'lines', name: 'Mass 1', line: { color: 'blue' } },
                { x: Array.from({ length: 500 }, (_, i) => i / 100), y: Array.from({ length: 500 }, (_, i) => c[1] * A[1] * Math.cos(omega[1] * (i / 100))), type: 'scatter', mode: 'lines', name: 'Mass 2', line: { color: 'orange' } },
                { x: Array.from({ length: 500 }, (_, i) => i / 100), y: Array.from({ length: 500 }, (_, i) => c[2] * A[2] * Math.cos(omega[2] * (i / 100))), type: 'scatter', mode: 'lines', name: 'Mass 3', line: { color: 'green' } }
            ];

            const velocityData = [
                { x: Array.from({ length: 500 }, (_, i) => i / 100), y: Array.from({ length: 500 }, (_, i) => -c[0] * A[0] * omega[0] * Math.sin(omega[0] * (i / 100))), type: 'scatter', mode: 'lines', name: 'Mass 1', line: { color: 'blue' } },
                { x: Array.from({ length: 500 }, (_, i) => i / 100), y: Array.from({ length: 500 }, (_, i) => -c[1] * A[1] * omega[1] * Math.sin(omega[1] * (i / 100))), type: 'scatter', mode: 'lines', name: 'Mass 2', line: { color: 'orange' } },
                { x: Array.from({ length: 500 }, (_, i) => i / 100), y: Array.from({ length: 500 }, (_, i) => -c[2] * A[2] * omega[2] * Math.sin(omega[2] * (i / 100))), type: 'scatter', mode: 'lines', name: 'Mass 3', line: { color: 'green' } }
            ];

            Plotly.newPlot('displacement-plot-container', displacementData, {
                title: 'displacement',
                xaxis: { title: 'time t' },
                shapes: [{ type: 'line', x0: t, x1: t, yref: 'paper', y0: 0, y1: 1, line: { color: 'black', dash: 'dash' } }]
            });

            Plotly.newPlot('velocity-plot-container', velocityData, {
                title: 'velocity',
                xaxis: { title: 'time t' },
                shapes: [{ type: 'line', x0: t, x1: t, yref: 'paper', y0: 0, y1: 1, line: { color: 'black', dash: 'dash' } }]
            });
        }

        document.getElementById('slider-tension').addEventListener('input', () => {
            tension = parseFloat(document.getElementById('slider-tension').value);
            resetSimulation();
        });

        document.getElementById('slider-x1').addEventListener('input', () => {
            displacements[0] = parseFloat(document.getElementById('slider-x1').value);
            resetSimulation();
        });

        document.getElementById('slider-x2').addEventListener('input', () => {
            displacements[1] = parseFloat(document.getElementById('slider-x2').value);
            resetSimulation();
        });

        document.getElementById('slider-x3').addEventListener('input', () => {
            displacements[2] = parseFloat(document.getElementById('slider-x3').value);
            resetSimulation();
        });

        document.getElementById('slider-m1').addEventListener('input', () => {
            masses[0] = parseFloat(document.getElementById('slider-m1').value);
            resetSimulation();
        });

        document.getElementById('slider-m2').addEventListener('input', () => {
            masses[1] = parseFloat(document.getElementById('slider-m2').value);
            resetSimulation();
        });

        document.getElementById('slider-m3').addEventListener('input', () => {
            masses[2] = parseFloat(document.getElementById('slider-m3').value);
            resetSimulation();
        });

        document.getElementById('btn-play-pause').addEventListener('click', () => {
            isAnimating = !isAnimating;
            if (isAnimating) {
                myp5.loop();
                document.getElementById('btn-play-pause').innerText = '❚❚';
            } else {
                myp5.noLoop();
                document.getElementById('btn-play-pause').innerText = '▶';
            }
        });

        document.getElementById('btn-reset').addEventListener('click', resetSimulation);
    </script>
</body>
</html>
