    </style>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
</head>
<body>
    <div id="app-container">
        <div id="header-controls">
            <button id="btn-move" class="active">move the points</button>
            <button id="btn-start-algo">start the algorithm</button>
        </div>
        <div id="step-controls" style="display: none;">
            <div>
                <button disabled>step 1</button>
                <button id="btn-step1-explain">explain</button>
                <button id="btn-step1-do">do it</button>
            </div>
            <div>
                <button disabled>step 2</button>
                <button id="btn-step2-explain">explain</button>
                <button id="btn-step2-do">do it</button>
            </div>
            <div id="step3-controls-container">
                <button disabled>step 3</button>
                <button id="btn-step3-explain">explain</button>
                <div id="step3-actions" style="display: inline-block;"></div>
            </div>
        </div>
        <div id="canvas-container"></div>
        <div id="explanation-panel">
            <p id="explanation-text"></p>
        </div>
    </div>

    <script>
        let mode = 'move';
        let points = [{x: 150, y: 350}, {x: 250, y: 150}, {x: 350, y: 250}, {x: 550, y: 180}, {x: 480, y: 320}, {x: 420, y: 400}];
        let draggedPoint = null;
        let algorithmState = {
            step: 0,
            hullPointsIndices: [],
            fullHull: [],
            leftmostPointIndex: null,
            currentPointIndex: null,
            candidateLines: [],
            explanationSteps: []
        };

        function setup() {
            createCanvas(700, 500);
            background(255);
            stroke(220);
            noFill();
            rect(0, 0, width, height);
            drawPoints();
        }

        function draw() {
            if (mode === 'move') {
                drawPoints();
            }
        }

        function drawPoints() {
            background(255);
            stroke(220);
            noFill();
            rect(0, 0, width, height);

            points.forEach((point, index) => {
                fill(draggedPoint === index ? 45, 60, 110 : 200, 220, 100);
                stroke(0);
                ellipse(point.x, point.y, 12);
                line(point.x - 5, point.y, point.x + 5, point.y);
                line(point.x, point.y - 5, point.x, point.y + 5);
            });

            if (algorithmState.step > 0) {
                stroke(45, 60, 110);
                strokeWeight(3);
                for (let i = 0; i < algorithmState.hullPointsIndices.length - 1; i++) {
                    const from = points[algorithmState.hullPointsIndices[i]];
                    const to = points[algorithmState.hullPointsIndices[i + 1]];
                    line(from.x, from.y, to.x, to.y);
                }
                if (algorithmState.step === 3 && algorithmState.hullPointsIndices.length > 1) {
                    const from = points[algorithmState.hullPointsIndices[algorithmState.hullPointsIndices.length - 1]];
                    const to = points[algorithmState.hullPointsIndices[0]];
                    line(from.x, from.y, to.x, to.y);
                }
            }

            if (algorithmState.candidateLines.length > 0) {
                stroke(60, 150, 140);
                strokeWeight(2);
                algorithmState.candidateLines.forEach(line => {
                    const from = points[line[0]];
                    const to = points[line[1]];
                    line(from.x, from.y, to.x, to.y);
                });
            }
        }

        function mouseClicked() {
            if (mode === 'move') {
                draggedPoint = points.findIndex(point => dist(mouseX, mouseY, point.x, point.y) < 6);
            }
        }

        function mouseDragged() {
            if (mode === 'move' && draggedPoint !== null) {
                points[draggedPoint].x = mouseX;
                points[draggedPoint].y = mouseY;
                redraw();
            }
        }

        function mouseReleased() {
            if (mode === 'move') {
                draggedPoint = null;
            }
        }

        document.getElementById('btn-move').addEventListener('click', () => {
            mode = 'move';
            algorithmState = {
                step: 0,
                hullPointsIndices: [],
                fullHull: [],
                leftmostPointIndex: null,
                currentPointIndex: null,
                candidateLines: [],
                explanationSteps: []
            };
            document.getElementById('step-controls').style.display = 'none';
            document.getElementById('explanation-text').innerText = '';
            redraw();
        });

        document.getElementById('btn-start-algo').addEventListener('click', () => {
            mode = 'algorithm';
            algorithmState = {
                step: 0,
                hullPointsIndices: [],
                fullHull: jarvisMarch(points),
                leftmostPointIndex: null,
                currentPointIndex: null,
                candidateLines: [],
                explanationSteps: []
            };
            document.getElementById('step-controls').style.display = 'block';
            document.querySelector('#step-controls button').classList.add('active');
            document.getElementById('step3-actions').innerHTML = '';
            for (let i = 1; i < algorithmState.fullHull.length - 1; i++) {
                const btn = document.createElement('button');
                btn.id = `btn-step3-repeat-${i}`;
                btn.innerText = 'repeat';
                btn.disabled = true;
                document.getElementById('step3-actions').appendChild(btn);
            }
            redraw();
        });

        document.getElementById('btn-step1-explain').addEventListener('click', () => {
            algorithmState.explanationSteps.push("Step 1. Find the leftmost point.");
            updateExplanationText();
        });

        document.getElementById('btn-step1-do').addEventListener('click', () => {
            algorithmState.leftmostPointIndex = algorithmState.currentPointIndex = points.reduce((minIndex, point, index) => 
                point.x < points[minIndex].x ? index : minIndex, 0);
            algorithmState.hullPointsIndices.push(algorithmState.leftmostPointIndex);
            algorithmState.step = 1;
            document.querySelector('#step-controls button').classList.remove('active');
            document.querySelectorAll('#step-controls button')[1].classList.add('active');
            redraw();
        });

        document.getElementById('btn-step2-explain').addEventListener('click', () => {
            algorithmState.explanationSteps.push("Step 2. Find the point clockwise furthest to the right relative to the leftmost point.");
            updateExplanationText();
        });

        document.getElementById('btn-step2-do').addEventListener('click', () => {
            const currentPoint = points[algorithmState.currentPointIndex];
            let nextPointIndex = -1;
            for (let i = 0; i < points.length; i++) {
                if (i !== algorithmState.currentPointIndex && !algorithmState.hullPointsIndices.includes(i)) {
                    if (nextPointIndex === -1 || orientation(currentPoint, points[nextPointIndex], points[i]) < 0) {
                        nextPointIndex = i;
                    }
                }
            }
            algorithmState.hullPointsIndices.push(nextPointIndex);
            algorithmState.currentPointIndex = nextPointIndex;
            algorithmState.step = 2;
            document.querySelectorAll('#step-controls button')[1].classList.remove('active');
            document.querySelectorAll('#step-controls button')[2].classList.add('active');
            redraw();
        });

        document.getElementById('btn-step3-explain').addEventListener('click', () => {
            algorithmState.explanationSteps.push("Step 3. Repeat finding the point clockwise furthest to the right relative to the previously found point, until the leftmost point is reached again.");
            updateExplanationText();
        });

        document.addEventListener('click', event => {
            if (event.target.id.startsWith('btn-step3-repeat')) {
                const index = parseInt(event.target.id.split('-')[3]);
                performStep3(index);
            }
        });

        function performStep3(index) {
            const currentPoint = points[algorithmState.currentPointIndex];
            let nextPointIndex = -1;
            for (let i = 0; i < points.length; i++) {
                if (i !== algorithmState.currentPointIndex && !algorithmState.hullPointsIndices.includes(i)) {
                    if (nextPointIndex === -1 || orientation(currentPoint, points[nextPointIndex], points[i]) < 0) {
                        nextPointIndex = i;
                    }
                }
            }
            algorithmState.hullPointsIndices.push(nextPointIndex);
            algorithmState.currentPointIndex = nextPointIndex;
            algorithmState.step = 3;
            document.getElementById(`btn-step3-repeat-${index}`).disabled = true;
            if (index < algorithmState.fullHull.length - 2) {
                document.getElementById(`btn-step3-repeat-${index + 1}`).disabled = false;
            }
            redraw();
        }

        function updateExplanationText() {
            document.getElementById('explanation-text').innerText = algorithmState.explanationSteps.join('\n');
        }

        function jarvisMarch(points) {
            let leftmost = 0;
            for (let i = 1; i < points.length; i++) {
                if (points[i].x < points[leftmost].x) {
                    leftmost = i;
                }
            }

            let hull = [leftmost];
            let endpoint = 0;

            do {
                endpoint = (endpoint + 1) % points.length;
                for (let i = 0; i < points.length; i++) {
                    if (orientation(points[hull[hull.length - 1]], points[endpoint], points[i]) < 0) {
                        endpoint = i;
                    }
                }
                hull.push(endpoint);
            } while (endpoint !== leftmost);

            return hull;
        }

        function orientation(p, q, r) {
            const val = (q.y - p.y) * (r.x - q.x) - (q.x - p.x) * (r.y - q.y);
            if (val === 0) return 0; // collinear
            return (val > 0) ? 1 : 2; // clock or counterclock wise
        }
    </script>
</body>
</html>
