    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
</head>
<body>
    <div class="container">
        <div id="controls">
            <details>
                <summary>Initial Conditions</summary>
                <!-- Additional initial conditions can be added here -->
            </details>
            <div>
                <label for="slider-alpha-max">maximum fraction of respiring biomass</label>
                <input type="range" id="slider-alpha-max" min="0.1" max="1.0" step="0.01" value="0.3">
                <span id="value-alpha-max">0.3</span>
            </div>
            <div>
                <label for="slider-t-min">run time minimum (h)</label>
                <input type="range" id="slider-t-min" min="0" max="0" step="1" value="0" disabled>
                <span id="value-t-min">0</span>
            </div>
            <div>
                <label for="slider-t-max">run time maximum (h)</label>
                <input type="range" id="slider-t-max" min="10" max="100" step="1" value="60">
                <span id="value-t-max">60</span>
            </div>
            <div>
                <label for="slider-enzyme-control">intermediate enzyme control [mol/m³]</label>
                <input type="range" id="slider-enzyme-control" min="0.1" max="5.0" step="0.01" value="1">
                <span id="value-enzyme-control">1</span>
            </div>
            <div>
                <label for="slider-feed-rate">feed flow rate (m³/h)</label>
                <input type="range" id="slider-feed-rate" min="0.0" max="2.0" step="0.01" value="0.2">
                <span id="value-feed-rate">0.2</span>
            </div>
            <div>
                <label for="slider-substrate-feed-conc">substrate feed concentration [mol/m³]</label>
                <input type="range" id="slider-substrate-feed-conc" min="10" max="200" step="1" value="100">
                <span id="value-substrate-feed-conc">100</span>
            </div>
            <div>
                <label for="slider-feed-time">feed time (h)</label>
                <input type="range" id="slider-feed-time" min="0" max="60" step="1" value="35">
                <span id="value-feed-time">35</span>
            </div>
            <div>
                <label for="select-plot">plot</label>
                <select id="select-plot">
                    <option value="fed batch">fed batch</option>
                    <option value="α">α</option>
                </select>
            </div>
        </div>
        <div id="visualization">
            <div id="plot-top"></div>
            <div id="plot-bottom"></div>
        </div>
    </div>

    <script>
        const initialConditions = {
            X: 0.1,
            Glu: 60,
            Eta: 0,
            E0: 1.0,
            E1: 0.0,
            E2: 0.0,
            V: 1.0
        };

        const sliders = {
            'slider-alpha-max': { value: 0.3 },
            'slider-t-min': { value: 0 },
            'slider-t-max': { value: 60 },
            'slider-enzyme-control': { value: 1 },
            'slider-feed-rate': { value: 0.2 },
            'slider-substrate-feed-conc': { value: 100 },
            'slider-feed-time': { value: 35 }
        };

        const plotOptions = {
            'fed batch': {
                top: {
                    title: 'diauxic behavior',
                    yLabel: 'concentration [mol/m³]',
                    traces: ['X', 'Glu', 'Eta']
                },
                bottom: {
                    title: 'enzyme concentration',
                    yLabel: 'enzyme concentration [mol/m³]',
                    traces: ['E2', 'E1', 'E0']
                }
            },
            'α': {
                top: {
                    title: 'glucose concentration',
                    yLabel: 'glucose concentration [mol/m³]',
                    traces: ['Glu']
                },
                bottom: {
                    title: 'fraction of respiring biomass',
                    yLabel: 'α',
                    traces: ['alpha']
                }
            }
        };

        function updateSliderValue(event) {
            const sliderId = event.target.id;
            const value = event.target.value;
            document.getElementById(`value-${sliderId}`).textContent = value;
            if (sliderId === 'slider-t-max') {
                document.getElementById('slider-feed-time').max = value;
                document.getElementById('slider-feed-time').value = Math.min(value, sliders['slider-feed-time'].value);
            }
            sliders[sliderId].value = parseFloat(value);
            runSimulation();
        }

        function updatePlotSelection(event) {
            const selectedPlot = event.target.value;
            drawPlots(selectedPlot);
        }

        function runSimulation() {
            const { alphaMax, tMin, tMax, enzymeControl, feedRate, substrateFeedConc, feedTime } = sliders;
            const dt = 0.1;
            const t = [];
            const X = [], Glu = [], Eta = [], E0 = [], E1 = [], E2 = [], alpha = [];

            let currentTime = 0;
            let currentConditions = { ...initialConditions };
            while (currentTime <= tMax.value) {
                t.push(currentTime);
                X.push(currentConditions.X);
                Glu.push(currentConditions.Glu);
                Eta.push(currentConditions.Eta);
                E0.push(currentConditions.E0);
                E1.push(currentConditions.E1);
                E2.push(currentConditions.E2);
                alpha.push(alphaMax.value * Math.pow(enzymeControl.value, 4) / (Math.pow(enzymeControl.value, 4) + Math.pow(currentConditions.Glu, 4)));

                const derivatives = calculateDerivatives(currentConditions, currentTime, alphaMax.value, enzymeControl.value, feedRate.value, substrateFeedConc.value, feedTime.value);
                currentConditions.X += derivatives.X * dt;
                currentConditions.Glu += derivatives.Glu * dt;
                currentConditions.Eta += derivatives.Eta * dt;
                currentConditions.E0 += derivatives.E0 * dt;
                currentConditions.E1 += derivatives.E1 * dt;
                currentConditions.E2 += derivatives.E2 * dt;
                currentConditions.V += feedRate.value * dt;

                currentTime += dt;
            }

            drawPlots(document.getElementById('select-plot').value, { t, X, Glu, Eta, E0, E1, E2, alpha });
        }

        function calculateDerivatives(conditions, time, alphaMax, enzymeControl, feedRate, substrateFeedConc, feedTime) {
            const { X, Glu, Eta, E0, E1, E2, V } = conditions;
            const alpha = alphaMax * Math.pow(enzymeControl, 4) / (Math.pow(enzymeControl, 4) + Math.pow(Glu, 4));

            const dXdt = alpha * 0.5 * Glu / V;
            const dGluDt = -alpha * 0.5 * Glu / V + (time <= feedTime ? feedRate * substrateFeedConc / V : 0);
            const dEtadt = alpha * 0.5 * Glu / V;
            const dE0dt = 0.1 * Glu / V - 0.01 * E0;
            const dE1dt = 0.05 * Glu / V - 0.02 * E1;
            const dE2dt = 0.03 * Glu / V - 0.01 * E2;

            return { X: dXdt, Glu: dGluDt, Eta: dEtadt, E0: dE0dt, E1: dE1dt, E2: dE2dt };
        }

        function drawPlots(selectedPlot, data) {
            const { t, X, Glu, Eta, E0, E1, E2, alpha } = data || {};
            const topPlotData = [];
            const bottomPlotData = [];

            const colors = { X: 'blue', Glu: 'red', Eta: 'green', E0: 'orange', E1: 'magenta', E2: 'black', alpha: 'blue' };

            if (selectedPlot === 'fed batch') {
                ['X', 'Glu', 'Eta'].forEach(trace => {
                    topPlotData.push({
                        x: t,
                        y: eval(trace),
                        type: 'scatter',
                        mode: 'lines',
                        name: `${trace}(t)`,
                        line: { color: colors[trace] }
                    });
                });

                ['E2', 'E1', 'E0'].forEach(trace => {
                    bottomPlotData.push({
                        x: t,
                        y: eval(trace),
                        type: 'scatter',
                        mode: 'lines',
                        name: `E${trace.slice(-1)}(t)`,
                        line: { color: colors[trace] }
                    });
                });
            } else if (selectedPlot === 'α') {
                topPlotData.push({
                    x: t,
                    y: Glu,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Glu(t)',
                    line: { color: colors.Glu }
                });

                bottomPlotData.push({
                    x: t,
                    y: alpha,
                    type: 'scatter',
                    mode: 'lines',
                    name: 'α',
                    line: { color: colors.alpha }
                });
            }

            Plotly.newPlot('plot-top', topPlotData, {
                title: plotOptions[selectedPlot].top.title,
                yaxis: { title: plotOptions[selectedPlot].top.yLabel },
                xaxis: { title: 'time (h)' }
            });

            Plotly.newPlot('plot-bottom', bottomPlotData, {
                title: plotOptions[selectedPlot].bottom.title,
                yaxis: { title: plotOptions[selectedPlot].bottom.yLabel },
                xaxis: { title: 'time (h)' }
            });
        }

        document.querySelectorAll('input[type="range"]').forEach(slider => {
            slider.addEventListener('input', updateSliderValue);
        });

        document.getElementById('select-plot').addEventListener('change', updatePlotSelection);

        // Initialize the simulation on page load
        runSimulation();
    </script>
</body>
</html>
