        }
        #control-panel {
            background-color: #f0f0f0;
            padding: 10px;
            display: flex;
            flex-direction: column;
            gap: 10px;
        }
        .slider-group {
            display: flex;
            align-items: center;
            gap: 10px;
        }
        #btn-reset {
            margin-left: auto;
            background-color: #ff0000;
            color: white;
            border: none;
            padding: 5px 10px;
            cursor: pointer;
            border-radius: 50%;
            font-size: 16px;
        }
