            background-color: white;
            padding: 10px;
            position: relative;
        }
        canvas {
            border: 1px solid #ccc;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="slider-group">
                <label for="slider-current">current (A)</label>
                <input type="range" id="slider-current" min="0" max="1" step="0.001" value="0.431">
                <span id="value-current">0.431</span>
            </div>
            <div class="slider-group">
                <label for="slider-earth-field">horizontal Earth field (T)</label>
                <input type="range" id="slider-earth-field" min="0" max="0.0001" step="0.0000001" value="0.0000194">
                <span id="value-earth-field">0.0000194</span>
            </div>
            <div class="slider-group">
                <label for="slider-radius">radius (m)</label>
                <input type="range" id="slider-radius" min="0.1" max="1.0" step="0.01" value="0.5">
                <span id="value-radius">0.5</span>
            </div>
            <div class="slider-group">
                <label for="slider-turns">number of turns</label>
                <input type="range" id="slider-turns" min="1" max="100" step="1" value="25">
                <span id="value-turns">25</span>
            </div>
            <button id="btn-reset">↻</button>
        </div>
        <div id="viz-container">
            <canvas id="p5-canvas"></canvas>
        </div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>
    <script>
        const μ₀ = 4 * Math.PI * 1e-7; // Permeability of free space in T·m/A

        function ellipticK(m) {
            let a = 1;
            let b = Math.sqrt(1 - m);
            let t = 1;
            let p = 1;
            let ak, bk, pk, qk;
            while (Math.abs(a - b) > 1e-10) {
                ak = (a + b) / 2;
                bk = Math.sqrt(a * b);
                pk = (a - b) * (a - b) / (4 * bk);
                qk = Math.sqrt(pk);
                a = ak;
                b = bk;
                t += 2 * p * qk;
                p *= 2;
            }
            return Math.PI / (2 * t);
        }

        function ellipticE(m) {
            let a = 1;
            let b = Math.sqrt(1 - m);
            let t = 1;
            let p = 1;
            let ak, bk, pk, qk;
            while (Math.abs(a - b) > 1e-10) {
                ak = (a + b) / 2;
                bk = Math.sqrt(a * b);
                pk = (a - b) * (a - b) / (4 * bk);
                qk = Math.sqrt(pk);
                a = ak;
                b = bk;
                t += 2 * p * qk * qk / (a + b);
                p *= 2;
            }
            return a / 2 * t;
        }

        let current = 0.431;
        let earthField = 0.0000194;
        let radius = 0.5;
        let turns = 25;

        function setup() {
            createCanvas(800, 600);
            updateValues();
        }

        function draw() {
            background(255);
            drawAxes();
            drawStreamlines();
        }

        function drawAxes() {
            stroke(255, 0, 0);
            strokeWeight(2);
            line(width / 2, 0, width / 2, height);
            line(0, height / 2, width, height / 2);

            textSize(14);
            fill(255, 0, 0);
            textAlign(CENTER, TOP);
            text('Z', width / 2 + 10, 10);
            textAlign(RIGHT, CENTER);
            text('Y', 10, height / 2 - 10);

            for (let i = -0.5; i <= 0.5; i += 0.2) {
                let x = map(i, -0.5, 0.5, width / 2, width);
                line(x, height / 2 - 5, x, height / 2 + 5);
                text(i.toFixed(1), x, height / 2 + 20);
            }

            for (let i = -0.5; i <= 0.5; i += 0.2) {
                let y = map(i, -0.5, 0.5, height, height / 2);
                line(width / 2 - 5, y, width / 2 + 5, y);
                text(i.toFixed(1), width / 2 + 20, y);
            }
        }

        function drawStreamlines() {
            let stepSize = 0.01;
            let numSteps = 100;
            let gridSize = 25;
            let minMag = Infinity;
            let maxMag = -Infinity;

            for (let i = 0; i < gridSize; i++) {
                for (let j = 0; j < gridSize; j++) {
                    let y = map(i, 0, gridSize - 1, -0.5, 0.5);
                    let z = map(j, 0, gridSize - 1, -0.5, 0.5);
                    let [bx, by, bz] = B_total(y, z);
                    let mag = Math.sqrt(by * by + bz * bz);
                    minMag = Math.min(minMag, mag);
                    maxMag = Math.max(maxMag, mag);
                }
            }

            for (let i = 0; i < gridSize; i++) {
                for (let j = 0; j < gridSize; j++) {
                    let y = map(i, 0, gridSize - 1, -0.5, 0.5);
                    let z = map(j, 0, gridSize - 1, -0.5, 0.5);
                    drawStreamline(y, z, stepSize, numSteps, minMag, maxMag);
                    drawStreamline(y, z, -stepSize, numSteps, minMag, maxMag);
                }
            }
        }

        function drawStreamline(y, z, stepSize, numSteps, minMag, maxMag) {
            strokeWeight(1);
            let [bx, by, bz] = B_total(y, z);
            let mag = Math.sqrt(by * by + bz * bz);
            if (mag === 0) return;
            let hue = map(mag, minMag, maxMag, 270, 60);
            colorMode(HSB, 360, 100, 100);
            stroke(hue, 100, 100);
            let x = map(y, -0.5, 0.5, width / 2, width);
            let yPrev = map(z, -0.5, 0.5, height, height / 2);
            for (let k = 0; k < numSteps; k++) {
                let d = createVector(bx, bz).normalize();
                let xNext = x + stepSize * d.x;
                let yNext = yPrev + stepSize * d.y;
                line(x, yPrev, xNext, yNext);
                x = xNext;
                yPrev = yNext;
                if (x < 0 || x > width || yPrev < 0 || yPrev > height) break;
                if (k % 10 === 0) {
                    let arrowLength = 5;
                    let angle = atan2(d.y, d.x);
                    line(x, yPrev, x + arrowLength * cos(angle - PI / 6), yPrev + arrowLength * sin(angle - PI / 6));
                    line(x, yPrev, x + arrowLength * cos(angle + PI / 6), yPrev + arrowLength * sin(angle + PI / 6));
                }
            }
        }

        function B_total(y, z) {
            let B_earth = [0, 0, earthField];
            let B_coils = B_coil(y, z, -radius / 2);
            B_coils[0] += B_coil(y, z, radius / 2)[0];
            B_coils[1] += B_coil(y, z, radius / 2)[1];
            B_coils[2] += B_coil(y, z, radius / 2)[2];
            return [B_coils[0] + B_earth[0], B_coils[1] + B_earth[1], B_coils[2] + B_earth[2]];
        }

        function B_coil(y, z, z_offset) {
            let zp = z - z_offset;
            let m = (4 * radius * y) / ((radius + y) ** 2 + zp ** 2);
            let common_factor_1 = (μ₀ * current * turns) / (2 * Math.PI);
            let common_factor_2 = 1 / Math.sqrt((radius + y) ** 2 + zp ** 2);
            let B_y = common_factor_1 * (zp / y) * common_factor_2 * (((radius ** 2 + y ** 2 + zp ** 2) / ((radius - y) ** 2 + zp ** 2)) * ellipticE(m) - ellipticK(m));
            let B_z = common_factor_1 * common_factor_2 * (ellipticK(m) - ((radius ** 2 - y ** 2 - zp ** 2) / ((radius - y) ** 2 + zp ** 2)) * ellipticE(m));
            return [0, B_y, B_z];
        }

        function updateValues() {
            current = parseFloat(document.getElementById('slider-current').value);
            earthField = parseFloat(document.getElementById('slider-earth-field').value);
            radius = parseFloat(document.getElementById('slider-radius').value);
            turns = parseInt(document.getElementById('slider-turns').value);

            document.getElementById('value-current').textContent = current.toFixed(3);
            document.getElementById('value-earth-field').textContent = earthField.toExponential(2);
            document.getElementById('value-radius').textContent = radius.toFixed(2);
            document.getElementById('value-turns').textContent = turns;

            redraw();
        }

        document.getElementById('slider-current').addEventListener('input', updateValues);
        document.getElementById('slider-earth-field').addEventListener('input', updateValues);
        document.getElementById('slider-radius').addEventListener('input', updateValues);
        document.getElementById('slider-turns').addEventListener('input', updateValues);
        document.getElementById('btn-reset').addEventListener('click', () => {
            document.getElementById('slider-current').value = 0.431;
            document.getElementById('slider-earth-field').value = 0.0000194;
            document.getElementById('slider-radius').value = 0.5;
            document.getElementById('slider-turns').value = 25;
            updateValues();
        });

        window.onload = setup;
    </script>
</body>
</html>
