        #app-container {
            width: 80%;
            max-width: 800px;
            background-color: #fff;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
            border-radius: 8px;
            overflow: hidden;
        }
        #tab-controls {
            display: flex;
            justify-content: space-around;
            background-color: #e0e0e0;
            padding: 10px 0;
        }
        #tab-controls button {
            background-color: #f1f1f1;
